-- ----------------------------
-- Table structure for ys_article
-- ----------------------------
DROP TABLE IF EXISTS `ys_article`;
CREATE TABLE `ys_article` (
  `id` mediumint(9) NOT NULL AUTO_INCREMENT COMMENT '编号',
  `title` varchar(50) NOT NULL COMMENT '标题',
  `nav` mediumint(8) NOT NULL COMMENT '栏目id',
  `attr` varchar(30) DEFAULT NULL COMMENT '自定义属性 头条 推荐 加粗 跳转',
  `tag` varchar(30) DEFAULT NULL COMMENT 'tag标签',
  `keyword` varchar(30) DEFAULT NULL COMMENT '关键词',
  `thumbnail` varchar(100) DEFAULT NULL COMMENT '缩略图',
  `source` varchar(20) DEFAULT NULL COMMENT '来源',
  `author` varchar(10) DEFAULT NULL COMMENT '作者',
  `info` varchar(200) DEFAULT NULL COMMENT '内容摘要',
  `content` text COMMENT '文章内容',
  `comment` tinyint(1) DEFAULT '1' COMMENT '是否允许评论',
  `count` smallint(6) DEFAULT '0' COMMENT '浏览次数',
  `gold` smallint(6) DEFAULT '0' COMMENT '默认金币',
  `sort` tinyint(1) DEFAULT '0',
  `readlimit` tinyint(4) DEFAULT NULL COMMENT '阅读权限',
  `date` datetime DEFAULT NULL COMMENT '发布时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=180 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of ys_article
-- ----------------------------
INSERT INTO `ys_article` VALUES ('1', '河南省健身气功一级社会体育指导员培训班在南阳开班', '1', '头条', '', '', '', '网络', 'admin', '', '<p>6月20日上午，河南省第十二期健身气功一级社会体育指导员培训班(南阳站)在南阳市龙湾温泉开班。根据课程安排，开班仪式后，培训班首讲暨反邪教知识专题讲座开讲，来自全省各地健身气功站点指导员约150余人参加并现场聆听了讲座，讲座由南阳市宛城区防范办负责人主讲。</p><p>\n</p><center><img alt=\"\" src=\"http://p0.ifengimg.com/a/2018_25/08f397c10fcdcce_size524_w750_h563.png\" width=\"550\" height=\"413\"></center><p></p><p>讲座运用电脑课件，图文并茂，并采用结合具体案例形式，详细讲解了邪教特征、危害、相关法律法规、健身气功与有害气功的区别等相关知识。讲座现场，学员们认真做笔记，并不时上前拍摄课件具体内容，休息期间还与主讲人沟通交流，整个课程现场氛围热烈高涨，在历时两个小时的讲解中，学员们被精彩生动的讲解所吸引，不时地响起阵阵掌声。</p><p>宛城区防范办工作人员还现场向学员们发放了反邪教知识宣传彩页，致广大健身气功爱好者倡议书共200余份，现场悬挂反邪教宣传横幅2条。</p><p>\n\n\n\n\n</p><p>学员们纷纷表示，通过聆听讲座，进一步提高了自身防范邪教和抵制有害气功的意识和能力，回去后要把今天所学到的反邪教知识传递给更多的广大健身气功爱好者和居民群众，把“崇尚科学反对邪教科学健身幸福生活”的理念植根于每一个人心中，进一步挤压邪教和有害气功的生存空间。</p>', '1', '16', '0', '1', '0', '2018-06-21 08:30:42');
INSERT INTO `ys_article` VALUES ('2', '江西省健身气功巡回教学在新余圆满结束', '1', '头条', '', '', '', '网络', 'admin', '本次教学活动以健身气功·马王堆引导术、大舞、易筋经、五禽戏为主要内容，旨在规范各站点骨干学员功法动作，提高动作理论水平，达到增进身心健康以及传承中华悠久文化的目的，并让参加培训的骨干学员以最佳状态备战江西省运会。', '<p>6月15日，为期两天的“江西省健身气功巡回教学活动(新余站)”在江西省新余市举行。来自新余市90多个主要站点的健身气功骨干参加了此次教学活动。</p><p>\n</p><center><img alt=\"\" src=\"http://5b0988e595225.cdn.sohucs.com/images/20180619/f247785ed04e4ad2977df101173f387f.jpeg\" width=\"558\" height=\"373\"></center><p></p><p>江西省健身气功管理中心主任陆跃忠，新余市体育总会秘书长张时春，江西师范大学体育学院教授曾凡文等先后发表讲话。</p><p>\n</p><center><img alt=\"\" src=\"http://5b0988e595225.cdn.sohucs.com/images/20180619/f64f57bb56544c9eb352ca2ebc377553.jpeg\" width=\"558\" height=\"373\"></center><p></p><p>\n</p><center><img alt=\"\" src=\"http://5b0988e595225.cdn.sohucs.com/images/20180619/a83b7f470faf4d49993155d1ed78dd45.jpeg\" width=\"558\" height=\"373\"></center><p></p><p>本次教学活动以健身气功·马王堆引导术、大舞、易筋经、五禽戏为主要内容，旨在规范各站点骨干学员功法动作，提高动作理论水平，达到增进身心健康以及传承中华悠久文化的目的，并让参加培训的骨干学员以最佳状态备战江西省运会。</p><p>\n</p><center><img alt=\"\" src=\"http://5b0988e595225.cdn.sohucs.com/images/20180619/1414dfe704f7409dbde6b097b0d891a7.jpeg\" width=\"558\" height=\"373\"></center><p></p><p>\n\n\n\n\n\n\n\n\n\n\n</p><p>培训期间虽然是周末，又逢假期，但没能阻挡学员们学习的热情。本次教学活动的开展，不仅为健身气功惠及更多人群起到推动作用，也进一步促进了江西健身气功事业的发展，有助于中华悠久文化的传播。</p>', '1', '1', '0', '1', '0', '2018-06-21 08:30:42');
INSERT INTO `ys_article` VALUES ('3', '重新认识气功，走入人体科学，生命科学的领域！', '11', '头条', '', '', '', '网络', 'admin', '', '<p>包含中医学在内的生命科学，是我国古代文明中的珍宝，其在先秦文明中，就现已老练并被容纳于儒学和道学当中。生成于我国文明中的这种生命科学办法，因有其相应的哲学为根底，所以不能以西方的科学理论和科学规则进行界定或许类比。笔者在研讨中，依据形而中论的哲学原理，把以生命科学为代表办法的我国科学以本来质性界定为形上性科学，把以声光电化为代表办法的西方科学以本来质性界定为形下性科学，从科学的基赋性上处理了这一研讨领域存在的疑问(例如“李约瑟疑问”等)。详细推论请参阅拙作《寻找我国科学精力》一书。</p><p>咱们知道，我国今世的生命科学研讨由“气功热”所引发，因为没有相应的根底理论之预备，所以，生命科学的根底理论研讨的滞后一直变成制约研讨中的疑问。这些疑问表现出今世文明办法与“气功”文明的母体──我国文明的非统一性，特别是其间的哲学疑问变成疑问中的疑问。因而，对这些疑问的反思将推进哲学、文明与科学的前进。对生命科学的哲学疑问的考虑，不光使人类文明的实质性和科学的实质性疑问得以弄清，并且使哲学在这一进程中带来自身的革新，哲学将在对存在本体的新知道中突寒酸的办法，将在对人类终极关心中树立人类的哲学。并且，只要在这一哲学理路中，使生命科学的本体论疑问得到化解，然后推进生命科学的研讨。在这一进程中将推定人类存在的实质性，使人类的文明在存在的本体性办法上处理终极关心疑问。</p><p>所以，深化探究生命科学研讨中的根底理论疑问，使今世以来发作的“气功热”向健康的方向上开展及对社会发作活跃的影响，是这一作业中的极其重要的组成部分。因为人的存在的终极关心疑问是人的思想和行动的价值主体(生命是全部别的价值所推定的主体)，加当我国社会从八十年代以来开端了安稳、健康和政治宽松的前进期间，大家的价值主体知道在必定的程度上被社会政治知道形态所认同。在这种社会空气中，大家开端有条件以价值主体动身关心自身的存在疑问，开端有条件挑选医疗保健办法，以到达增强身体健康和延伸生命之意图。依据自身的医疗经历，遍及的一致以为惯例的西方医学的办法不能在最根本的价值本体的含义上到达上述方针。因而，从我国古代社会就发作的，被先秦儒学和道学及两汉今后的道教现已证实的卓有成效的“气功”修炼办法，就变成大家挑选的强身健体的锻练办法。可是，因为今世大家的思想办法、言语内在与古代大不相同，加之今世人的社会存在办法与古代(发作“气功”的)安闲办法相差悬殊，更加之今世大家习惯于用西方的科学办法和科学主义的价值观评介气功修炼，这就构成了一系列的理论疑问，特别是其间的根底理论疑问。因为古代“气功”门户很多，各执其一，良莠参杂，令人难辨真伪。疑问标明，今世的“气功”热始于社会自发性，而不是出于我国文明和哲学的理性。出于有用的心态，这使生命科学的研讨相对被迫，因而表现出根底理论研讨的单薄。由此可知，在生命科学研讨中进行根底理论方面的研讨，以断定“气功”功理功法的遍及含义，使人的价值本体在准确的办法论中到达安闲，是当时生命科学研讨中的重要作业。而这一作业，也只要人类社会开展到今日，在具有了可供反思的人类学、社会学、政治学、生理学、医学及科学学等老练的具有外在价值的理论和实习的根底上，人类的知道在树立了价值主体并证实其是仅有最高的价值时才有也许完结。</p><p>从哲学而言，在今世的文明办法中发作的“气功热”，实质上是今世干流文明观的“异化”，因为不管从何种含义而言，以“气功”为办法表现出的我国文明的实质性与今世的干流文明观是不兼容的。这实习生成了下面的悖论──以我国现代干流文明的西方哲学思想推定我国陈旧的“气功”。所以，这些干流哲学和文明观念上的疑问，在实习中变成练功者的心里阻碍，练功者需具有的忘我、无为等心里实质，与今世的价值取向(特别是商品经济的价值观)明显是相悖的。因而，对气功修炼者的文明心里实质的研讨标明，社会上发作的广泛的群众性的气功热，存在着文明上的阻碍，存在着根底理论的艰难，存在着哲学知道论的疑问。这些，也是存在于生命科学研讨者中的疑问。“气功热”以来发作的疑问标明，因为在研讨中短少对我国传统哲学文明方面的根底性研讨，因而发作了所谓的对立伪科学的疑问，当然，以伪科学贯之于这些疑问明显出于科学主义的价值观，虽然能阐明批评者的心态，可是，因为这种批评不具有本体论含义，因而终非准确，而其间所反映出的哲学和文明价值观疑问对批评者和被批评者都是具有同一性的疑问。关于用对立伪科学为兵器的批评者而言，把孩子和脏水一起倒掉(例如于光远、何柞庥、司马南等人出于知道形态的价值许诺而不是出于理性的许诺而进行的批评)。关于被批评者而言，因为短少文明的主体知道或出于某种意图，把脏水叫孩子喝掉(例如有的气功门派宣称不二法门，修者必需将本来所习所修的常识及书本等遗忘和焚毁等，这种做法不光有违于文明开展的规则，一起也构成人的文明道德修养的丢失)。试问，没有文明能有今日的文明架构然后能使大家知道气功吗?这些就不是用所谓对立伪科学能处理的疑问，因为用西方的形下性的科学许诺真理并批评我国形上性的生命科学，因为形下性和形上性的不相同，实质上因其不能无懈可击而处于两难窘境。对立伪科学和把气功称为科学因为相同不知其形上性和形下性的差异，这将使大家的知道堕入严峻的误区。这些乃是非理性的行动，开展下去，这是对文明的反抗，是对生命科学研讨及文明理性的摧残。因而，“气功”修炼的文明学、人类学含义及其哲学根底理论和科学的形上性和形下性的不相同办法疑问，应导致生命科学研讨者的极大注重。</p><p>如今生命科学研讨的办法论标明，仅仅以西方天然科学的办法研讨人体的某些“气功”效果下的物理表象以作出某种科学的解说明显是不行的，更首要的根底性的作业即哲学和文明的研讨还没有在应有的层次上打开，这使得理论上的紊乱和构成研讨中的艰难就不行避免。这一疑问，从一些当时发行的气功杂志和理论刊物的论点中略见一斑。因而，应在当时的生命科学研讨中注重根底理论方面的研讨，特别注重生命科学的哲学和文明疑问，在处理了上述的根底理论疑问以后，生命科学研讨才会深化和健康的开展。而这一进程标明，人类社会开展到今世，因为价值主体的反思和被从头树立，将使西方天然科学的办法由形下性转化为形上性，在我国生命科学对终极关心的许诺中，使生命科学研讨具有人和社会存在办法的必定统一性。</p><p>今世的生命科学研讨标明，因为哲学思想安闲于研讨中的效果，并因为有适当多的研讨者的哲学思想捆绑于西方哲学的办法，使生命科学的研讨因哲学思想办法上的疑问，呈现了一些误解和争辩，没有发作打破性的研讨成果。黑格尔以为：“已然文明上的差异通常地依据思想领域的差异，则哲学上的差异则更是依据思想领域的差异”(黑格尔：《哲学史讲演录》商务印书馆1959年9月新1版第1卷第47页)因而，假如说以形下性的西方科学的办法许诺并生成了西方的医学系统，这由其哲学的统一性所决议，那么，形上性的我国生命科学也只能是我国哲学生成的科学办法。所以，假如不是出自于理性，而是以几十年来养成的西化的思想定势、那种被异化的文明价值观及其对知道形态上的许诺，因而以西化的概念和领域作为坐标系进行我国的生命科学研讨，那么，明显因为我国生命科学研讨的形上性，其概念领域在两种科学办法及实质上的非统一性，将构成了解我国生命科学的严峻误区。</p><p>咱们看到，今世的生命科学研讨始于“特异功能”、“气功”等表象的呈现，研讨方向是依据人的内在性的自我操作所发作的人体效应以对生命的重要效果作出科学的阐释。可是，这一阐释的办法虽然学习于西方科学，因为生命科学的目标及试验的主体是修炼者自我的感受经历，表现出科学的形上性;而西方科学是物理性的真实办法，表现为形下性，使之形下性科学的物理办法与经历感受的形上性不具有齐备同一的对应性变成科学办法上的艰难。因而，对人体的齐备的物理丈量，特别是“气功”效果下的人体效应，是形下性的科学办法和技术水平所无法到达的。从哲学视点而言，因为研讨目标的操作办法发作于安闲的哲学思想办法(道学、儒学、医学)，不相同于西方科学的哲学思想，所以，我国生命科学的研讨实质上是逾越西方科学办法的研讨办法。之所以学习科学，仅仅是寻求以西方天然科学已然成功的物理办法作为知道论的参照系。可是，对人体安闲的内在性、对生命的可操作性(我命在我而不在天)之效应(例如大、小周天)的形上性的遍及原理的知道，因为西方天然科学并不具有本体论的安闲性，所以将对形上性的生命科学的推定显得力不从心。因而，只要在清晰区别形上性和形下性科学的不相同然后推定准确的研讨办法，以此进行生命科学研讨并终究树立形上性的科学办法，才是准确的研讨方向。</p><p>把生命科学研讨的安闲性放到其前史和逻辑的进程中反思，能进一步了解我国古代生命科学不相同于西方天然科学的实质性。任何科学的发作都有其安闲的本体论支撑，例如西方的科学发作于古希腊的哲学。可是，所谓的生命科学研讨的目标却是早在几千年前发作于我国的古代哲学文明中的中医学及儒学和道学当中，如上所论，生成其形上性生命科学的哲学是与西方哲学截然不相同的形而中论。明显，关于人的存在，不管我国哲学的形而中论仍是详细的生命科学办法，也不管是其表如今前史中的和逻辑中的统一性，都不相同于西方的哲学和形下性的天然科学系统，所以，不能以西方哲学和形下性科学的办法对我国的生命科学办法进行简略的推定。</p><p>明显，不能以西方的哲学和天然科学的办法推定生命科学研讨的终极价值，因为人类现已有了适当长的和丰厚的社会前史来了解人类的曩昔，而这一了解并不再是个别的经历，而是常识的沉淀，人类现已总算在遍及含义上发现对终极关心疑问的处理绝不是任何物质性的满意所能到达的，也不是西方的科学办法所能完结的，而有必要寻求其它的路途。恰是在这么一种只要在今世才干导致遍及性一致的文明空气中，今世的“气功热”和生命科学研讨才变成也许。如上所述，西方科学是十足的形而下的产品，而我国古代的生命科学研讨和修炼实习却是形而上的产品，所以，西方的形而下与我国的形而上的哲学和科学的融通，是今世生命科学研讨中的潜在的哲学艰难之一。</p><p>现实标明，以西方哲学和科学办法对生命科学的研讨办法，已在生命科学的本体论许诺中发作了知道论疑问，因为把人的存在作为纯物质性存在因而用物质试验的办法以证实例如外气 \n、内气等效应性疑问的研讨， \n实质上是用科学办法对人体科学研讨在办法上的限制。应当以为，在有关的物质能量变换的层次上使用科学办法辨别人体的物理效应，在办法上是有用的。可是，对发作这些物理能量效应的本体上的操作办法和本来质性的研讨，特别是安闲于存在者当中的知道自为所发作的形上性的生命科学效果，今世天然科学以物理性和真实性办法所进行的研讨，因为其被时空的参照系所限制，将是适当艰难的，乃至是力不从心的。生命科学研讨将标明，今世的生命科学研讨不具有与前史上别的部类的科学研讨相同办法的研讨，其应当是哲学和科学的结合──以新的科学办法并在其间进行的研讨和推定。</p><p>关于科学研讨中的知道论疑问，爱因斯坦曾指出：“即使是有英勇精力和敏锐天性的专家，也能够因为哲学上的成见而阻碍他们对现实作出准确解说。这种成见──至今还没有灭绝──就在于信任毋须有自在的概念结构，现实自身就能够并且应该为咱们供给科学常识。这种误解之所以也许，仅仅因为大家不容易知道到，通过验证和长期使用而显得好像同经历资料直接相联系的那些概念，本来都是自在挑选出来的”(《爱因斯坦文集》第1卷，第22页)。爱因斯坦指出的疑问， \n有遍及性的含义，在今世的生命科学研讨中显得更为重要：因为物理科学办法与生命科学效应的能量变换的时空非统一性艰难，此刻，以形下性的科学办法对形上性的生命科学表象的解说，不光显的顺理成章，并且不利于生命科学的实质性研讨。所以，哲学思想的死板，是其根本疑问。虽然已有专家指出，生命科学的研讨将股动科学革新，完结科学办法的内向性转化(见辛立洲: \n《二十一世纪的明珠──我国文明简论》天津人民出版社，1994年4月第1版 )。 \n但并没有导致生命科学研讨者的广泛留意。咱们看到，在如今的生命科学研讨中，因为物理科学办法运用于生命科学研讨由形下性向形上性转化办法上的艰难，把人体气功效应中的不能被形下科学所推定的表象称为妖言惑众、封建迷信等时而有之。更有甚者， \n以“气功”表象与形下性的科学原理的相悖为理由而加以否定。之所以发作这些知道，除却知道形态方面的因素而外(因不是学术疑问，故不在这篇文章的评论以内)，都是自觉和不自觉的堕入了哲学上的误区而构成的。因而，应当了解形下性科学的实质性。任何形下性的科学，都是主体对客体进行的办法上的变换，使客体能够以被感知的办法进行调查和了解，进而重构概念和理论系统。因为时空的办法化，办法变换的时空限制性变成形下性科学中的艰难疑问。在形下性的科学研讨中，为打破时空限制性，测验仪器被不断改进，数学──时空办法化的根本东西不断被新的数论所替代，使主体对存在的知道向深层开展。可是，形下性科学办法的时空变换疑问却一直变成制约人的知道的根本疑问。自相对论面世以后，时空被引入知道论领域，可是，时空的根本疑问，例如时空在物理效应的办法中的效果、观测仪器的时空效应等疑问，没有导致研讨者的留意，而生命科学研讨最具本体性的疑问则是在科学效应的形上性与人体效应的时空办法的非对应性当中。关于这一形上性科学的实质性疑问能否导致研讨者的留意并在实习中加以处理，是今世生命科学研讨能否真实的称其为科学的实质性关键疑问。</p><p>今世生命科学研讨所发现的哲学疑问标明，不能在生命科学的研讨中把西方科学和哲学办法论作为齐备的坐标系。对科学的知道应消解这么的一个误区，即：科学是知道国际的东西，东西自身不能变成国际的代表，科学是知道真理的东西，而东西自身并不能代表或替代真理。</p><p>就西方天然科学原理的实质性而言，是思想以领域的办法化发作的对客体的物理性建构。因为其具有物理的外在的物质性，所以，到如今为止，还没有发现科学的领域和办法能与人体的练功效应齐备的对应性，以所谓的科学仪器的办法所进行的对人体效应的观测，则因为能量和时空变换的艰难，使对其所进行的解说多有勉强的地方。这些标明，以气功修炼所进行的经历办法和科学办法之间需求一种变换的办法，而这一办法的变换在西方的天然科学系统内是不能完结的。所以，西方的天然科学系统并不能对人体的练功效应处以终极价值的推定。</p><p>这么，将呈现下述的艰难：既不能把形下性科学的办法用于生命科学研讨，又要对我国的“气功”修炼的形上性办法给出科学办法上的证实。处理这一艰难的办法只能是在知道论的层次上处以界定，使人体的有关研讨于科学的办法中在必定的层次上以办法的变换办法为科学所容纳，然后把科学办法区别为形上性和形下性。以人体“气功”效应的自为性而言，假如以形下性的科学仪器对人体效应作测定，对“外气”尚能够某种物理效应或以物理真实进行解说，但关于“内气”，以物理效应或物理真实进行解说，就变成疑问。例如，在修炼道家内丹功的进程中，身体中的内气依据其练功的层次的不相同，感受到的内气“质量”大不相同，所循走的经络也不尽相同，小周天，大周天，玉液还丹，金液还丹等层次均具有不相同的感受和能量办法(如：热感、凉感、气重气清、有汗无汗等等)。又如，内气以时刻的不相同而循行于必定的经络(例如子时为复气在尾闾穴)，这一循行进程不管对任何人都相同，是一个天然的进程，可是，对经络的知道用形下性的科学办法进行的推定现已十分艰难，对这一进程的的可操作性的测定与对受试者的主体感受进行定性的形下性的科学办法化就更艰难。因为人是受试的主体，而人体的气功效应是主体的操作性，并不具有形下性的科学仪器对人体干与的决议性。所以，关于形下性科学的阐释而言，西方科学的办法并不具有仅有性，因为生命科学的研讨存在着办法化的定性与主体的感受同一性的艰难疑问。这一点使咱们回到休谟哲学。</p><p>\n\n\n\n\n\n\n\n\n\n\n\n\n\n</p><p>休谟哲学标明，关于人的主体性知道，主体的一切知道都不能供给牢靠的与存在的同一证实，所以，主体的一切的知道并不具有本体论含义。对休谟疑问的哲学反思，使休谟的怀疑论哲学推进了西方知道论哲学的开展。但到如今为止，西方的后现代主义以海德格尔哲学为代表办法依然俳徊于休谟的疑问当中。因为在形下性的科学哲学中存在这么的疑问，所以，妄图以形下性的科学办法阐解人体的“气功”效应，就必定遇到的上述的不行战胜的本体论和知道论艰难。</p>', '1', '1', '0', '1', '0', '2018-06-25 13:21:23');
INSERT INTO `ys_article` VALUES ('4', '站着不动能治病？站桩就是这么神奇', '28', '推荐,头条', '', '', '', '网络', 'admin', '', '<p>有句话说“求医不如求己”。因为“人体自有大药”。</p><p>生活中，人们已经习惯了一生病就看医生，人体真的这么脆弱吗?假如你不小心手划破一个口子，即使看医生也只是涂上消毒液再用纱布包扎好而已，其余的什么也没做。接下来伤口的愈合乃至于恢复到完美的状态，就是身体自己的事了。小到皮肤划伤，大到手术创口，无一不是如此。</p><p>人体蕴藏有巨大的自愈潜能，正是这种不依靠任何外力的人体自愈能力，人类才能在恶劣环境下得以进化生存。身体靠什么自愈呢?古有“天有三宝日月星，地有三宝水火风，人有三宝精气神”的说法，人体自愈靠的就是“精、气、神”。从中医的非药物疗法上看，针灸、按摩、导引、气功、拔罐、放血、刮痧等等所有这些方法都是在做同一件事情：就是借助一种方法疏通人体经络，使气血在人体内循环畅通。</p><p>从生命角度看，生命可以分为“形”与“神”两个部分。精、气、血、津液等人体基本精微物质组成了人的“形”，而人的精神、意识、思维活动等生命的主宰则称之为“神”。故有“无形则神无以附，无神则形无以活”、“形为神之宅，神为形之主”、“形与神俱，则寿蔽天年”之说。生命物质起源于“精”，生命能量有赖于“气”，生命活力表现为“神”。</p><p>生命能量赖以存在的“气”，主要是指人体的元气。元气是维持人体组织器官生理功能的基本物质，是人体生命力状态的综合反应。元气充足的人，对外界气候冷热变化有较强的适应能力，即使受到风、寒、湿、邪的侵袭，也有充沛的自我修复能力。</p><p>而元气不足的人，身体缺少了“战斗力”，对外界不良侵袭会特别敏感，也很难把“不良分子”赶出去。加上很多不良情绪、不良习性对身体造成的伤害，久而久之的积淀就会出现失眠多梦、醒来无力、经常感冒、面色无华、头晕头痛、四肢倦怠等症状，这就是很多人忽视的“亚健康”状态。</p><p>实际上，每个人都可以调动自身的潜能治病。站桩，正是通过“固本培元”的方式调动人体自身的潜能。只需简简单单的一站，就可以激发先天的能量，自我调理，自我修复，这就是为什么内劲一指禅对于慢性疾病调理恢复的效果会比较好。</p><p>给大家举个例子来说明。北方冬季取暖用的暖气管道，用的时间久了，管道转弯处最容易堵塞。这时最直接有效的办法，就是给管道注水打压，冲刷淤堵垃圾。假如压力不足，淤堵就很难冲开;压力加到足够大，管道淤积自然会疏通。假如把人体的经络血脉比喻成暖气管道，人体的双胯、膝盖、脚踝、臂肘等大关节就像管道转弯处一样容易受风、寒、湿、邪等侵害而形成淤堵。</p><p>气血运行不畅，人体就会有肿、胀、痛、麻、凉、酸、板七种感觉，这就是为什么说“痛则不通，通则不痛”的道理。内劲一指禅的马步站桩，之所以能明显觉察体内器官的气滞血瘀之痛，关键是长时间站立不动的姿势(弯曲的双腿支撑着上身，上身支撑着高抬张开的双臂)，能调动起全身血脉加速流动，碰到气滞血瘀之处流通不畅即产生疼痛，使深层次的瘀滞之处暴露出来。站桩，其实就是通过培补“元气”给人体“打压”，使其形成内向的一种“冲击力”。压力充足，人体就会气血畅通，自愈潜能就会增强，所有不适症状也会自然消失。</p><p>\n\n\n\n\n\n\n\n\n</p><p>简单的道理告诉我们，站桩是一种既能治“已病”又能治“未病”的不花钱的养生法，更是现代人摆脱亚健康状态的最简单的养生法。看似简简单单的一站，身体在一定时间内处于相对静止状态，就可以调动全身的气机，疏通经络，调和气血，使阴阳相交，加速新陈代谢，增强细胞功能，祛病延年。所有这一切，需要坚定的信念、恒久的耐力和不懈的坚持精神。</p>', '1', '7', '0', '1', '0', '2018-06-25 13:35:39');
INSERT INTO `ys_article` VALUES ('5', '宋新红：气功是从我的兴趣延伸出来的事业（上）', '10', '推荐', '', '', '', '网络', 'admin', '', '<p>当我们在谈论气功的时候我们在谈些什么?跟很多人一样，小编对气功也不是很了解，带着诸多疑问，我们拜访了闻名海内的高级气功师——同时也是广州中医药大学气功教研室的资深教师宋新红老师。相信在很多人心中，气功是一个神秘的存在。那么接下来小记者将带领大家一起揭开气功的神秘面纱，了解宋新红老师的气功之路。</p><p>简介：</p><p>宋新红，女，1962年3月，出生广西南宁市。气功专业硕士研究生课程毕业，攻读传统医学获博士学位，高级气功师。在大学从事气功专业教学三十年，对气功理论和方法，及优化教学有独到的悟性，对脑保健与健康、养生美容，临床心理咨询与调治有独特理念和健身健脑方面的实操技能;在教学、科研、临床等方面取得创新性成果，特别是参与国防科工委某研究所，做《人体脑功能控制调节》航天医学ET脑科学实验研究上，取得创新、突破性成果。1993年原学院上报省级专家评定为:高级气功师。曾是双学位、留学生导师;全国高校气功保健班主讲教授。</p><p><b>1气功是什么?</b></p><p>记：宋老师您好，现在很多人对气功还是不太了解，您能先为我们介绍一下什么是气功吗?</p><p>宋：气功是什么?在我和夏双全教授编著的《医学气功学》教材上写得很清楚了。简单地说，就是用古人留下的非常宝贵的一些简单的动作疏导和导引身体，叫调体，对呼吸吐纳导引，叫调息，对意识的导引练习，叫调心，即“三调”。(平形，和气，养神)，主动地对人体预防、保健、养生、调治疾病和增强我们自身功能都非常实用和非常简单的方法，我们要做的就是认识它和坚持练习，功效就会出现了，大道至简，志在与恒哦。这也是几千年来这种经济又实惠的调节方法能够源源不断地流传的原因，不管时代怎么变迁，人们都需要它。</p><p>现代的医疗，你们也知道，如果一个家庭有一人生所谓的大病，不生病则已，一病则几乎就破产了;一场大病下来，全家倾尽人力物力，也不一定能抢救回来。如果通过老师的几十年的教学和你们学生们的宣传，大家都能认识到改善自身的内环境和提高身体素质的重要性，并且有意识的加强自我保健，把古人留下的简单实用的调节方法变成自己的生活习惯，不但能够提高自己的生存质量，国家也减少了这方面的支出，对吧!这是一个非常好的方法。</p><p><b>2气功的功能</b></p><p>记：随着现代社会的发展，人的体质也都在不断地在变化。</p><p>宋：最近新闻报道的一些数据表示，许多疾病都有年轻化趋势，例如：糖尿病，心脑血管，骨关节和便秘等。现在的青年人，甚至是小孩子，会有多少时间在室外活动呢?“低头族”是现代的新名词。大人玩，学生玩，甚至祖孙三代都在玩手机。有的报道：连续玩十几个小时人甚至会猝死的。随着现代科技的发展，人的社会性活动会减弱很多，而虚拟性会增加。社会性就是人与人直接的一个沟通，人与自然的沟通。而现在这些现象的出现其实是不太符合自然规律的，可能当下大家都不觉得这样有什么不对，因为现今社会好像大家都是这个样子。但从中医的角度看，生命是一个整体。过了一段时间，当你觉得身体不舒服了，到处去找原因却不知道因在哪里，其实这个因有可能在几年前就种下了，它或许在人体内潜伏很长一段时间，但那个时候人们并没有注意这些。想要改变这种状况就要有意识地提高人们的思维高度和拓展看问题的宽度，这样才能保证你的健康。因为人是一个很庞大的复杂的巨系统。人们都觉得宇宙很浩瀚，但其实比人也是一个小宇宙。人体有多少亿个细胞?多少亿个脑细胞?现在医学讲的九大系统，循环系统、免疫系统、内分泌系统、呼吸系统等等每时每刻都在新陈代谢中，大脑这个统率了解它们吗?能把它们管好吗?它们都听指挥和调节吗?系统之间能量分配均匀吗?中医抓的就是这个相对的动态平衡，身体这个巨系统是不是在规律地运行?不知道。五脏六腑调节好了没有?不知道。每个细胞的凋亡、再生正常吗?有没有主动调节积极的信息频率参与，不知道。这么多不知道，人体怎么会健康呢?所以就要打问号了。</p><p>记：就是气功能够教我们认识自己、去管理健康。</p><p>宋：对，气功的方法是根据人的形、神、气来组编的。简单的说形，就是你的身体，神，就是你的意识.精神.情志，气，就是你的呼吸，与外界交换的通道。气为血之帅，没有了一口气，人体如何运行呢?那么气功就完全体现在这几个方面，每个方面都有非常具体的方法。书上叫做练功三要素，具体方法就是用来保护你的神、形、气方面，按照方法去坚持做了，你就会对自己多了解认识一些，得到一个相对平衡的健康的状态，那么你的思维、你的生存状态能进入到一个符合自然规律的良好状态。</p><p>记：就是说我们在练气功的时候就加强了我们跟自然的联系。</p><p>宋：对啊，不止是这样，它是加强了你回归自然，得到自然能量的滋润，寻找到万物归一回家路的频率和通道，当人们在社会上行走，很多时候会迷失自我，只知道一个劲往前冲，所以练气功可以帮助人们回归自然，并在心静的时候找寻自我。曾子说：“吾日三省吾身。”练功除了一些方法是练习与自然沟通获取自然能量，内观自我也是一种静功，反观内省也是其中方法的体现。日省我身，在练功后，相对安静的状态下，人是比较清晰的，此时，反省一天自己的所作所为，做了哪些努力?哪些是符合自然规律，哪些做的不够好的有待改善提高的。哪些地方超出和偏离了自然规律，就要进行反省和微调。练功，就是每天不断的调节改善自己的行为和思想去合符自然规律。除了静功调神之外，调息和调体的方法也会教你的，气功其实也是一个很庞大的系统。</p><p>记：感觉现在的人都很容易浮躁，想要安静地读一本书都很困难的样子，看来看去，翻来翻去还是那一页，就很难静下心来。</p><p>宋：人是社会的人，必然受到这个社会大环境的影响。但人的思维意识是可以培养的。现在很多的疾病都是生活方式导致的，是思维模式导致的。为什么说我们要学习古人大德圣贤智慧?现在中国国家领导人提倡学习中国的传统文化，它的精髓在哪里?大智慧在哪里?人们的思维模式是可以培养的，培养的话就要进行练习，修炼，每天都微调，慢慢的就是你的习惯思维模式了，人是有双重身份的，一个是社会人(在社会公共场合时)，一个是自然人(在自己空间时)，要有意识的调控好转化好角色。比如，作为社会人的时候，遵守一切社会法规，学习如何掌握技能进入社会为社会服务，所以使命任务就是努力学习，要全力以赴地去学，不耻下问的去学，要有一日不学一日不食的精神。儒家著作有一句：“为学日增，为道日损”。为什么为学要日增呢? \n做为人，活到老是要学到老的，你们作为学生，每一天都要学习和掌握新知识，学了许多知识就可以慢慢变得有智慧一点;回归自然叫为道，为道日损，就是作为自然人时，要放下一些欲望，逐步的体验到自然的规律。欲望减少一点，快乐幸福感会增多一点，会使人健康，因为，许多痛苦的根源是欲望太过，叫“求不得苦”。每个人都要学着慢慢的放下一些欲望导致的太过的追求，人的实际需要的有限的，欲望是无止境的，学着调控自己的欲望，是需要很高的智慧和静心功夫的哦。如果能做到这样，你们就达到相对平衡了，是健康的了，对不对?为学，以你们为例，你们作为学生，就要努力学习，作为自然人，要想健康，要遵循自然规律，如黄帝内经第一篇：“上古之人，其知道者…”。就是要人们遵循昼夜更替，日出而作，日落而息。像四季的变化，四季的饮食，都有一个相对平衡的原则。你们要遵循自然的法则，不能一天只低头看手机不去运动，大脑好像动很多，大量的习惯的接收的多是碎片化的信息，都很短很快，养成了这样的思维模式，所以就太浮躁啦，这也是不健康的。所以你们回归自然就能知道，自然就是这样变化，反省自己那方面不够好，哪方面会太过，这样就叫做“人法地，地法天，天法道，道法自然。”自然的运行需要相对平衡，所以相对平衡也是中医经典《皇帝内经》的核心啊，不平衡就病啦，所以需要拿药来进行调整。气功中调神部分就是要回收意识和静心一会，在练功里面调神是最难的，是需要在老师的指导下学习训练的。放松安静一会，了解你自己，认识自己，每一天都要了解认识一点，放飞出去，收回来一点。这样又可以跟社会相融，又可以回到自然，这个过程就是调控的训练，专门有功法的。几十年修炼后，现在老师很享受那种静态，静态当中有更多更美好的能量。</p><p><b>3我的气功之路</b></p><p>记：老师您介绍了那么多关于气功的方面，那您是什么时候接触气功的呢?</p><p>宋：什么时候啊?老师大概是在十几岁吧，就喜欢到空气好的舒服的地方。一开始老师体质不太好，先天体质比较弱。因为身体不好，又不想住院。那时家庭条件还可以，但就是抗拒住院。无意间有机会去运动，身体健康得到了改善，后来也参加了系统的训练。生病少了，体质增强了。在市.省队专业训练中参加过三次全国赛，代表省青少年队都拿了第二名。专业系统的体育训练，使我对人体机能和身心的潜能力有了比较深切的认识体验，在大学学习的过程中，有一个气功研究小组，自己有兴趣，在学习过程中遇到了好老师，就转而主攻这方面了。有的很有造诣的老师们和社会上的师傅们都给了我很大的教导和鼓励。自己也觉得对人的健康和生命的认识有帮助的，我就去学习和研究了，时至今日几十年过去了都一直在坚持。练功之后觉得是一片新的天地，轻松愉快，自己还可以通过练功来解决生病的问题。疾病初始，自己先用功法技能来调治，例如，感冒发烧也试过调治不需要吃药和打针，自己就可以解决。对自己有用，对家人有用，所以我就继续练下去了。七八十年代，那时我所在的武汉学校的学院里的领导和老师挺有开拓精神和远瞻目光，在我们那个学校设置了武术气功系，气功教研室，气功专业课，气功公选课，八十年代，在武汉那边开展得非常好，做了许多有价值的实验研究。</p><p>记：老师您家人对你去学习气功是持什么态度的?</p><p>宋：我家人在最初十多年的时候不理解、不支持。社会上对气功一会狂热，一会反对，跌潮起伏比较大，外面的人不理解甚至父母当时也是反对的，但自己坚信自己的选择，因为我在其他方面表现得还比较优秀，所以父母并没有坚持反对我的决定。我在武汉和夏双全教授一起做了十几年实验，一有成果我就寄给父母，体验到好的保健方法就讲给他们听再教他们，久而久之，我教的那些方法对他们有用了，他们也看到我的体质增强了，以前比较容易得的疾病通过练功也得到了改善，所以我很开心;而且把自己亲身经历不断地耐心地去与他们沟通，希望他们也能够得到健康，是发自内心的对他们的一种爱，所以有信心和耐心，潜移默化的持之以恒的建设好自己，才能帮助别人。现在家人都受益匪浅，都注意预防保健了，有的家人朋友做得非常好，也有值得老师学习的地方。一家人都形成了相互学习分享的这个习惯，大家在一起时都会交流各自的好的经验，所以在他们心目中，老师还是相当有信服力的。</p><p>记：老师，请问之前您的父母为什么持反对态度呢?</p><p>宋：当时我在自己的专业领域有一些成绩，社会上得到了一些荣誉，父母对儿女的希望就是平安健康，有个安稳的工作…，当我选择气功专业，这在当时看来视乎是放弃原来的工作，而选择一门有争议的工作。再加上修行修炼很是清苦，父母也怕我吃苦。其实老师进入气功领域思想也挣扎过的，因为20岁左右的年轻人都有其特有的身心特征嘛，好表现，希望被认可啊。但修行修炼是要学会调控自己，包括欲望等。老师是幸运的，修行开始就遇到好老师，一路下来有很好的造诣很高的师傅的传授，也接触过很高水平的科学家和医学家，被他们的精神气质潜移默化的感染和熏陶，原来的魔鬼式专业体育训练让老师形成了一种思维模式——只要坚持，不断完善提高自我，没有什么是做不到的。耐心是福啊。</p><p>记：我们知道在体育这个方面训练的话，这种体育精神就是追求向上，更快、更高、更强。</p><p>宋：对，在十几岁体育竞技训练领域，让你对人体的极限不断反复的挑战和战胜。这个对我以后的气功修行也是非常有作用的，一种坚韧(坚强与忍韧)的精神，对你的精神起作用，所以不轻易放弃自己所选择的路，在修行修炼中就会自觉一些，希望探究认识得更加深刻一点。</p><p>记：您一开始是体育专业的，然后您转而去修行，我们知道修行就是会追求那种静心的境界。在这个过渡中有遇到什么问题吗?</p><p>宋：竞技体育训练的时候，每天训练是很累很累的，一般累的话呢，就被动地吃饭睡觉了，老师喜欢到外面空气好的地方啊，躺下来换换气，主动的放松一下，这样能快速的消除身体的疲劳，对体力精力恢复很有帮助，但当时不知道这也是气功的一种。大学第一年，就很幸运遇到气功研究小组，每天练习并开展气功领域的科学研究，我就去参加了，后来也就系统地坚持练习下来。修行初期过程中，拒绝了各种各样的诱惑，一直专心修行。即使这样，时间还是不够用，因为，气功修行领域涉及的范围很广，要想自信，就要对不同门派的的功法进行了解，例如，佛家、道家都要进入很深的了解，不了解不体验，就不可能有自信，修行是万法归一。中国的文化几千年下来佛家、儒家、道家这些文化都相融的，如果把它们分解剥离抽出来，中国的文化就支离破碎了。就像你们了解中医传统文化里面，有许多道家文化。医书有记载：“不知易者不为医。”《易经》就是道家的重要经典。</p><p>记：对，不完整了。</p><p>宋：不完整了，实际上每一个门派的修行都有相融合的地方。例如，你要修行要达到高层次高境界，就要放松静下心来，这样心里面才能接触到很深层的东西。道家，佛家和气功等，都有的入静的坐式修行方法，就叫共法。</p><p>记：就是原理都一样。</p><p>宋：对，就是都有放松入静的过程，都有盘坐姿势，这叫共法。共同的方法，但切入点不一样。不同门派切入点不一样，走到最高点，最高点只有一个，就一样了。气功练习从放松入静这点用在人们的保健和调节健康上，因为，在很深的放松入静时，人体有自愈能力，从这方面可以对生命进行探索。</p><p>记：殊途同归。</p><p>宋：对，如同登山，上山的道路不同，山顶的至高点只有一个。上山的路有很多条。道家从这一条路上来了，佛家从那一条路上去了，还有不同的门派从不同的路上去了，但上到最高的点是一样的，这就是天人合一、万法合一、殊途同归的道理。就像我们大家一样，大道同归，有生就有死，谁逃脱的了死?但是你没有完成你的使命，死是没意义的，你这个能量是要完成使命的，找找你的使命是什么，然后就不懈努力达到使命的目的，这才是人们都要做的事情。你们和老师一样幸运，很年轻在大学学习深造，有非常好的学习机会，能遇到非常好的师长，例如，在修行方面老师就得到张厚存，邓述祜等老前辈们的亲自传授指导，在专业学科研究方面得到冯理达和钱老(注：钱学森先生)等的支持好鼓励，在医学领域得到邓老(注：邓铁涛先生)的支持并在老师的教材中题词鼓励，所以老师很幸运。其实，人与人之间的交流也是一种互相能量的交流和精神的传承，就像老师从长者们那里得到的正能量，将它们传给你们，那么你们青年人呢，积极乐观向上才是正确的。</p><p><b>4气功的创新</b></p><p>记：那之前您说了，气功是一种很古老的文化了，它融合了很多古代的文化，那么气功在创新方面，就是在现在这个发展中会不会有创新啊?</p><p>宋：有啊。在气功的发展过程中，针对不同的人群都会有不同的气功方法的变化调整。例如，一，结合你们大学生的情况，学校开设了不同的班种的气功课程，(针灸七年制、全英班、境外班、康复治疗专业等等)，二，结合现代社会上的人们的需求，国家体育总局推广的健身气功就是在古人的气功功法基础上改编的。在老师看来这些都是创新。创新是在了解和继承的基础是进行的。对于气功学科来说，第一是要了解继承好，要真正把古人的实用的方法扎实地继承下来。为人们的健康和快乐的生活服务，在继承的基础上，再提高，再根据当下社会的变化做一些创新，就很好了。不要想着一口气成为吃成胖子。中国历史悠久，有很多优秀的文化和方法，例如，七十年代在湖南马王堆出土的战国的文物[行气玉佩名]等。 \n所以你们要努力学习，了解中国的传统文化和和继承古人的方法，在这个基础上提高了，也是创新。没有基础怎么创新啊，对吧!例如，建楼房，没有基础稳固的地基，怎么建楼房啊? \n建了也容易倒塌的，所以不能急，基础打好，耐心是福。修行这条路，最终调的是心/神，意识，意念，念力的变化，是从耐得住寂寞与清苦，而达到享受寂寞和清淡的境界。中医文字记载：恬淡虚无，真气从之。</p><p>记：那现在的健身气功应该也是一种创新吧。老师您练的这种气功跟我们这种的健身气功有什么区别?</p><p>宋：健身气功，是国家政府部门根据古代相对的气功功法改编后推广的四种功法，主要是以调体动功为主，形体为主;所以名称是健身气功。我们练的功法，更注重气、形、神三个方面，非常符合中医的生命整体观;调形，有意识的疏导肢节调和气血，疏通经络。一些动功强壮肌肉才能维护你肌肉的饱满，卫气才充实。调息，有意识的疏导导引改变呼吸节奏和吸氧量，根据那不同的需要、目的，有不同的吐纳导引节奏。比如说，体质差的时候，气虚的时候，以摄/纳为主;即吸为主;体内浊/废气多的时候，排和泄为主，吐为主，即呼为主。这也跟中医治疗原则，气功和中医的原则是一样的先排再补。</p><p><b>5气功的“天人合一”</b></p><p>记：像中医它们里面说“天人合一”，在气功这一方面怎么体现呢?</p><p>宋：气功 \n“天人合一”的体现。一，与自然交换气，练功时首先是选择外面空气好的地方，花草多，树木比较茂盛的地方。人需要树的、花草的空气、负离子、氧分子，树要我们的什么呢?我们的废气像二氧化碳这些，在这个层面上就是天人合一啦!二，与自然交换能量，滋润自己。如果功能提高了，能量层次也会不断变化。人是一个能量体，一个高强度的化学能量体，我们体内有生物电.磁,磁场和其他许多的生化物资，微量元素等。人所需要的能量自然空间中都存在，因为我们是大自然的产物，跟大自然一样，气是构成我们人体最基本的物质，也是构成我们宇宙的最基本的物质。三，利用五行相生时空场，时间方位能量场。练功时，找相生的方位。比如，五行属水的人，《周易》六十花甲纳甲表或(从出生年月日时)中可得知 \n，练习选择场地时，面向北方，亥之时能量场比较好。如果达到一定境界时，体会能量变化越来越细，所以说为什么高功夫的人能量场大呢? \n他与自然契合，跟所处的社会契合，跟自己契合。这也是天人合一。其实我们人体是开发自然的天人合一状态，只是紧张的生活模式导致没有打开，没有找到频率而已。通过一些气功的方法练习，可以帮助实现。例如，人会有七情变化，看到喜欢的东西就整个人都很开心，遇到不开心的事就会难过。这就是生物体内的一些化学物质在起作用，那它为什么起作用呢?这涉及到大脑的统率作用。而这些化学物质都是中性的，就像六气，自然界的六气没有好坏，你体内的生化物质也没有好坏，有好坏之分是因为你的内环境和积极思维/意识或消极思维/意识导致的。自然界的六气变成六淫之气，也是因为个体体质变弱了，免疫功能下降。就像现在秋季微风吹来，对体质好的人感觉很清爽、很舒服，可是对体质弱的人呢，感觉寒凉，冷，它就是邪风邪气，是不是?这样的话体质弱的人就知道应该加强那些部位的锻炼啦!不是只苛求外界，而是要把自己内在内环境建设好。六气是自然的现象，本身体质弱了，外面美好的四季变化就都享受不了。</p><p>记：老师，我发现你是一个非常爱学习的人，就是很多方面都达到刚刚讲的天人合一的境界，那对于我们学生来说，最重要的是学习啊，那您有什么特别好的学习上的心得跟我们分享吗?</p><p>宋：前面说到过，老师的体验是，人活到老，要学到老，生命才有意义。每个年龄段有每个年龄段的学习任务。对于你们学生来说，现在这个年龄段非常宝贵，大学这几年是一辈子当中最美好的时光，学生要自觉做儒家弟子，把主要的精力投入到学习中，争取优秀的成绩，争上游，是你们的使命。但要明白奋发努力只是前提，不管结果拿不拿得到好成绩，只要自己努力了。了解自己调节好健康也是同等重要，如果身体不好，怎么取得好成绩?首先要培养自己有一个保健的思维意识，把气功许多实用的方法，或者其他的健身方法，选择适合自己的为健康服务。健康了，取得成绩了，就是对父母的最大的孝敬!在健康的状态取得好成绩，就是你们给父母最好的礼物啊。传统文化不是讲孝嘛，孝为先，什么叫做孝敬啊?你有那么多病，孝敬吗?《孝经》里面说：“身体发肤，受之父母，不得毁伤。” \n要努力学好习，还要把身体搞好，还要快乐，对吧!本来很简单的事情，可是对现在的人来说多难啊!因为我们人目光短浅，看不长远也看不到整体。你只有不断地努力，不断地充实自己，不断地通过努力克服战胜很多困难，取得自信，一步一步地向上调节，慢慢的量变到质变你才发现你会散发出一种浩然正气。</p><p>记者说：</p><p>\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n</p><p>当记者们叩响宋老师的办公室门时，就听到了舒缓的佛教音乐，只见老师在椅子上以莲花座的形态闭目养神。感觉像是我们不经意惊扰了坐落凡间的精灵。与宋老师的交谈真的是一件很享受的事情，像是非常亲密的长辈，轻声地亲切地和我们交谈着。在这将近两个小时的交谈中，她一直面带微笑，即使是在谈论到人们对气功的误解、谈到生死观的问题。在随后的采访中，让我们一起来听听宋老师对于生死，对于气功的“非科学化”，对于气功的推广等问题的看法，敬请期待。</p>', '1', '1', '0', '1', '0', '2018-06-25 13:39:32');
INSERT INTO `ys_article` VALUES ('6', '【人物】|宋新红老师：气功是从我的兴趣延伸出来的事业（下）', '10', '', '', '', '', '网络', 'admin', '', '<p>在上次的采访中，我们知道其实气功并不神秘，它在人们的日常生活中大多扮演着保健的角色。那么这次让我们一起来了解一下气功作为一种治疗手段的角色，并且一起来倾听宋老师对气功推广的看法。</p><p>记：感觉现在的人都很容易陷入浮躁的心境。</p><p>宋：人是社会的人，必然受到这个社会大环境的影响。但人的思维意识是可以培养的。现在很多的疾病都是生活方式导致的，是思维模式导致的。为什么说我们要学习我们要学习古代的圣贤智慧，现在中国国家领导人提倡学习中国的传统文化，它的精髓在哪里?大智慧在哪里?人们的思维模式是可以培养的，培养的话就要进行练习，修炼，每天都微调，慢慢的就是你的习惯思维模式了，人是有双重身份的，一个是社会人(在社会公共场合时)，一个是自然人(在自己空间时)，要有意识的调控好转化好角色。比如，作为社会人的时候，遵守一切社会法规，学习如何掌握技能进入社会为社会服务，所以使命任务就是努力学习，要全力以赴地去学，不耻下问的去学，要有一日不学一日不食的精神。儒家著作有一句：“为学日增，为道日损。”为什么为学要日增呢? \n做为人，活到老是要学到老的，你们作为学生，每一天都要学习和掌握新知识，学了许多知识就可以慢慢变得有智慧一点;回归自然叫为道，为道日损，就是作为自然人时，要放下一些欲望，逐步的体验到自然的规律。欲望减少一点，快乐幸福感会增多一点，会使人健康，因为，许多痛苦的根源是欲望太过，叫“求不得苦”。每个人都要学着慢慢的放下一些欲望导致的太过的追求，人的实际需要是有限的，欲望是无止境的，学着调控自己的欲望，是需要很高的智慧和静心功夫的。如果能做到这样，你们就达到相对平衡了，是健康的了，对不对?为学，以你们为例，你们作为学生，就要努力学习，作为自然人，要想健康，要遵循自然规律，如黄帝内经第一篇：“上古之人，其知道者……”就是要人们遵循昼夜更替，日出而作，日落而息。像四季的变化，四季的饮食，都有一个相对平衡的原则。你们要遵循自然的法则，不能一天只低头看手机不去运动，大脑好像动很多，大量的习惯的接收的多是碎片化的信息，都很短很快，养成了这样的思维模式，所以人们就容易浮躁，这也是不健康的表现。所以你们回归自然就能知道，自然就是这样变化，从而反省自己哪方面不够好，哪方面会太过，这样就叫做“人法地，地法天，天法道，道法自然。”自然的运行需要相对平衡，所以相对平衡也是中医经典《皇帝内经》的核心，不平衡就病啦，所以需要拿药来进行调整。气功中调神部分就是要回收意识和静心一会，在练功里面调神是最难的，是需要在老师的指导下学习训练的。放松安静一会，了解你自己，认识自己，每一天都要了解认识一点，放飞出去，收回来一点。这样又可以跟社会相融，又可以回到自然，这个过程就是调控的训练，专门有功法的。几十年修炼后，现在老师很享受那种静态，静态当中有更多更美好的能量。</p><p><strong>气功“非科学化”</strong></p><p>记：现在很多人认为，练气功是一种非科学的做法，您怎么认为?</p><p>宋：看你怎么理解，也看是从什么途径接受到的信息。国家为了人们的健康在《全民健身计划》中创新并推广了健身气功。人们是接受和热爱的。你们学生每年也都有这方面的比赛。这是科学还是非科学?众多古籍经文中记载了练功的的文字，几千年来一直对人们的保健健康养生康复起着经济实惠的作用，这是科学还是非科学?老师在大学教《医学气功学》三十多年了，学生们都喜欢。不光是这个学校。老师有时候受邀请也去一些不同地方和国家，或一些国外学生、医师等到中国来求学，他们也非常喜欢。每个人都需要，因为每个人都希望了解自己，关心自己，气功有这些功法。气功有三调，人们比较容易接受调体，一些以动作为主的属动功类的功法，因为看得见，比较难接受调心/神属静类的功法，因为是意识的训练过程，看不见，效果见效也比较不明显，调息也一样，看见的吐纳，腹式呼吸容易接受，但细微进入调息入的静类也传授得不多。所以这对专业教师要求很高，真正的气功老师要有点真功法哦，我在今年学校的师德征文里写到，给学生一瓢水，你要有一桶水，这句俗语是大家认同的。但做为气功老师我认为一桶水是不够的，你要给学生一瓢水，你得建设好自己要像一条宽广的河流，你才能够活学活用，通俗易懂地传授学生。否则只能是比较呆板地去教学。所以这对于气功老师的要求是很高的。其实这和做人一样，有的人对自己要求很高，有人要求很低，那么就看你自己的选择，对吧。就像电脑，表面上都一样，内存不一样啊，所以这就要看个人的一个追求，是成为一个向古代的圣贤学习看齐的智者还是成为一个碌碌无为的人。不管是在哪个领域，都要对自己有所要求。</p><p>记：老师，现在有一个说法是：“练气功的人，不宜随便给人发气治病。”如果违反了会怎么样?</p><p>宋：嗯，一般来说，练气功是培养和保护我们精元之气，为健康服务。 \n不建议也不提倡随便给人发气治病，如果违反了，反伤其身。但古籍有记载，体内气充实的时候，有这个技术，顺春夏生发之际，利用气能量也可以助人为乐，有余力则助人。但是如果没有经过训练练习，没有发外气技能，是不可以做的，这点一定要注意,会耗元气的。</p><p><strong>气功的推广与教材的编写</strong></p><p>记：您怎么看中国对气功推广的一个文化氛围?在社会，对气功文化这个氛围可能会有误解，您怎么看?</p><p>宋：误解，历来都有啊。老师觉得现在已经比以前改善多了。人们的思维也开放多了，现代化通讯工具接受信息来源也容易多了，老师也在教学第一线教了几十年，在社会上多少也起到了一些好的效应，是不是?如果你们真的希望古人这些宝贵方法让众人认识，为他们健康起作用，首先你们要认真学习，坚持练习，随机缘再将自己的亲身体验传授给需要的人们就非常好了，如果结合你们针灸专业再用现代化通讯设备传播给更多的人，帮助他们掌握这门技能，为健康服务就更好了，是一件大功德哦。气很重要，《黄帝内经》里面有各种提到气将近3000处可见其重要性。但我们认识得还是比较少的，例如，你吃饭进去，气不足的话，消化代谢都不好的，食物转换率不高，所以气很关键，是大自然的慷慨地馈赠。所以这些都是好东西，你们把它学好了，自己用好了，再去教别人。不要自己还没有掌握，就去教别人，别人怀疑上来，你自己也怀疑了。误解在很多领域都有，但起码我们从自己做起，将对有用的帮你健康的气功方法，真心去教授别人。你健康了父母会开心，你也会得到一个惊喜，“哇，我感冒少了，饮食香甜了，睡眠质量高了，眼睛看物清晰一些了，我怎么会变健康呢?”慢慢就会得到改善的。其实很多东西，不要太过于强求，有帮助别人的心就好了，这些会渐渐得到人们的认识。因为温饱以后，大家都会去寻求怎么更健康的生活方式。 \n现在领导人重视提倡发扬中国传统文化的推广，气功又是里面一个宝贵的瑰宝。等到未来科学再提高发展到一定时候，都会认识到的。老师有足够的耐心哦。好东西始终都会被人认识到的，只是时间关系罢了。老师用气功方法保健，同学们觉得好，朋友们也觉得好，老师我是受益者，会一直坚持。老师的做法是，面对质疑和误解，始终是微笑面对。</p><p>记：像老师的学生，现在也不只是我们中国的了，也有其他国家的学生，像爱尔兰等各地都有。</p><p>宋：从老师大学毕业做教师职业至今几十年，是教了许多学生其中也有许多国外学生。真的各地都有。</p><p>记：现在也可以说是，气功逐渐地走向国际化。</p><p>宋：嗯，其实不是现在，气功早就走向国际化走向世界了。老师在80年代初一做教师开始就有外国人来中国学习气功，一直都有教国外学生，遍及许多国家都有，有的现在也有了他们自己的学生。比如，爱尔兰中医学院，就是院长带队来广州中医药大学学习气功，在他们学院也开设了气功专业，因此还是他们学院招生宣传的一个亮点呢。即使这样，也是他们自己努力的结果，老师是一名普通教师，坚持着教师的本职工作，只是多了一种开心地享受与学生分享教和学生命互动的过程。成为教师，就是我的工作，使命，站上三尺讲台，把我的技术、技能传给学生。老师尊重和爱护你们学生，你们也尊重和喜欢老师，是吧!因为爱是相互的。</p><p>记：老师，您是怎么想到把这个经历和修炼的功法出版成一本书的?</p><p>宋：因为老师一直在大学教授《医学气功学》课程，教学都是很规范的，从研究这个专业开始，社会上就有很多很多诱惑的，但老师专注自己的专业放下了诱惑，扎扎实实地练习，体验，修行。把行之有效的体验教给学生，然后学生反馈过来的信息大部分都是积极的需要的，方法是众人智慧的结晶不属于我一个人的，有古人，有同道老师们，还有那么多老前辈们的，在老师这里只是验证了，受益了，针对不同的对象学生，也选择了不同的方法。那么全部验证过，成千上万的学生学习了，验证是好方法，为满足更多人的需要，将老师亲自练过验证过的实用方法才写成书。在正规出版之前，我们学校广州中医药大学各学院班种的学生先试用自编教材十多年，反复验证以后，专家认可了，才成为现在公开出版的高等院校学生用书。还出版了一套彩色纪念版的《医学气功学》，内容更丰富。</p><p>记：那这样的话，也算是气功的一种推广的方式。</p><p>宋：算吧，尽力而为比较系统地科学地传播。因为气功有它的特殊性，在调神静功部分，一般不建议学生自己练习，一定要跟老师先打基础;所以老师编著的教材必须上老师的课才可获得。老师从1999年开始，也参加气功专业领域国家的高校教材的编委，每隔四年更新一次，每次都参加了编写，十二五规范教材也参加。</p><p>记：老师，您在编写教材的过程中，遇到的最大的困难是什么?</p><p>宋：精力和金钱。查阅大量资料，汇总，功法图片拍摄筛选，电脑上编排等等，这部分得到一些气功爱好的国内外学生帮助，我和夏双全教授夜以继日的撰写编著，耗时耗精力耗眼力，那时眼睛是受到了一些伤害有点疾患。之后是资金。资金，得到了朋友及外国友人学生的支持，才有了你们现在使用的教材哦。</p><p>记：老师，您在练功的过程中，印象最深刻的一件事是什么?</p><p>宋：印象最深刻的就是练功可以改变自身能量场。练功中的一些微观变化很好奇，练功后一种无法言表的轻松舒服喜悦，还可以自己给自己治疗，给别人治疗。我十七八岁时，像你们一样正式练习练松通养心法才三个月左右，不小心手被夹伤了，用松通养心法调治，竟然好了。本来就觉得是很神奇的方法，自己把自己治好了以后，更加增强了信心。之后在不断的练功之中，身体出现的毛病，都是自己调整过来的。甚至在小孩身上也实验，生病时先试用气功调治，不吃药不打针，也调治好了几次。特别好的感觉，能体验到人体潜在的能力，记忆犹新。所以人要学会保健功法，健康时预防，亚健康时保健，生病初期加强自我调治和食疗，可能不需要到用药的地步。自己调不了，再寻求医药帮助。因为药都有一定的毒性，俗语：凡药都有三分毒。</p><p><strong>气功以外的兴趣</strong></p><p>记：老师，现在您在气功方面也取得了很大的成就，您有想过在其他领域有所突破吗?</p><p>宋：气功取得很大成就?是吗?我自己觉得也没有取得很大的成就。写书也是水到渠成的，也是一种经验的传承。要说成就算是人生的收获吧，老师学会了每天感恩的生活，尽量的能对人们有帮助。在老师看来，老师一直都和三、四十年前一样的，坚持练功，体察着生命的变化，每天反观内省形气神方面有没有偏颇，根据身体，年龄内外环境的变化，不断调节，尽量做到顺应自然，就跟自己吃饭一样的，是习惯，是生命一部分。也获得了自然的回报，相对比较健康，就此老师一直是心存感激的生活着。所以坚持认真教学，保证教学质量，你们学习有收获对健康有帮助老师感到非常快乐，就此，心里也感激着学生。至于你问有想过在其他领域有所突破吗，其实我们人体有这么多奥秘，特别是微观方面的，一些在练功状态下感觉到体察到的能量变化，很多不能说或者是语言无法表达的一些东西，老师还希望继续不断深入学习，已收集整理了一些修行练功资料，这些将来有机会老师可能再写与修行兴趣者一起分享。老师我只是一般的老师，几十年都是在教学一线最基层的老师和其他老师一样，只是认真敬业的做了本职工作，感受本职工作带来的快乐。</p><p>记：对，是会有这样的，有的时候，在练功时会有很多东西无法用语言去描述。</p><p>宋：哦，太多这种感觉了。感觉是丰富的，语音和文字相比而言要匮乏很多。有的是叫什么，内境隧道别有洞天。管子曰：“静则得之。”相对安静的状态，人体内的能量变化有很大的不一样，会分泌许多对人体健康有利的生化物质，体内更有一番美景。比宏观世界还要美哦。所以老师非常享受练功时的喜悦和充实感，通过这个方法，我得到一种快乐，一种内在欢喜充满的感觉，叫法喜充满。所以练功修行的人不会感觉孤独，不会觉得寂寞。每天时间都好像不够用，每天也很开心。老师希望你们也通过自己的练习寻找到自己内在充实的方法。</p><p>记：老师，您除了练功，平时还会有其他的兴趣爱好吗?</p><p>宋：有啊!弹古琴，登山呀，很多。</p><p>记：一般学习古典文化的学者，都会对书法、画画、古琴这种古典音乐有一种特别的兴趣。</p><p>宋：嗯，大概是因为它们有些相通的旋律频率。就说弹古琴吧，即使是每个单音都有一种魔力，可以把你与自然沟通，经常练琴3、4个小时，时间过去都没什么感觉。就像练功，享受这与自然沟通的能量滋润的过程，3、4个小时过去也没什么感觉。如果不练习只是呆着3、4个小时?即使一个小时，人就会烦躁了，想去找事情做。弹琴很专注，内心享受着这个过程，练功也是享受自然的一种能量滋润，其实都是一样的，充实了就舒服了。如果空站在那儿，与自然不相和，那不是很难受吗?也会觉得很浪费时间。书法也是非常好的，写的时候也是相对安静的。老师有时喜欢写“和气”两个字。可以正方去读，反着读‘气和’即五脏六腑气和平衡得健康，顺着读‘和气’,是心平气和，经常保持就是一种境界，一种功夫。正读和反读都是很美的，所以老师很喜欢这两个字，有时朋友喜欢也写了送给他们，有的说“啊，有老师的气场哦”。(笑)</p><p><strong>医患关系与生死观</strong></p><p>记：嗯，说到和气，近年来频发的伤医事件可能会让医学生对将要去向的工作岗位会心生忧虑和退怯，您对这个有什么看法?</p><p>宋：是啊。近两年特别多医患关系事件的新闻，甚至一些是非常恶性的事件。医患关系现在也是国家政府要解决的一个大问题，你们身临其中，更应该多关注。其实这是正常的心理反应，关心则忧则惧嘛。因为这两年来，医患紧张关系的报道很多，负面的更多。但如果是你追求的东西，是自己选择的专业，你就应该坚持下去。要明白任何事，任何工作都有其危险性。医生是救死扶伤的，是帮助病人的，是受到社会尊重的一个高尚的职业，如同教师职业一样。现在出现了一些问题困难，但时间会改变的一切，政府会改革，人们的素质会提高的。就世界先进国家来看，医疗工作者是受人尊敬的。因为你是治病救人啊，近期国内出现患者伤医致死事件，父母会担心，你们的话对社会的认知不多，就会动摇犹豫，是正常反应。所以你们要学得更扎实，做得更好，就把自己建设好，将来能够真的帮助病患。其实大多病患对医生是感恩的，感激的。你帮助他们，他们心里明白。不过也有个别医生做得不到位，或者有的病患有精神方面的问题等等。作为学生的你们，选择了真正想做的事情，没什么可怕的。哪样事情不会死人啊?都会的，你只要尽量把它做好就行了。</p><p>记：在练功的人的眼里，像您，是怎么看待生死的?</p><p>宋：生死是自然的现象，有生就有死啊，而且是人人如此，大道同归很平淡啊。但从生到死，整个生命的过程是非常美妙的，要学会珍惜和享受它。如孔子说：“未知生，焉知死。” \n在生的时间空间，利用当下的条件，把生的事做到最好，不要有遗憾，不要有后悔。我们都知道这个世界上是没有后悔药的啊。我所走过的路，不后悔，很自信;修行中也有了解死亡的功法功课，了解了就能比较好的面对。我的一个师傅就做到了轻松离开。也常听到一些例子，一些濒临死亡的人欲望缠身死不瞑目，那样多痛苦啊?生死是自然规律，是无法避免的。就像四季轮转，昼夜更替。生的事情做好了，你变得强大了，能帮助很多人，你就会感到幸福快乐。关键是生的事情你没做好，那是很可怕的事情，样样都放不下，要走的时候多难受?你样样都尽力了，明白了，走了就走了呗。</p><p>记：有的时候，在校园行走的时候，看到一些头发都白了的人，背着一个书包走在校道上面，那个时候就会觉得真的是“活到老学到老”，就会觉得有一种鼓励的感觉。</p><p>宋：是啊。生命是可以感动生命的，也是事物往往生的法则。你看到他，不认识他，可是他的一种精神就鼓励着你。就是积极因素，他那么年长，他还那么努力的学习，那我们年轻人呢更要努力。他们这样选择，去学习，他们的内心是充盈和丰富的。你不是他，你怎么知道他的快乐呢?“子非鱼，焉知鱼之乐也?”老师在你们这个年纪的时候，也有这种被陌生人鼓励的经历：“看到某人很优秀，心里好想像他啊。”。现在这个通常叫做榜样的力量。每人都有感动别人和被人感动的过程。学习别人好的地方，然后面对困难，要勇敢一点。</p><p><strong>当下即永恒</strong></p><p>记：老师，您现在的学生也很多，有很多想要学习气功的，也有像我们这样正在学习气功的学生，您有什么话想对他们讲?</p><p>宋：第一开心和谢谢他们能选择这个方法去为自己的健康服务，第二尽量做到干一行，能爱一行，做好自己本职的事情，当下的事情。例如，学生学习的就努力学习，工作的就努力工作，然后要享受当下的过程(包括酸苦甘辛咸五味和一切境遇)。因为当下过了就没有了，所以当下就是永恒的。所以你们学生要学习好，身体好，把古人留下的宝贵财富吸取继承了，先为自己的健康服务，然后再帮助别人提升自我的能量。</p><p>记：好的，通过与老师的长谈，我们获益匪浅，真挚地感谢老师能接受我们的采访。</p><p>宋：不客气。</p><p>记者说</p><p>\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n</p><p>在采访开始之前，为了让同学们对气功有更加具体深刻的了解，宋老师给我们准备了很多资料。就像她所说的一样，“要给学生一瓢水，首先就要把自己建设成一条宽广的河流。”在采访过程中，宋老师一直强调自己是一个站在三尺讲台的老师，并且非常享受和学生交流的时刻。宋新红老师一直微笑地耐心地从容地面对所有质疑和误解。无论如何喧嚣，无论如何纷杂，她都以莲花般挺直的姿态，自在绽放。</p>', '1', '1', '0', '1', '0', '2018-06-25 13:43:43');
INSERT INTO `ys_article` VALUES ('7', '“神”气“牛”人李土生', '10', '', '', '', '', '网络', 'admin', '在中国传统文化和文人圈里多次听到李土生的美名与传奇故事，可惜总是无缘相见。甚至在他去年访美，莅临我定居的华府时，也阴差阳错地与我出差撞期。近日在加勒比海度假时，惊闻慈母蒙佛接引，排除万难以最快速度赶回北京奔丧。或许孝心通达神明，上苍赐予我采访李土生老师的善缘。', '<p align=\"center\"><strong>正文</strong></p><p align=\"center\"><strong>中华传统文化“十项全能”</strong></p><p align=\"center\"><strong>十年两套巨著前无古人</strong></p><p align=\"center\"><strong>天赋异禀致力扬德弘道</strong></p><p align=\"center\"><strong>薄己宽人乐于行孝尽忠</strong></p><p align=\"center\"><strong>作者：黄斯诺、陈正圭</strong></p><p>移居美国后的30年里，足迹遍布四大洲、五大洋，见识、采访、结交过太多各国的政治家、艺术家、企业家、学者等杰出人物，但没有一位能像中国传统文化促进会名誉会长、北京土生书院院长李土生这样震撼我的人物!结束一整晚的采访，疲惫地瘫坐在回住所的车里，闭目自问好几次：这人真“神”，他是“人”吗?</p><p>近几年来，我每次回国，在中国传统文化和文人圈里多次听到李土生的美名与传奇故事，可惜总是无缘相见。甚至在他去年访美，莅临我定居的华府时，也阴差阳错地与我出差撞期。近日在加勒比海度假时，惊闻慈母蒙佛接引，排除万难以最快速度赶回北京奔丧。或许孝心通达神明，上苍赐予我采访李土生老师的善缘。</p><p>李老师办公室里的大件宝贝：红木立体镂空精雕如意(左上)、玉葡萄(右上)、玉佛手(左下)、立体雕刻的九龙屏风与一对巨型大毛笔(右下)。</p><p><b>一身牛气 满屋宝气</b></p><p>一进李老师位于东四十条附近阳光都市的办公室，琳琅满目且件件价值不菲的中国艺术品便像磁铁般地吸引了我：玄关是一个巨大的木雕九龙屏风，屏风下是一座硕大的石雕山水，旁边摆着造型精美逼真生动的玉雕佛手;屏风背部，一对齐人高的木杆大毛笔挂在屏风两侧，底部是我见过最大的博物馆收藏级的红木立体镂空精雕如意;客座几案上陈列栩栩如生的玉石荔枝及葡萄盆景。这个有三间房的办公室除了李老师与职工的办公桌椅外，几乎被众多的收藏品、字画、大部头藏书以及各种证书、照片挤得满满当当。行走其间都得小心翼翼，深恐一不小心撞坏这些简直像当杂物摆放的珍品。虽然很想知道这些宝贝的故事，但比不上我对这位传统文化牛人是怎么炼成的好奇。</p><p>2007年2月，孝顺的李老师为祝贺母亲周美珠80岁大寿，特地延请安徽黄梅剧团到东阳仙山村老家，为双亲及家乡父老演出几天他们最喜欢的黄梅戏。</p><p>采访前做功课时，搜集的资料越多，好奇心就越强。这个李老师博学多才，于易经、佛学、儒学、道学、武术、气功、魔术、汉字、书画、中医等领域均享盛名。如此多才多艺，怎么炼成的?虽然自己在采访名人方面算得上身经百战，但要面对这样一位中国传统文化的“高大上”人物，还是有些许不自信。可与李老师握手的那一霎那，他发自内心的微笑顿时便溶解我潜意识里微微的忐忑。搬动办公室里的摆设，腾出空间，架好摄像机，访谈就这样在相对狭小的录制空间开始了。</p><p>李老师字子选，号韬庵，出生于浙江省东阳市上卢乡仙山村。1972年毕业于东阳中学，1974年应征入伍，1986年毕业于中国人民解放军南京陆军指挥学院。那一年，我正好从北京师范大学毕业后，直接远渡重洋赴美留学。在我们成长的年代，中国传统文化还处于“尘封状态”，他如何在那个时代研修中国传统文化，且十个领域样样精通?我难以想象。</p><p>李老师在武术和气功领域成名后，回老家探望父母时，两父子一时兴起还会切磋武艺。</p><p><b>家学渊源 戎马岁月</b></p><p>李老师认为这半生的成就，得益于家庭的启蒙，在幼年打下坚实的基础。小时候，父亲李纯汉是当地武术名家。他自幼随父习武、诵读经典，养成晨起练功的习惯、钻研武术的爱好与吃苦耐劳的超强生命力。祖母通晓命理，透过念咒、画符、推拿按摩给人治病，经常带着他走街串巷为街坊乡亲指点迷津。耳濡目染她的神奇能力及获得乡亲敬重的魅力，儒慕玄学之心油然而生。另外，孝悌仁义的家风熏陶及双亲的身教，使得中华传统文化的基因深深植入他的血液里。</p><p>1974年李老师从军报国，在海岛当炮兵侦察兵，表现优秀多次获奖。1978年因创新技术提升战力，被部队记功、入党、提干，当上指挥排长，后升任作战参谋。约14年的军旅生涯，为了研究作战方案，经常要上山下海勘察地理，接触多样地形地貌。这种实战经历使得他有机会印证周易、风水方面的知识，更提供他足够的时间和环境完成此生成长的能力“大跃进”;武术修为从外家蜕变成内外双修，走上未来成为享誉国内外的武学名家和气功大师之路。易学和中医领域也由原本懵懂到窥得堂奥，并在闲暇时开始尝试用气功为人治病，并屡获奇验，除同僚外，连首长、部队家属也闻名造访。1987年开始利用公假在社会上从事气功研究实践。</p><p>当年帅气的小战士，如今已逾耳顺之年。回家自讨妈妈一棒子，疼在身上，乐在心里。</p><p>1991年，李老师转业到杭州市电信局公安处工作。有一次在单位财务处发生一件重大偷窃案，警方调查多日，苦无线索，难以破案。李老师主动向上级提供其在打坐观想中所得到的信息：包括犯罪嫌疑人面貌特征、作案手法及所藏赃物的地点。警方在质疑中姑且一试，竟然因而破案。罪犯被捕后的供述，完全吻合李老师的“推测”。然而在那个特殊年代，人们难以接受不可思议”的事实。老师好心帮忙，反而为他带来意想不到的困扰。在委屈与失望中，慨叹不如归去，1992年默然离开公务员的岗位。</p><p><b>访师问道 挺身护“法”</b></p><p>离开公职，李老师可以毫无羁绊地追寻老祖宗留下的智慧与宇宙人生的真相。他开始游走大江南北、名山古刹，访求明师问佛求道。卓绝的天赋与好学的天性，在短短十几年让他于儒释道、易学、武术、气功、魔术、汉字、书法、绘画、养生、中医等领域纵横捭阖。而在此期间，他在气功领域也有所成就，尤其值得称道的是让瘫痪多年的病人当场起身行走。不少洋弟子慕名来华拜师习武，一律按中国传统向师父行跪拜礼，且举手发誓只能济弱扶倾，绝不挟技欺人。</p><p>洋弟子拜师，一样必须按中国传统向师父行跪拜礼，且举手发誓只能行侠仗义济弱扶倾。</p><p>身怀绝技并没有让李老师在功利主义横流的上世纪90年代成为他发家致富的工具。而是传承古圣先贤智慧、弘扬传统文化、打开行善积德的方便门。回顾过去25年，最让他欣慰的有四：对气功打假护真;借“说字”弘扬传统文化;以身教推广爱国;用真心导人向善。</p><p>先来说气功打假。中国在上世纪八、九十年代曾掀起一场全民气功热，气功报刊、气功理疗院、气功表演会遍布各地。所谓大师遍地开花，其中以假乱真者不少。为避免无辜者受骗，李老师不顾安危，多次挺身打假。不过，谁要说中国气功都是骗人的把戏，李老师可不容许这种胡言乱语侮辱古圣先贤。1998年5月，标榜反伪气功和特异功能的司马南、何祚庥在中国科技会堂宣布悬赏特异功能，设奖金100万元，并与美国的魔术师兰迪发表联合声明。李老师为正视听、维护气功界的尊严，出面应战，未料司马南反弃甲匿身怯战。从1998年到现在近二十年中，李老师锲而不舍，多次下达战书，甚至亲自登门拜访司马南，对方皆闻风遁逃。一次，在电视“反伪”节目中，两人不期而遇。李土生拿出一张他和司马南二人合拍的照片(李土生训斥，司马南倾听)，向司马南求证是否2005年12月9日在全国政协礼堂纪念抗日战争胜利65周年活动上，他向李土生保证以后决不参与打假、反伪科学领域时，……面对李土生的义正严辞，司马南一反往日的咄咄逼人、嚣张气焰，变得唯唯诺诺、不知所措。</p><p>2015年李老师应邀前往雅加达，在印尼前总统瓦希德的家中为十余年来只能借轮椅代步的瓦希德夫人治病，当场让她起身行走。</p><p><b>气功治病 雪地打坐</b></p><p>我在网上看过一篇文章，是李老师在2015年给印尼前总统瓦希德的夫人看病的报导。对于我好奇的提问，老师兴致勃勃地打开电脑，让我看历年各种场合用气功为人治病的图片。但国内法律规定行医的需有执照;也存在很多会治病的无证，有行医资质的不会治病的怪现象，但他是中国人，必须守法，多年来不再用气功对外行医。印尼前总统瓦希德的夫人瘫痪二十几年，只能以轮椅代步。现任国防部长出了车祸，行动不便，派人前往中国邀请。鉴于一则行医地点在国外，再则可借此事将中华文化的博大精深向友邦展示，因而应允。抵达雅加达次日上午，先赴瓦希德总统夫人寓所，当场让她从轮椅上起身行走，引起现场一片惊呼。连其长女因车祸造成的长短脚也连带治好。2月10号上午拜会国防部长，几番发功揉捏后，他的脚行动自如，恢复正常。媒体轰动，大幅报道，并刊登老师治病及医病双方的合照。</p><p>朋友从微信发给我一张李老师光着膀子只穿一条短裤在雪地打坐的照片。我打开微信向他展示，想了解怎么回事。原来是2016年初他到长白山天池旅游，处身美景，一时兴起，便脱衣在海拔2600多米的天池边打坐。山脚下是零下28度，山顶上的温度起码零下四五十度，照片里的其他人都裹着厚厚的连帽羽绒服，冻得缩成一团，他却赤身端坐两个小时。李老师解释：“人只要能量够，就可抵御外在环境的低温。能量越高，可适应的体温与外面环境之温差就越大。”这也是功夫呀!如此说来，他能一年四季都能只穿一件白短袖衬衫，外套一件具有中国民族特色的中山装，也就见怪不怪了!</p><p>2016年初李老师到长白山天池旅游，一时兴起，便脱衣在2600多米的天池上打坐。当时气温零下四五十度，同行其他人都裹着厚厚的连帽羽绒服，冻得缩成一团，他却赤身端坐两个小时。</p><p>我问老师“这过程必有神奇之处，怎么解释?”老师没有正面回答，笑着随口来个绕口令式的慧语“未知永远大于已知，用已知解释未知，便是无知”，出语精辟，发人深思。或许李老师具备的能量场已超越环境对他肉身的影响;“无知”的我想半天，只能如此认知。</p><p><b>八百万字《土生说字》640米《手抄经典》</b></p><p>再说《土生说字》。 \n国家语委、汉语言文字研究会、多所大学中文系，那么多人都没有人写出这方面的专著，与文字毫无因缘的他却完成了800多万字的巨著——《土生说字》。这样庞大的工程，很难相信这是个人的力量完成的。我好奇地请教李老师是怎么做到的。</p><p>说字的10年，每天睡眠3、4个钟头，工作十几个小时，除了接见访客，其它时间都用于写书，终于得偿所愿。他在东汉许慎《说文解字》的基础上，用新思维、新角度论述了汉字丰富的文化内涵和其对现实生活的指导意义。以每字1000字左右的篇幅，拆解和阐释7000个汉字。2009年10月这800万字的巨著才竟功付梓，并在人民大会堂新闻发布厅召开了新书出版座谈会，轰动全国出版界。当时不少党政军高层领导人发来贺电;当时已98岁的国学大师文怀沙先生看了《土生说字》书稿后欣然题词：“纷缊宜修，姱而不醜兮”。著名学者黄苗子也高兴地题字“仓颉造字鬼夜哭，土生说字神人服”。季羡林、方成等老前辈题词给予这套书很高的评价;媒体界也赋予他“当代许慎”的美誉。</p><p>上图：李老师著作等身，其中《土生说字》和《李土生手抄国学经典》都是历经孤灯长夜，伏案十年的作品。</p><p>下图：2009年10月这800万字的《土生说字》竟功付梓，并在人民大会堂新闻发布厅召开了新书出版座谈会，轰动全国出版界。</p><p>2011年12月，李老师再一次震撼文坛。历时十载完成的《李土生手抄国学经典》出版。共计36万字，长640米。他凭借深厚的国学功底，精选四书五经、诸子百家、章表奏议、佛教典籍、唐宋诗文等经典篇章，恭书而成。什么因素让他能够坚持伏案十年战战兢兢于蝇头小楷?自得其乐!抄录心存恭敬，神交古圣先贤，每每获益，自然乐此不疲。没想到的是，心满意足搁笔时，一晃十年已逝。</p><p><strong>字里乾坤 说字传道</strong></p><p>我请李老师举例讲几个汉字的含义，比如中国社会强调“和”，“以和为贵”这个“和”字如何解释?这个汉字有什么含义?老师随手拿起一张纸，不假思索先开始写下一个“禾”字。“禾”代表粮食，“口”代表人，如果人人有饭吃，有饭大家吃，社会便会“和”。原来寓意这么简单!接着问：“但中国现在已解决温饱的问题，为什么还会强调“和”字?”老师不思索地说：“那是因为有些政府官员没有按党章办事，出现了很多腐败现象!“腐”字上面是政府的“府”，下面是个“肉”，“府”字拆开是广大的“广”和付出的“付”，官“府”本应为广大百姓付出;而不少当官的不想付出，老盯着下面那块肉，便是“腐”。幸好现在国家雷厉风行反腐倡廉，中国梦迟早会实现的。”</p><p>“和”字是李老师最喜欢传播的汉字之一;寓于书画，洗涤人心。</p><p>对于中国的 “国”，繁体字写成 \n“國”。李老师的理解是，“國”外面的大“口”是一个国家的领域，包括领空、领海、领土、大陆架。里面的小“口”是指其中的国民，也是指每个中国人。而“戈”有三层含义：一是指改善生活品质的劳动工具;二是指有识别出善恶美丑的能力;三是指保卫国家的武器。如果“戈”字是在大“口”的外面，那说明我们喜好耀武扬威侵略别人;但是老祖宗造字时把“戈”在里面，所以说中国爱好和平，武器是用来抵御、震慑外侮的。</p><p>对于中国游客在国际的形象贬多褒少。问他怎样才能改变这种现象?李老师认为人与人交往，不以地域、种族，为重。“做人先能“仁”;“仁”字代表我、你、他三人，自己要站直行正，处理好人际关系。己所不欲勿施于人，处处为人着想。其次要信。“信”有两层意义：首先一个人要讲诚信，一个国家也是要讲诚信。什么是诚信?“人”“言”为“信”。人是站立为“人”，且要“言”之于行，才是“信”!第三是信守，走出国门，一定遵守人家的法律和风俗习惯。现在中国大力从幼儿园、小学扎根素质教育，学习《弟子规》童蒙养正，将来人人都有“仁”、“信”修养，中国游客在国际的形象自然变成褒多贬少了。</p><p>李老师常借赠送国徽胸章向外国友人宣扬中国文化，并为他们当场别上。左为汉字叔叔理查德·西尔斯(Richard \nSears)，右为美国Fairlawn市市长Willian J. Roth Jr.。</p><p><b>弘扬爱国主义 造就“和谐仙山”</b></p><p>再说以身教弘扬爱国主义。李老师是非常爱国的一个人：他的电脑桌面背景、手机屏幕都是国旗，手机彩铃则是国歌。李老师不穿西装，不管出席任何场合一律一身中山装，且在左襟别个国徽。口袋里随身装着几个国徽胸章，不管对象是何国籍，随时当做见面礼。早就听说他有这个特殊的习惯，所以不奇怪一见面，他从口袋里掏出一枚国徽送我。李老师一边给我别在胸前，一边强调爱国是身为中国人的本分，在与人交往的过程中，也是表达他对身为中国人的自豪及传播爱国精神宾主尽欢的方式。他经常应学校、企业、社团之邀演讲，除了弘扬传统文化、中华美德、养生保健的主题外，最乐意的就是宣讲爱国主义精神。</p><p>李土生老师开心的和孩子们在一起。</p><p>我们在采访中发现，李老师心胸特别豁达，性格特别开朗。我们问他，你对今后人生的态度时。他说，我只想活着的时候怪一点，走的时候快一点。我深知生命的长度不是由自己来决定的。人到这个江湖上来是偶然的，离开这个江湖是必然的。不是说你想来就来，不想走就不走，因此我只想在生命的宽度和深度上去努力。人走未必灯熄，我只希望离开这个江湖以后，江湖上还有人惦念我，思念我，想念我，而且在江湖上还有我的美好的、神奇的传说。</p><p>李老师每年春节都要回乡宴请村里的老人，并发红包和新年礼物。几年来多次包车，带他们出游。</p><p>李老师用真心导人向善，值得大书特书。“听说您逢年过节都回家乡跟年长的乡亲们吃饭、给红包，每年还组织他们出去旅游。每月去东直门街道敬老院慰问老人、逢年过节也请周边社区的服务人员聚餐，请问您有没有一个慈善机构或基金支持?还是都由自己掏钱?”听我如此提问，李老师立即起身，从中山装、裤子、白上衣里面外面的多个口袋里掏出一叠叠的百元大钞。说道：“人出生是一个偶然，死亡是一个必然!我们不能改变昨天，也无法知道未来。所以我们只能把握今天，做一点事。我的身上永远在不同的口袋里装着钱。因为不知道什么时候会碰到有人急需用到钱。及时行善，有备无患。常看到需要帮助的人，我便从口袋里拿出一把钱，从来也不数。能舍自然能得，我有多少，就做多少。有时我有心做，老天爷也常及时送钱成全我。财富只有用到有用的地方，才是财富，否则死后便是遗物，你是带不走的。”人生最大的悲哀是人到了天堂钱还在银行。他还说，他到今天还没有发现哪个人是带着钱到天堂的，但据他知道，有很多人是因为钱而下地狱的。钱只有用掉了是财富，不用的只能当遗物。</p><p>造就“和谐仙山”是李老师对家乡最得意的回报。少小离家，难忘乡愁，愁的是小小的仙山农村竟有几代人不相往来的两派。2008年中秋，他返乡设宴、送全村老人月饼，就为了让双方化干戈为玉帛。在老师精心安排和“威胁利诱”下，两派要员各个“被迫”比邻而坐。席间老师来回游走各桌斡旋，好酒好菜以诚相待。眼见两派祖辈遗下的矛盾一席冰释，欣喜之余发愿年年返乡与乡亲同乐。</p><p>李老师中秋节一定给村里老人发月饼(上)。个人先后捐赠母校上卢小学和上卢初中两百余万元的图书和电脑。(下)</p><p>李老师对家乡父老回报甚多，在教育上更是不遗余力。东阳市江北上卢小学和上卢初中是李老师的母校。2010年返乡探望几位耄耋的恩师，得悉母校图书匮乏，遂兴起捐“知”助学的念头。先后五次，个人共捐赠近两百余万元的图书和电脑。每次捐赠仪式后，都邀请恩师当嘉宾，还向学弟、学妹们宣讲孝亲尊师的传统美德。</p><p><b>感恩平头百姓 关爱弱势群体</b></p><p>对基层小老百姓和达官贵人，李老师始终保持平等心。有时还特别关爱弱势群体，用行动表达对身边这些人的感恩之心。“没有这些物业的警卫、维修工和保洁员，我的住家和办公室的社区怎么会这么干净、安全?所以逢年过节我都给他们买礼物，请他们吃饭。还有附近两家我常去的饭馆，也是年年请他们全体员工吃饭、给他们送礼。我有爱吃零食和甜食的习惯，餐馆服务人员听说我血糖高，我要喝饮料、吃甜品，他们都善意地骗我说没有。我让朋友点糖分高或较油腻的菜，他们吃，没事!看我要下筷子，服务员就马上端走。”听到李老师的“抱怨”，我心想，太夸张了吧，哪有这么服务的?采访结束后的餐叙，特地验证李老师的话，过程果真如此!看我表情惊讶，李老师笑道“你敬人一尺，人还你一丈。礼尚往来，我关心他们，他们当然不让我吃这些东西，生怕我得高血压、糖尿病。”</p><p>李老师逢年过节会宴请小区保安、保洁、物业职工及他常去的餐厅员工，感恩他们。利用饭前讲讲中华美德。</p><p>在采访中我们了解到，他每天得应酬接待，到处讲学，晚上闭门写作。我们问他累不累时，李老师不加思考马上回答说：这一生我活得很累，但不后悔;因为我生活在人类中，只有不怕累才称得上是合格的人类。有很多人说我傻，我觉得人傻是对的。因为小时候我什么也不懂，本来就傻，长大以后一定要学会装傻，如果耍小聪明，一定没有好的结果。现在我清楚地意识到，年轻的时候要懂得奋斗，成熟以后要学会总结，老来以后一定有一个美好的回忆。做人要站得正、行得稳;做事要看得清、想得开;生活上要拿得起、放得下。年轻的时候，我很想好好地干一番事业，总想走出一条别人没有走过的路。成熟以后，我才发现只要是路一定是有人走过的，关键是你能坚持走多久，走得多远。只有你坚持的比别人久，走得比别人远，才能见到别人没有见到过的风景。我深知在高速公路和水泥路上来回走，怎么走也不可能留下痕迹，只有选择在泥泞的土地上行走，才能留下自己的脚印。上苍不会让所有的幸福集中到你的身上，也不会把所有的痛苦强加到你的头上。这一生我在掌声鲜花中快乐开心地活着，尽管幸福的指数不高，但社会还是给了我不少幸福的回忆。</p><p>李老师常用“牌技”让好赌的败家子向双亲跪下，并发誓戒赌。</p><p><b>魔术拍案惊奇 藏书不学而知</b></p><p>采访结束时，我们仍还意犹未尽，就几番诚恳地请李老师来个助兴节目。我叫身边的朋友去买了副扑克牌，洗好牌后，他要我抽张牌拿在手上给大家看，梅花10!然后要我花色朝下，问我是否确定梅花10。牌始终没离开我的手，也没被他碰到，我当然肯定。然而翻过来，大家都傻了，竟然是黑桃A!最后要我随便洗牌，然后他动口，摄影师动手，让摄影师将牌分成4摞，倒来倒去，最后翻开每一摞最上面那张，居然是4张10!在座的每一个人都百思不得其解，李老师语重心长地劝人千万不要赌博：“每个赌场都养几个可以随心所欲控牌的高手，你要进赌场，只有送钱的份，据我所知，全世界没有一个靠赌博发财的。”李老师又向大家展示几张年轻人跪着向父母忏悔的照片，都是被李老师用牌技打醒的赌徒败家子向双亲发誓戒赌。</p><p>知道李老师曾被中国书画交流协会评为“书坛四大领袖”，我冒昧地为我们媒体及频道向他求字。当李老师挥毫“环球龙视、全球华人频道”时，旁边的大姐悄悄说，李老师是国务院正式任命的国宾礼书法专家，李老师所有的宣纸都有防伪标志。我细一看老师用的宣纸，果然如此。这种善缘真是用金钱也买不到的!所幸，我们摄影队将老师题字和落款的整个过程都记录了下来。</p><p>晚餐结束后，我们提出能否去李老师住所坐坐。我想，他办公室的摆设已件件价值不菲，家里的肯定更有看头，李老师欣然应允。但是，老师打开家门，映入我眼帘的一切让我有点“懵”：40平米两个房间的公寓里，我看不到一件想象中的艺术品。客厅摆着半套“历经沧桑”的沙发还显得狭小;卧室有张农民工级别的单人床和小书桌;只有书房的明式桌椅还有点格调;厨房一堆方便面和垃圾零食。最值得炫耀的是每个房间除了出入的门，每一面墙都堆着超过一人高、有的几乎到屋顶的书。我惊讶地问“这么多书您都看了?”李老师的回答简直不合逻辑：“很多没读过!但你可以随便挑一本，问我第几页第几行说什么。”那天李老师已颠覆我太多的认知，这个挑战?想想还是算了!</p><p>李老师对自己不是普通吝啬;住宅除了众多图书外，家具简陋，平常以方便面、零食充饥。</p><p>分手时，李老师一直送我们到车边，顽童似地笑，伸出右手叫我切脉，我摸了半天，感觉不到脉搏。不会吧，位置对呀!李老师笑着说“来了”，又立刻感觉到他的脉搏。后来听说他“断脉”这招，曾在一个中医论坛上让一位傲慢的老中医结舌瞠目，下不了台。朋友还透露：不仅脉搏，他还能随意控制血压、血糖。拿仪器测他，准叫你傻眼。</p><p>李土生老师接受美国环球龙视黄斯诺采访。</p><p>\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n</p><p>停笔之际，几个小时飞逝，已快到华盛顿了，可是满脑子还是李土生老师带来的惊异，居然有一种“犹恐相逢是梦中”的感觉……</p>', '1', '0', '0', '1', '0', '2018-06-25 13:45:34');
INSERT INTO `ys_article` VALUES ('8', '世界气功网站开通', '1', '头条', '', '', '', '网络', 'admin', '世界气功是由世界大健康运动联盟主办，面向世界，弘扬健康养生文化，传播优秀健身方法，汇聚各国健康养生新信息新成果等信息服务。', '<p>21世纪是大健康世纪。为顺应时代潮流，满足广大民众对健康健美长寿幸福生活的向往与追求，由世界大健康运动联盟主办的气功网站，全心全意为您提供服务。</p><p>面向世界，紧贴民众。健康健美长寿是世界语言，各国家各民族创造的健康养生功法技法是人类的共同财富。弘扬健康养生文化，传播优秀健身方法，世界气功网站就是您健康快乐幸福的良师益友。</p><p>传递信息，共享成果。世界气功网站汇总世界各国健康养生新信息新成果，依托“透视世界大健康”、“我与大健康同行”、“经典钩沉”、“古籍整理”、“学术论坛”、“产品博览”、“传道授业”、“功法荟萃”、“远程传递”、“特色养生”、“功德双馨”、“释疑解惑”等栏目，让您透视气功世界。</p><p>与您同行，为您服务。我们进入了新时代，人类同行在健康道路上，世界气功网站是您获得健康、获得健美、获得长寿、获得幸福的真诚伴侣。</p><p>与健康气功同行，有您、有我、有他!</p><p align=\"right\">世界气功网站</p><p align=\"right\">地 址： 香港湾仔告士打道151号资本中心11楼</p><p align=\"right\">联系人：麻先生 手机：18681858718</p><p align=\"right\">电 话： 00852-28019618 传 真： 00852-30191843</p><p>\n\n\n\n\n\n\n\n\n</p><p align=\"right\">网 址： www.shijieqigong.com</p>', '1', '11', '0', '1', '0', '2028-06-29 23:37:07');
INSERT INTO `ys_article` VALUES ('9', '拿什么奉献给健康中国新战略', '2', '', '', '', '', '网络', '本报评论员', ' 凝聚在为中国人民谋幸福，为中华民族谋复兴的旗帜下，在健康中国建设的新起点新征程上，找准定位，精准发力，为发展繁荣大健康事业做出新贡献，成为许多社团组织新年新举措新动作。', '<p><span style=\"display: inline !important;\">&nbsp;凝聚在为中国人民谋幸福，为中华民族谋复兴的旗帜下，在健康中国建设的新起点新征程上，找准定位，精准发力，为发展繁荣大健康事业做出新贡献，成为许多社团组织新年新举措新动作。</span><br></p>\n<p>&nbsp;国际健康健美长寿学研究会理事会和世界大健康运动联盟理事会，把贯彻落实健康中国战略作为头等大事来对待，凝聚两会共识，出台具体落实方案，将在第十次国际健康健美长寿论坛博览会暨首届大健康运动会上，向国际社会做出庄严承诺，表明自己的新主张新举措。</p>\n<p>&nbsp;高举思想旗帜，践行核心价值。一切伟大的时代，都需要思想领航。习近平新时代中国特色社会主义思想，是当代中国的马克思主义，是领航中国梦的根本指南和强大精神动力。健康养生学术团体作为健康中国建设的重要组成部分，在“四位一体”的组团中占有不可或缺的地位作用。听谁话，跟谁走，举什么旗，走什么路，为谁服务，如何服务，是社团组织必须用行动回答的原则问题。社团组织中的人，都是社会的人，都在为实现社团组织的目标和价值而奋斗。如果丢失思想武装，缺失理想信念，迷失目标方向，就不可能同心同德为使命任务而奋斗。只有用科学理论武装，用新思想指导航向，社团组织才能凝聚共识，知其所来，识其所在，明其所往，走正前进道路，才能自觉投身健康中国的伟大实践，在服务国家，服务社会，服务群众，服务行业中有所作为。</p>\n<p>&nbsp;弘扬优秀传统，贡献养生真知。文化润无声。中华文化积淀着中华民族最深沉的精神追求，包含着中华民族最根本的精神基因，代表着中华民族独特的精神标识。健康养生文化是中华文化的重要组成部分，积淀深厚，博大精深，让中华健康养生文化走出去走进去，是推进健康中国建设的一项重要任务，也是构建人类命运共同体的题中之意。做中华健康养生文化的传播者，就要弘扬优秀传统，以人民健康为中心，把敬重生命高高举过头顶，传业授道，传递真知，传播美德。坚决反对打着传统文化的旗号，传播与政治文明、社会文明、道德文明相悖的言行。坚决反对封建迷信、形式主义、花拳绣腿。一招一式体现博大胸怀，举手投足展现国际视野。尊重各国家各民族优秀健康养生成果，平等互鉴，开放包容，兼收并蓄，厚植自己。绿水青山就是金山银山，与自然和谐相处，保护环境，绿色低碳，清洁美丽，天人合一，增进民心相通。</p>\n<p>&nbsp;汇聚优势资源，促进发展繁荣。 \n发展是解决一切问题金钥匙。以健康中国战略为牵引，让中华优秀健身养生文化浸润一带一路，将富有特色的人脉资源、科技资源、品牌资源、财力资源，转化为构建人类命运共同体的软实力和硬实力，是发展大健康事业的客观需要和必然要求。国际健康健美长寿学研究会和世界大健康运动联盟，与国际健康养生界有着广泛的联系，积累了丰富的经验。整合优势资源，重构文化传播版图，肩负主体责任，以我为主，主动担当，打头阵、做旗手、掌旗舰，才能牢牢掌握发展繁荣大健康事业的话语权和主导权。与博大精深的中华健康养生文化价值观相一致，与健康中国的大国地位相适应，与中华文化走向世界的步伐相一致，需要进一步理顺政府部门、社会团体、精英人才之间的关系，升级产学研协同攻关，做大做强互“联网十”，不断释放岀发展潜力。</p>\n<p>&nbsp;遵守法律法规，从严修身自律。没有规矩不成方圆。推进伟大事业，实现伟大目标，离不开树立法治观念，运用法治思维，养成法治习惯。为人民大众谋健康、谋健美、谋长寿，是广施仁爱、功德无量的公益善举，就要把人民大众愿意不愿意、高兴不高兴、拥护不拥护，作为衡量传业授道的一把标尺。传播健康健美长寿文化又是传播社会文明、政治文明、道德文明、生态文明，充满正气正能量的事业，就要把法治和道德高高举过头顶，把自觉遵守所在国家地区法律法规，从严律己，厚德载物，为人示范，不碰红线，不触高压线，作为参与国际交流交往的一面镜子。顶得住各种诱惑，抵得住各种挑战，保持清醒和定力，远离恐暴，杜绝黑恶，不染黄毒。与真理同向，与政府同心，与民众同行，争当新时代为中国人民谋幸福，为中华民族谋复兴的传播者贡献者实干者。</p>\n<p>&nbsp;承诺如山，践行似箭。用健康中国这条红线互联互通造福七十亿民众，用健康养生这一世界共同语言唱响构建人类命运共同体旋律，社团组织在发展繁荣大健康的新时代里大有作为。抢占先机，乘势而上，让健康生活更美好，让人类文明更精彩。</p><p><br></p>', '1', '8', '0', '1', '0', '2018-06-27 08:10:42');
INSERT INTO `ys_article` VALUES ('10', '十年成果回顾 精彩瞬间回放', '0', '头条,推荐', '', '', '', '网络', 'admin', '国际健康健美长寿学研究会十年成果回顾', '<p><span style=\"display: inline !important;\">&nbsp; &nbsp;（1）2006泰国曼谷会议</span><br></p><p>　　2006年11月4日至6日，首届国际健康健美长寿论坛暨健康产业博览会在泰国曼谷隆重召开。来自中国、俄罗斯、美国、德国、日本、荷兰、澳大利亚、新西兰、瑞士、爱尔兰、中国台北等20多个国家和地区的300多名科学界、养生保健界的代表汇聚一堂，交流学术成果，演示健康养生功法绝技，规划未来发展远景。会议发表了著名的《曼谷宣言》。</p><p align=\"center\"><img alt=\"\" src=\"file:///C:/DOCUME~1/ADMINI~1/LOCALS~1/Temp/ksohtml/wpsDC.tmp.jpg\" width=\"367\" height=\"241\"></p><p>　　首届国际健康健美长寿论坛名誉主席、泰王国政府第22届总理差瓦立上将以泰民族最高礼仪——击鼓，宣布大会开幕。</p><p align=\"center\"><img alt=\"\" src=\"file:///C:/DOCUME~1/ADMINI~1/LOCALS~1/Temp/ksohtml/wpsDD.tmp.png\" width=\"355\" height=\"265\"></p><p>　　郭周礼副主席兼秘书长在大会上领读《曼谷宣言》</p><p>　　（2） 2007中国北京会议</p><p>　　2007年11月14日至17日，国际健康健美长寿学研究会主办的2007北京国际健康健美长寿论坛暨健康产业博览会隆重举行，来自中国、美国、俄罗斯、泰国、波兰、菲律宾、日本、澳大利亚、马来西亚、新加坡、新西兰、香港和台湾等国家和地区的著名健康学专家、医学美容学专家、人体科学专家、政府高级官员、著名企业家和从事健康健美长寿学研究人员600余人参加会议。会议围绕“推动全民健康，构建和谐社会”宗旨，广泛开展了健康健美长寿学学术探讨，养生术交流，健康产品展示。胡锦涛同志指示办公室工作人员发来贺电，温家宝同志题词祝贺。全国妇联、国家体育总局、国家卫生部、全国政协港澳台委员会、国家民政部给予了大力支持。新华社、人民日报、中央电视台、光明日报、北京电视台、香港日报、新华网、千龙网、新浪网、搜狐网等50余家新闻单位和网络媒体记者作现场报道。</p><p align=\"center\"><img alt=\"\" src=\"file:///C:/DOCUME~1/ADMINI~1/LOCALS~1/Temp/ksohtml/wpsDE.tmp.jpg\" width=\"373\" height=\"280\"></p><p>　　冯理达主席宣布大会开幕</p><p align=\"center\"><img alt=\"\" src=\"file:///C:/DOCUME~1/ADMINI~1/LOCALS~1/Temp/ksohtml/wpsDF.tmp.png\" width=\"360\" height=\"269\"></p><p>　　大会期间冯理达主席同著名科学家、诺贝尔奖获得者牛满江教授亲切交谈。</p><p>　　（3）2008中国北京会议</p><p>　　全国妇联批准并担任指导单位，由中国妇女报社、国际健康健美长寿学研究会共同主办的2008北京国际健康健美长寿论坛暨健康产业博览会于2008年10月7日至9日在北京举行。来自美国、加拿大、澳大利亚、英国、法国、西班牙、俄罗斯、日本、韩国、泰国、新加坡、马来西亚等20多个国家和港澳台地代表600余人参加大会。</p><p>　　大会以“弘扬传统文化，构建和谐世界”为主题，开展了专家讲座、养生术交流、保健产品推广、健康知识咨询等丰富多彩活动，展示了健康健美长寿学研究应用新成果。</p><p>　　伍绍祖主席在会上指出，健康长寿事业要以中国特色理论为指导，将健康事业融入市场，抓住机遇，遵守法纪，中西结合，贯彻“双百”方针，把论坛博览会办成像奥运会一样精彩。</p><p align=\"center\"><img alt=\"\" src=\"file:///C:/DOCUME~1/ADMINI~1/LOCALS~1/Temp/ksohtml/wpsE0.tmp.png\" width=\"401\" height=\"264\"></p><p>　　国际健康健美长寿学研究会主席、论坛组委会主席伍绍祖，向时任全国妇联党组书记、副主席、书记处第一书记黄晴宜颁发名誉主席聘书。</p><p>　　（4）2009中国上海会议</p><p>　　2009年 \n10月29日至11月2日，2009上海国际健康健美长寿论坛暨健康产业博览会在上海友谊会堂和上海展览中心隆重举行。来自20多个国家和地区的3000余名代表，围绕“共享健康科学成果，共建人类和谐世界”这一主题，通过国际健康健美长寿主题报告、学术交流、专题研讨、成果展示、健身讲座、健康咨询、现场观摩、产业推介等系列活动，充分展示了推动健康事业发展，倡导科学健康生活方式取得的新成果。</p><p align=\"center\"><img alt=\"\" src=\"file:///C:/DOCUME~1/ADMINI~1/LOCALS~1/Temp/ksohtml/wpsE1.tmp.png\" width=\"412\" height=\"308\"></p><p>　　伍绍祖主席与获得寿星奖的部分代表合影</p><p>　　（5）2010中国北京会议</p><p>　　全国妇联担任指导单位，由国际健康健美长寿学研究会、中国妇女报社、中国老年保健协会等共同主办的“振国杯”2010北京国际健康健美长寿论坛暨健康产业博览会，于2010年9月2日至5日在北京温都水城举行。论坛围绕健康健美长寿主旨，通过健康健美长寿学新成果展示、健康健美养生术表演、专题讲座、名家名医咨询、寿星表彰、养生名家书画展等系列精彩纷呈的活动，体现了共享健康科学成果，共建人类和谐世界这一主题。来自美国、加拿大、波兰、澳大利亚、俄罗斯、日本、新西兰、荷兰、马来西亚、泰国、德国、英国、台湾、香港等20多个国家和地区的2000余名政府官员、健康科学专家、国际贸易商、国际健康健美长寿学研究会各分会负责人和海内外友人参加了大会。</p><p align=\"center\"><img alt=\"\" src=\"file:///C:/DOCUME~1/ADMINI~1/LOCALS~1/Temp/ksohtml/wpsE2.tmp.png\" width=\"236\" height=\"157\"></p><p align=\"center\"><img alt=\"\" src=\"file:///C:/DOCUME~1/ADMINI~1/LOCALS~1/Temp/ksohtml/wpsE3.tmp.png\" width=\"238\" height=\"159\"></p><p>　　原全国人大副委员长、全国妇联主席、大会名誉主席顾秀莲宣布大会开幕（左）伍绍祖主席在闭幕式上讲话（右）</p><p>　　（6）2011中国济南会议</p><p>　　2011年11月5日至9日， \n国际健康健美长寿论坛暨健康产业博览会在山东济南隆重举行。全国妇联担任本次大会指导单位，由国际健康健美长寿学研究会、中国妇女报社共同主办，山东天地健生物工程有限公司、北京振国康美文化传播有限责任公司联合承办。 \n来自国内外20多个国家和地区的2000余名代表汇聚一堂，围绕“传承人类优秀文化遗产，推动健身文化发展繁荣”这一主题，通过举办健康健美长寿主题报告、学术交流、专题研讨、成果展示、健身讲座、健康咨询、现场观摩、产品推介等系列活动，充分展示了健身文化创新发展所取得的新亮点新成果。</p><p align=\"center\"><img alt=\"\" src=\"file:///C:/DOCUME~1/ADMINI~1/LOCALS~1/Temp/ksohtml/wpsE4.tmp.png\" width=\"414\" height=\"270\"></p><p>　　原中国最高检察院检察长、大会名誉主席贾春旺宣布大会开幕</p><p>　　（7）2013中国通化会议</p><p>　　2013年8月17日至20日，2013国际健康健美长寿论坛暨健康产业博览会在吉林通化举行。本次论坛开展了学术交流、养生术交流、保健咨询、专家讲座、产品展示等交流研讨。 \n会议由国际健康健美长寿学研究会、中国妇女报社、北京振国集团主办。原全国妇联副主席、书记处第一书记黄晴宜，中国老年体育协会主席、原国家体委副主任张发强等领导出席。来自俄罗斯、新加坡、德国、法国、日本以及港澳台等十多个国家和地区的健康养生领域专家和企业代表汇聚长白山下，共商健康健美长寿事业发展大计。论坛博览会期间召开的国际健康健美长寿学研究会理事会上，一致推选黄晴宜女士为国际健康健美长寿学研究会主席，并担任2014莫斯科国际健康健美长寿论坛暨健康产业博览会主席。俄罗斯武术联合会会长基里延科、特鲁特涅夫向大会发来贺电。</p><p align=\"center\"><img alt=\"\" src=\"file:///C:/DOCUME~1/ADMINI~1/LOCALS~1/Temp/ksohtml/wpsE5.tmp.png\" width=\"405\" height=\"283\"></p><p>　　大会主席黄晴宜宣布论坛暨博览会开幕</p><p>　　（8）2014俄罗斯莫斯科会议</p><p>　　2014年8月16日至17日，2014国际健康健美长寿论坛暨健康产业博览会在莫斯科隆重举行。本次论坛博览会是一次规模最高、规模最大、影响最广、亮点最多的盛会。会议作为中俄文化年活动之一，受到中俄两国政府、联合国教科文组织和广大民众的高度重视和关注。俄罗斯副总理特鲁特涅夫出席开幕式并致辞。联合国教科文组织代表阿拉女士、中国驻俄罗斯大使馆李辉大使，肯尼亚驻俄罗斯大使馆库尔加特大使等出席并致辞，中国人民对外友好协会李小琳会长发来贺信。会议由国际健康健美长寿学研究会黄晴宜主席主持。来自30多个国家和地区的600余名代表参加会议。会议围绕医疗与健康、体育与健康、文化艺术与健康、武术气功与健康等内容展开交流探讨。</p><p align=\"center\"><img alt=\"\" src=\"file:///C:/DOCUME~1/ADMINI~1/LOCALS~1/Temp/ksohtml/wpsE6.tmp.png\" width=\"412\" height=\"274\"></p><p>　　（1） 特鲁特涅夫副总理、黄晴宜主席、李辉大使出席会议</p><p align=\"center\"><img alt=\"\" src=\"file:///C:/DOCUME~1/ADMINI~1/LOCALS~1/Temp/ksohtml/wpsE7.tmp.png\" width=\"415\" height=\"279\"></p><p>　　（2） 联合国教科文组织代表阿拉女士致辞</p><p>　　（9）2015韩国大邱会议</p><p>　　2015年10月12日至16日，“文登杯”2015韩国大（邱）国际健康健美长寿论坛暨健康产业博览会在韩国大邱市举行。韩国大邱市市长权永臻、副市长金延昶、中国驻韩国釜山总领事阎凤兰等致辞，中国对外友协李小林会长、中国外交学会杨文昌会长等发来贺电。来自10多个国家和地区600余名代表参加了会议。会议围绕健康、健美、长寿主题，交流科研学术成果，优秀健身功德绝技，展出出养生、养老、美妆等新产品。会议的最大亮点对加强中韩友好合作，推动自贸区试点工作产生了积极作用。</p><p align=\"center\"><img alt=\"\" src=\"file:///C:/DOCUME~1/ADMINI~1/LOCALS~1/Temp/ksohtml/wpsE8.tmp.png\" width=\"372\" height=\"247\"></p><p>　　中韩之夜韩方表演人员合影留念</p><p>　　（10）2016韩国大邱会议</p><p>　　2016年10月13日至16日，2016国际健康健美长寿论坛暨首届大健康运动会在韩国大邱市举行。本次大会是检阅10年成果，辞行划今年10年发展大计、开放创新的的盛会。会议将融学术交流、健身功法演示、健身产品推介展销与大健康运动会为一体，将强身健身、延年益寿、大众实用、易于普及，未列入国际和地区及行业运动会的健身项目囊括其中，充分体现了本次大会更高、更强、更健康的特色理念。</p><p align=\"center\"><img alt=\"\" src=\"file:///C:/DOCUME~1/ADMINI~1/LOCALS~1/Temp/ksohtml/wpsE9.tmp.png\" width=\"372\" height=\"275\"></p><p>\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n</p><p>　　黄晴宜主席为大邱市市长权泳臻颁发奖牌</p><p><br></p>', '1', '0', '0', '1', '0', '2018-06-28 02:35:29');
INSERT INTO `ys_article` VALUES ('11', '中华健康文化走向世界的回顾与瞻望', '2', '头条', '', '', '/static/upload/20180628-13-43-13_11516_图片1.png', '网络', '郭周礼', '从2006年到2016年,国际健康健美长寿文化走过了十年道路。十年间,乘着改革开放的时代巨轮,由本会发起和主导承办的国际健康健美长寿论坛暨健康产业博览会,从泰国曼谷启程,先后在北京、上海、济南、通化、莫斯科、韩国大邱举办了九次盛会。', '<p style=\"text-align: center;\"><img src=\"/static/upload/20180628-13-48-16_17115_图片1.png\" style=\"max-width: 100%;\" class=\"\"></p><p>从2006年到2016年,国际健康健美长寿文化走过了十年道路。十年间,乘着改革开放的时代巨轮,由本会发起和主导承办的国际健康健美长寿论坛暨健康产业博览会,从泰国曼谷启程,先后在北京、上海、济南、通化、莫斯科、韩国大邱举办了九次盛会。健康健美长寿文化在走向世界的征途上,留下了本会弘扬中华文化、致力民众健康,披荆斩棘、开拓进取、举旗正路的奋斗足迹。</p><p><b>健康健美长寿文化走向世界十年成果丰硕</b></p><p>由本会发起和主导举办的国际健康健美长寿论坛暨健康产业博览会,是中华健康文化率先走出国门、走向世界的创新之举。十年来,健康文化传播速度不断加速,传播效果日益显现,得到了国际社会的广泛认同和支持,取得了阶段性重要成果,为中华文化走出去、走进去探索出了成功的路子。</p><p>一、庄严宣示赢得了国际社会话语权。2006年,在泰国曼谷举办的首届国际健康健美长寿论坛暨健康产业博览会,开启了中华健康文化迈出国门、走向世界的第一站。大会受到党和国家有关部门的重视和支持,泰国政府第22届总理差瓦立先生,以泰民族最高传统礼仪击鼓宣布大会隆重开幕并致辞,中国全国政协常委伍绍祖主席主持大会并讲话。来自中国、美国、澳大利亚、加拿大、韩国、日本、新加坡及香港和台湾等国家和地区的健康专家、健身产品企业代表和五百多名中外热爱健康文化民众出席大会。80多篇健康科研论文在论坛上作了交流,20多家企业展示了健康新产品。引领大健康时代发展潮流,顺应广大民众追求健康健美长寿的美好梦想,用科学理论指引,用先进理念导航,崇尚科学,反对迷信,弘扬美德,构建和谐,维护稳定,珍爱自然,保护环境,《曼谷宣言》作为本会对国际社会的庄严承诺和行动纲领,表达了国际健康健美长寿学界的共同心声和立场。2007年和2009年,又分别发表了《北京宣言》和《上海宣言》。三部宣言作为新形势新阶段本会宗旨意志的宣示和章程的延伸,具有十分重要的里程碑意义,赢得了国际社会的认同和信任,树立了本会在国际健康健美长寿领域传播健康文化的指导地位和中心话语权。</p><p>二、同心同行赢得了政府和民众支持。2007年和2008年,在北京举办的两届健康健美长寿论坛博览会,是本会发扬奥运精神,乘着举世瞩目的北京奥运会全民健身热潮,顺势开展的健康文化接力。以构建和谐社会,推动全民健身为主题,以与政府同心,与民众同行为宗旨,论坛博览会引起党和国家领导的高度重视和大力支持。胡锦涛总书记办公室打电话表示祝贺,温家宝总理亲笔致信给予充分肯定和支持。全国人大副委员长顾秀莲，全国妇联党组书记第一副主席黄晴宜出席会议,伍绍祖主席作重要讲话,冯理达教授作科研学术成果讲座。来自20多个国家和地区医学美容学专家、健康教育专家、健康产品企业代表、优秀健身功法团体代表600余人出席了大会。100多篇健康健美长寿学术论文在论坛作交流,50多家企业的健身产品参加了展示推介。《北京宣言》作为团结一致,与政府同心、与民众同行的根本指南,自觉把传播中华健康健美长寿文化的时代重任置于党的领导和政府的引导下,尊重各民族优秀健身文化传统和习惯,遵守所在国法律法规,自觉接受社会和民众监督,德行合一传播社会正能量,举旗正路,以论坛博览会为平台,做中华健康文化的推动者、传播者和友好使者,受到广泛支持。</p><p>三、举旗正路找准了目标方向和道路。2009年和2010年举办的上海和北京论坛博览会,不仅是对百年世博会首度登陆上海所掀起的全民健身活动的助力,而且从理论和实践上解决了论坛博览会发展方向、发展目标、发展道路等关键问题。2009年上海论坛博览会上,上海市副市长赵雯出席并讲话,强调了发挥政府引导功能在健康文化传播中的地位和作用。伍绍组主席强调指出,传播中华健康健美长寿文化,要紧扣“第一要务”、做到“四个坚持”、高举“四面旗帜”。即把致力于人民群众健康作为本会和论坛博览会的中心和第一要务,坚持党的正确领导,坚持先进文化的正确方向,坚持科学发展观,坚持中国特色社会主义方向。高举奉献的旗帜,高举科学的旗帜,高举团结的旗帜,高举文明的旗帜。2010年北京会议上,原全国人大副委员长顾秀莲，全国妇联党组书记第一副主席黄晴宜出席并致辞,伍绍祖主席主持会议。2011年济南会议上,原最高人民检察院贾春旺检察长出席并致辞。北京、上海、济南三次论坛博览会进一步完善了政府引导、本会主体、民众参与、社会监督,规范国际健康健美长寿文化传播运行机制,明确了目标系、线路图、运行表、责任体。</p><p>四、科研学术代表了健康文化新成果。每年一届的论坛博览会,是传播中华健康文化、检阅国际健康健美长寿科研学术成果和健身产品的大聚会。十年来,本会与政府教育体育部门、健康科研机构、高等院所、健身社会团体、健康产品研发企业、优秀功法传人等建立了广泛联系,在36个国家和地区建立了国际健康健美长寿学分会组织,与五大洲热爱中华健身文化的华人华侨社团密切交往交流,指导开展了国际和地区健康健美长寿文化传播活动。十年间,论坛博览会收到科研学术论文近千篇,内容涉及中医中药、针灸、武术、太极、气功、健身功法、心理健康、美容化妆、环境保护、健身新产品研发推介等诸多领域。按照健康健美长寿学基础理论研究、健康健美长寿学应用与实践、健康健美长寿学文献研究与挖掘整理三个专题,每年都要在全球征集健康科研学术论文,经过专家委员会审查,精选出50至80篇出版论文集,评选出10至20篇优秀论文表彰奖励。具有原创性、创新性、前瞻性和实践性,理论创新和操作性强的成果,代表了国际健康健美长寿学界最新科研学术水平。</p><p>五、深度融合拓展了文化传播新途径。2013年吉林通化会议,是本会承前启后、继往开来的重要聚会,在传播中华健康文化,共筑中国梦、健康健美长寿梦的道路上,开启了推广健康新理念、拓展优秀健身方法向中小城镇和广大农村地区延伸的新征途。论坛博览会期间召开了本会理事会,出席会议的全体理事和来自10多个国家地区的分会组织负责人,一致推选黄晴宜女士出任国际健康健美长寿学研究会主席。黄晴宜主席主持制定了新阶段国际健康健美长寿学走向更加开放、迈向深度融合的行动目标。会议决定,为大力传播中华优秀健身文化走向世界,从2014年起,国际健康健美长寿论坛博览会将在海外举办。本会要率先走出去、走进去,加强与世界各国各地区政府和分会组织的深层次密切联系,尤其是加大中华健康文化在欧美大洲的传播力度。理事会批准了俄罗斯分会申请,决定2014年论坛博览会在俄罗斯首都莫斯科举行。深度融合释放出巨大的能量效果,加速了产学研国际链的发展进程,助推了国际健康热的快速形成,健康健美长寿科研学术新成果不断涌现,,转化力度不断加大,研发健身产品越来越多,健康新产品得到广泛推广运用。与此同时,推动了经济、社会、文化、体育、旅游、外交等发展,日益显现出桥梁、纽带维护和平稳定作用。</p><p>六、创新驱动办会规模质量越来越高。2014年8月在莫斯科举办的论坛博览会,是本会首次在欧洲举办的盛会,也是9年论坛博览会历史上规格最高、规模最大、影响最广、亮点最多的一次会议。中国和俄罗斯政府高度重视,俄罗斯政府将论坛博览会列入了中俄文化交流年活动之一,中国人民对外友协、中国外交学会等发电表示祝贺,俄罗斯政府副总理特鲁特涅夫、联合国教科文组织代表阿拉女士、中国驻俄罗斯使馆李辉大使、肯尼亚驻俄罗斯使馆保罗·库尔加特大使,以及俄罗斯国家科学院院士、二战老兵代表等出席并致辞。来自亚洲、欧洲、非洲、大洋洲等20多个国家和地区700余名代表参加会议,中俄两国30余家健身产品开发企业上百种产品参展推介。会议收到论文上百余篇,50多篇论文在论坛作交流。论坛博览会传播健康文化的力度受到广泛认可赞同,平台更加坚实,传播作用日益凸显。2015年韩国大邱论坛博览会,是健康文化方舟登陆半岛的又一重要驿站,中韩两国高度重视关注,韩国政府将此列入中韩旅游年文化交流之一,韩国执政党主席金武星、大邱市市长权泳臻和副市长金延昶出席并致辞,大会期间丰富多彩的交流展示和优秀健身功法绝技表演等系列活动,为中韩自贸试验区提速发展创造了良机。</p><p><b>健康健美长寿文化走向国际十年的基本经验和启示</b></p><p>健康健美长寿文化走向国际的十年,是开拓创新的十年,是不懈奋进的十年。中华健康文化之所以能走出国门,走向国际,走进海外民众之中,根本原因是改革开放中国的综合国力日益提高,中国国际地位和影响力不断提升,中华优秀健康文化的独特魅力,健康健美长寿事业符合广大民众美好愿望和时代潮流,论坛博览会成为传播健康文化的有效载体和平台。</p><p>一、举旗正路是传播中华健康文化的灵魂。举什么旗、走什么路、为谁服务,是中华健康文化能不能走出去、如何走进去的根本所在。立足中国,放眼世界,服务人类,用旗帜引领方向,用旗帜锻造灵魂,把举好旗子、走正路子,结出果子作为传播中华健康文化的生命工程和灵魂工程。坚持中华健康文化本土化特色,创新健康健美长寿国际化发展之路,国际健康健美长寿学研究会始终都有自己的主心骨。始终高举“四面旗帜”、做到“四个坚持”、牢记“第一要务”。用科学的旗帜、文明的旗帜、奉献的旗帜、法治的旗帜导航领路,坚定不移坚持中国共产党的正确领导,坚定不移坚持先进文化的前进方向,坚定不移坚持科学发展观,坚定不移坚持中国特色社会主义理论指导,把致力于人民大众健康作为第一要务和根本目标。只有顺应时代发展潮流,顺应民心民意,弘扬美德,维护稳定,用清风正气正能量,才能确保中华健康文化传播的正确方向。</p><p>二、践诺践行是传播中华健康文化的动力。中华健康文化走出去、走进去,必须知行合一,在传播健康健美长寿理念的同时,努力提高健康意识和健身实践能力。十年来,我们主动发挥本会在国际健康界的独特优势和影响力,团结国内和国际健身文化社团、特别是海外华人华侨等多种力量,形成传播共识,共同为这一崇高事业齐心奋斗。紧抓健康健美长寿这一首要任务,本会牵头组织30多个会员国分会组织负责人,共同签署和发表了《曼谷宣言》、《北京宣言》、《上海宣言》,向国际社会做出了庄严承诺。顺应时代发展,致力民众健康,用科学理念引领,自觉维护和平和谐,加强合作交流,珍爱自然,保护环境。三部宣言集中体现了本会宗旨方向、科学精神、国际责任、时代担当。言必行,行必果,抱着高度负责精神举办的论坛博览会,为传播中华健康文化做了大量工作,发挥了重要作用。</p><p>三、政府推动是传播中华健康文化的保证。用先进文化引领方向,让健康文化走出国门,走进世界,离不开党的正确领导和政府的主导推动。做好传播中华健康文化这篇大文章,政府主导推动是社团组织走正路子,迈好步子,结出果子的根本保证。十年来,本会自觉置于政府主导推动下,每一届论坛博览会都要事先向国家有关部门主动汇报,自觉接受领导指导和监督。在海外办会,既要向我国驻外使领馆主动汇报会议内容规模,还要向所在国政府主动汇报议题和组团情况,自觉遵守所在国法律法规,得到了所在国提供的法律和安全保护。全国妇联、国家体育总局、中国奥委会、中国人民对外友协、中国人民外交学会、中国旅游协会、中华医学会、我国驻外使领馆等部门为本会开展健康文化传播铺路搭桥,给予了有力指导和支持推动。每届论坛博览会都有所在国重要领导人出席。政府主导推动有力提高对外健康文化交流水平,促进健康文化传播能力建设。</p><p>四、本会主体是传播中华健康文化的关键。在传播中华健康文化中坚持本会的主体地位,开拓进取,主动作为,是健康健美长寿文化走出去与走进去的关键所在。改革开放新时代的楷模、著名免疫学家冯理达教授,作为健康健美长寿事业的奠基人,率先将免疫学与气功学相结合,创立了健康健美长寿学,开展了深入研究,著书立说,成果丰硕。伍绍祖主席作为健康文化走向国际的开拓者和领路人,从主管国家体委、国家人体科学到主持研究会工作的40多年中,为我国体育走向世界、尤其是推动健康文化走出去把握了正确方向,贡献了毕生精心。黄晴宜主席以国际视野和加快走出去的胆识,确立了今后每一届论坛博览会都要选择在海外举行的运行图,主持举办了规格最高、规模最大、影响最广、成果最多的莫斯科论坛博览会。坚持中华健康文化传播的主体地位,确保了本会在国际健康健美长寿界的主导权和话语权。</p><p>五、合作共赢是传播健康文化的途径。中华健康文化是人类健康文化的重要组成部分,实现中华健康文化走出去,既要保持本土化特色,又要尊重和吸收各国各民族优秀传统健身文化,特色性和多样性融为一体,才能不断增強中华健康文化的传播力。十年来,国内全民健身活动广泛开展,健身文化日益融入现代文化、传统文化、校园文化、社区文化、企业文化之中,深刻影响着社会经济发展,改变着人们的生活方式。中华健身文化在走出去的国际化进程中交流日益紧密,合作研究更加深入,学术交流更加广泛,创新成果更加多样,争相申办承办论坛博览会的国家和地区越来越多,成为提升文化软实力的重要标志。产学研链条在国际化延伸中,科研成果转化成果日益增多,健身新产品得到广泛推广普及。合作共赢、深度融合发展,形 \n成了传播中华健康文化的新方略、新途径。</p><p><b>推动中华健康文化走出去的重要任务</b></p><p>党的十九大,习近平总书记对中华文化走出去多次作出重要论述、提出明确要求。十八大以来的五年，我们认真学习领会习总书记关于提高文化开放水平、加快中华文化走出去的重要论述,进一步做好健康文化走出去的工作,并将其作为时代赋予本会的一项长期任务。我们要以十九大精神为指导,以健康健美长寿论坛博览会举办十周年纪念活动为起点,把走出去传播中华健康文化作为中心任务,全方位作好健康健美长寿文化传播工作。</p><p>一、坚定文化自信，推动健康文化走出去是一项长期任务。习近平总书记指出,中华文化积淀着中华民族最深沉的精神追求,包含着中华民族最根本的精神基因,代表着中华民族独特的精神标识,要努力展示中华文化独特的魅力,塑造我国的国家形象。健康健美长寿文化是中华文化的重要组成部分,推动中华健康文化走出去,让国外民众透过健康健身文化的独特魅力,触摸中华文明脉博,感知当代中国发展活力,了解中华文化内涵,理解中国制度理念和价值观,具有极为重要的意义和作用。应当看到,推动健康文化走出去,是增强国家软实力、在综合国力竞争中赢得主动的迫切需要,是形成与经济社会发展水平和大国地位相适应的国家健康文化软实力的必然要求,是传播当代中国健康价值观的重要内容之一。这些年来,我们搭建起了论坛博览会这个平台,为健康文化走出去作了许多工作,但是,从服从和服务国家发展战略需求上看,还存在实力跟不上、力度不够大、内涵欠突出、合作交流不够深、内容和方法比较单一等不足和短板,与走出去的要求还有很大差距。要充分展示文明大国、健康大国的形象,就必须坚持国家站位,把传播健康健美长寿文化作为一项长期任务,坚持不懈地作下去。中华文化走出去的历史责任告诉我们,必须主动发声、敢于亮相,在国际健康健美长寿论坛博览会上占据话语权和主动权。</p><p>二、传播健康文化把内涵建设放在第一位。习近平总书记指出,文化的影响力首先是价值观的影响力,世界各种文化之争,本质上是价值观之争。中国优秀传统文化,包括健康文化是一座精神富矿,不仅铸造了历史的辉煌,而且在今天依然充满了智慧力量。坚守中华健康文化立场,把中华民族特色和世界潮流结合起来,把中华民族最基本的健康文化基因推广开来、传播出去,使跨时空、超国度、富有永恒魅力、具有当代价值的文化精神在世界上弘扬起来,是健康文化内涵建设的根本所在。尽管我们在健康健美长寿学研究和普及推广方面积累了一定的经验,但是,通过传播健康理念和健身方法,把当代中国价值观传播出去,还做得不够強,缺乏主动发声占据先机的敏锐。话语背后是思想,话语的力量体现着思想的力量。我们要在传播能力上打开思路,盘点梳理,充分发挥论坛博览会这个载体平台作用,围绕和平发展道路、合作共赢发展观、命运共同体、人类健康长寿共同价值观,对健康理念作出新阐释,从老方法中生出新话题,展现中国思想,响出中国主张。要积极参与世界健康文明对话,丰富人类健康健身文明色彩,让民众享内涵更丰富的健康文化生活。</p><p>三、主动肩负起健康文化走出去的主体作用。党和国家高度重视社团组织在传播中华文化中的独特作用,鼓励社团组织者参与文化中心交流项目,使经济走出去与文化走出去相得益彰。本会作为健康健美长寿学术国际性社团组织,在海外有30多个会员国分会组织,与海外华人华侨联系广泛,有着独特的学术优势、文化优势、交往优势、人脉优势和健身产品市场优势,我们要运用好这些平台和优势,主动肩负起健康文化走出去的主体责任,当好健康文化走出去的旗舰和主力,发挥海外热爱健康文化的华友华人作用,借助外力、借用外脑,借嘴说话、借筒传声,传播好健康理念,传播好健身方法,把中华优秀传统文化和当代中国价值观传播出去。应该看到,我们不仅有着巨大的优势,同时有着巨大的潜力,在走出去与走进去中以本会话语为主,以传播健康文化、传播中国价值观为核心,深入研究国外不同受众的文化传统、价值取向和接受心理,创新健康文化表达方式,展示健身功法绝活绝招绝技,精心构建对外话语体系,打造新概念新范畴新表述,增强话语传播的创造力、感召力、公信力。</p><p>四、拓展健康文化传播空间创新表达内容方式。健康文化的传播空间决定了走出去的宽度长度,健康文化的表达方式决定了走进去的维度和深度。中国日益走近世界舞台的中心,国内国际两个大局深度互动的大势,为拓展健康文化传播空间提供了千载难逢的机遇,紧跟中华文化走出去的时势,迫切需要我们厚植创新、协调、绿色、开放、共享的健康发展理念,培育良好的社会风尚,培育健康文化传播新能量新动力,我们必须依据世界政治生态、价值取向、健康追求,积极拓展健康文化传播空间,用好文化交流、文化传播、文化贸易三种方式,提高对外健康文化交流水平。从2014年起,把每一届国际健康健美长寿论坛博览会放到国外举办,让国外民众与健康文化零距离接触,亲手触摸,亲眼目睹,亲身体验健康文化的独特魅力,是拓展健康文化传播空间的重要举措。2016年韩国大邱论坛博览会又首次增加了大健康运动会项目,将強身健体、延年益寿、实用管用,奥运会、亚运会以及地区和行业运动会沒有列入的健身项目纳入其中,目的就是推动健身活动向多领域多层次延伸。要精心构建对外一体化健康传播新模式新体系,打造同声翻译、新范畴表述、新内容阐述、分论坛交流、新产品展示等多种传播平台,讲好中国故事,传播好中国声音。</p><p>五、重构全球化创新友好健康文化传播团队。最大限度用足用好用活全球健康传播资源,精心培育与世界第二大经济体和大国地位相一致相适应的大健康文化传播力量,推进大健康文化传播全球覆盖。凝聚在中华文化走出去的战略布局下,在政府、企业、社会组织和个人四种传播力量的组团中,争当社会组织传播中华健康文化的主力军和旗舰旗手。牵头重构健康文化传播四大版图,即创建全球健康文化协同创新中心,充分发挥本会协同创新优势,以各会员国分会组织为纽带和桥梁,以高校和科研院所为方阵,聚合人才资源,厚实科研学术力量,打造高端学术交流平台;构建覆盖五大洲会员国分会中心,用三年时间建立遍及全球的会员国分会组织网格,新增会员国到50个,会员人数达到上万人;成立全球健康文化传播协同指挥中心,打造走出去、走进去拳头力量,办好香港日报主流媒体,创办一流健康文化传媒和网络资讯平台。组建健康文化传播主力团队,抽组40到60人的精干力量,分期分批巡展健康文化瑰宝,传播健身功法绝招绝技;构建国内国际健身产品企业家联盟,邀请全球50位健身产品企业家组成企业家联盟,按照市场运作,建立强强联合,加速产学研融合,促进新成果转化,带动新产品研发,做大做強博览会推介。</p><p>\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n</p><p>中华文化迎来了走出去的春天,传播健康文化千载难逢,前景光明,道路宽广。发展好与大国地位相称的国家文化软实力,发挥好本会在国际健康健美长寿讲台上的主体作用,搭建好论坛博览会平台,在走进去的道路上宣传好中国主张,讲好中国故事,传播好中国声音,任重道远。让我们团结一心,不畏艰难,在致力于民众健康的新征途上,坚持创造性转化、创新性发展,不断铸就中华文化新辉煌,做出新成绩新贡献。</p>', '1', '12', '0', '1', '0', '2018-06-28 05:39:40');
INSERT INTO `ys_article` VALUES ('12', '十年成果回顾 精彩瞬间回放', '0', '', '', '', '', '网络', 'admin', '十年成果回顾 精彩瞬间回放', '<p><span style=\"display: inline !important;\">&nbsp;（1）2006泰国曼谷会议</span><br></p><p>&nbsp; 2006年11月4日至6日，首届国际健康健美长寿论坛暨健康产业博览会在泰国曼谷隆重召开。来自中国、俄罗斯、美国、德国、日本、荷兰、澳大利亚、新西兰、瑞士、爱尔兰、中国台北等20多个国家和地区的300多名科学界、养生保健界的代表汇聚一堂，交流学术成果，演示健康养生功法绝技，规划未来发展远景。会议发表了著名的《曼谷宣言》。</p><p align=\"center\"><img src=\"/static/upload/20180628-22-15-50_16908_首届国际健康健美长寿论坛名誉主席、泰王国政府第22届总理差瓦立上将以泰民族最高礼仪——击鼓，宣布大会开幕.jpg\" style=\"max-width:100%;\"><br></p><p>　　首届国际健康健美长寿论坛名誉主席、泰王国政府第22届总理差瓦立上将以泰民族最高礼仪——击鼓，宣布大会开幕。</p><p align=\"center\"><img src=\"/static/upload/20180628-22-16-01_13353_郭周礼秘书长在泰国大会上领读宣言.jpg\" style=\"max-width:100%;\"><br></p><p>　　郭周礼副主席兼秘书长在大会上领读《曼谷宣言》</p><p><span style=\"display: inline !important;\">&nbsp;（2） 2007中国北京会议</span></p><p>&nbsp;2007年11月14日至17日，国际健康健美长寿学研究会主办的2007北京国际健康健美长寿论坛暨健康产业博览会隆重举行，来自中国、美国、俄罗斯、泰国、波兰、菲律宾、日本、澳大利亚、马来西亚、新加坡、新西兰、香港和台湾等国家和地区的著名健康学专家、医学美容学专家、人体科学专家、政府高级官员、著名企业家和从事健康健美长寿学研究人员600余人参加会议。会议围绕“推动全民健康，构建和谐社会”宗旨，广泛开展了健康健美长寿学学术探讨，养生术交流，健康产品展示。胡锦涛同志指示办公室工作人员发来贺电，温家宝同志题词祝贺。全国妇联、国家体育总局、国家卫生部、全国政协港澳台委员会、国家民政部给予了大力支持。新华社、人民日报、中央电视台、光明日报、北京电视台、香港日报、新华网、千龙网、新浪网、搜狐网等50余家新闻单位和网络媒体记者作现场报道。</p><p align=\"center\"><br></p><p>　　冯理达主席宣布大会开幕</p><p align=\"center\"><br></p><p>　　大会期间冯理达主席同著名科学家、诺贝尔奖获得者牛满江教授亲切交谈。</p><p>　　（3）2008中国北京会议</p><p>　　全国妇联批准并担任指导单位，由中国妇女报社、国际健康健美长寿学研究会共同主办的2008北京国际健康健美长寿论坛暨健康产业博览会于2008年10月7日至9日在北京举行。来自美国、加拿大、澳大利亚、英国、法国、西班牙、俄罗斯、日本、韩国、泰国、新加坡、马来西亚等20多个国家和港澳台地代表600余人参加大会。</p><p>　　大会以“弘扬传统文化，构建和谐世界”为主题，开展了专家讲座、养生术交流、保健产品推广、健康知识咨询等丰富多彩活动，展示了健康健美长寿学研究应用新成果。</p><p>　　伍绍祖主席在会上指出，健康长寿事业要以中国特色理论为指导，将健康事业融入市场，抓住机遇，遵守法纪，中西结合，贯彻“双百”方针，把论坛博览会办成像奥运会一样精彩。</p><p align=\"center\"><br></p><p>　　国际健康健美长寿学研究会主席、论坛组委会主席伍绍祖，向时任全国妇联党组书记、副主席、书记处第一书记黄晴宜颁发名誉主席聘书。</p><p>　　（4）2009中国上海会议</p><p>　　2009年 \n10月29日至11月2日，2009上海国际健康健美长寿论坛暨健康产业博览会在上海友谊会堂和上海展览中心隆重举行。来自20多个国家和地区的3000余名代表，围绕“共享健康科学成果，共建人类和谐世界”这一主题，通过国际健康健美长寿主题报告、学术交流、专题研讨、成果展示、健身讲座、健康咨询、现场观摩、产业推介等系列活动，充分展示了推动健康事业发展，倡导科学健康生活方式取得的新成果。</p><p align=\"center\">&nbsp;</p><p>　　伍绍祖主席与获得寿星奖的部分代表合影</p><p>　　（5）2010中国北京会议</p><p>　　全国妇联担任指导单位，由国际健康健美长寿学研究会、中国妇女报社、中国老年保健协会等共同主办的“振国杯”2010北京国际健康健美长寿论坛暨健康产业博览会，于2010年9月2日至5日在北京温都水城举行。论坛围绕健康健美长寿主旨，通过健康健美长寿学新成果展示、健康健美养生术表演、专题讲座、名家名医咨询、寿星表彰、养生名家书画展等系列精彩纷呈的活动，体现了共享健康科学成果，共建人类和谐世界这一主题。来自美国、加拿大、波兰、澳大利亚、俄罗斯、日本、新西兰、荷兰、马来西亚、泰国、德国、英国、台湾、香港等20多个国家和地区的2000余名政府官员、健康科学专家、国际贸易商、国际健康健美长寿学研究会各分会负责人和海内外友人参加了大会。</p><p>　　原全国人大副委员长、全国妇联主席、大会名誉主席顾秀莲宣布大会开幕（左）伍绍祖主席在闭幕式上讲话（右）</p><p>　　（6）2011中国济南会议</p><p>　　2011年11月5日至9日， \n国际健康健美长寿论坛暨健康产业博览会在山东济南隆重举行。全国妇联担任本次大会指导单位，由国际健康健美长寿学研究会、中国妇女报社共同主办，山东天地健生物工程有限公司、北京振国康美文化传播有限责任公司联合承办。 \n来自国内外20多个国家和地区的2000余名代表汇聚一堂，围绕“传承人类优秀文化遗产，推动健身文化发展繁荣”这一主题，通过举办健康健美长寿主题报告、学术交流、专题研讨、成果展示、健身讲座、健康咨询、现场观摩、产品推介等系列活动，充分展示了健身文化创新发展所取得的新亮点新成果。</p><p align=\"center\"><br></p><p>　　原中国最高检察院检察长、大会名誉主席贾春旺宣布大会开幕</p><p>　　（7）2013中国通化会议</p><p>　　2013年8月17日至20日，2013国际健康健美长寿论坛暨健康产业博览会在吉林通化举行。本次论坛开展了学术交流、养生术交流、保健咨询、专家讲座、产品展示等交流研讨。 \n会议由国际健康健美长寿学研究会、中国妇女报社、北京振国集团主办。原全国妇联副主席、书记处第一书记黄晴宜，中国老年体育协会主席、原国家体委副主任张发强等领导出席。来自俄罗斯、新加坡、德国、法国、日本以及港澳台等十多个国家和地区的健康养生领域专家和企业代表汇聚长白山下，共商健康健美长寿事业发展大计。论坛博览会期间召开的国际健康健美长寿学研究会理事会上，一致推选黄晴宜女士为国际健康健美长寿学研究会主席，并担任2014莫斯科国际健康健美长寿论坛暨健康产业博览会主席。俄罗斯武术联合会会长基里延科、特鲁特涅夫向大会发来贺电。</p><p align=\"center\"><br></p><p>　　大会主席黄晴宜宣布论坛暨博览会开幕</p><p>　　（8）2014俄罗斯莫斯科会议</p><p>　　2014年8月16日至17日，2014国际健康健美长寿论坛暨健康产业博览会在莫斯科隆重举行。本次论坛博览会是一次规模最高、规模最大、影响最广、亮点最多的盛会。会议作为中俄文化年活动之一，受到中俄两国政府、联合国教科文组织和广大民众的高度重视和关注。俄罗斯副总理特鲁特涅夫出席开幕式并致辞。联合国教科文组织代表阿拉女士、中国驻俄罗斯大使馆李辉大使，肯尼亚驻俄罗斯大使馆库尔加特大使等出席并致辞，中国人民对外友好协会李小琳会长发来贺信。会议由国际健康健美长寿学研究会黄晴宜主席主持。来自30多个国家和地区的600余名代表参加会议。会议围绕医疗与健康、体育与健康、文化艺术与健康、武术气功与健康等内容展开交流探讨。</p><p align=\"center\"><br></p><p>　　（1） 特鲁特涅夫副总理、黄晴宜主席、李辉大使出席会议</p><p align=\"center\"><br></p><p>　　（2） 联合国教科文组织代表阿拉女士致辞</p><p>　　（9）2015韩国大邱会议</p><p>　　2015年10月12日至16日，“文登杯”2015韩国大（邱）国际健康健美长寿论坛暨健康产业博览会在韩国大邱市举行。韩国大邱市市长权永臻、副市长金延昶、中国驻韩国釜山总领事阎凤兰等致辞，中国对外友协李小林会长、中国外交学会杨文昌会长等发来贺电。来自10多个国家和地区600余名代表参加了会议。会议围绕健康、健美、长寿主题，交流科研学术成果，优秀健身功德绝技，展出出养生、养老、美妆等新产品。会议的最大亮点对加强中韩友好合作，推动自贸区试点工作产生了积极作用。</p><p align=\"center\"><img alt=\"\" src=\"file:///C:/DOCUME~1/ADMINI~1/LOCALS~1/Temp/ksohtml/wpsFA6.tmp.png\" width=\"372\" height=\"247\"></p><p>　　中韩之夜韩方表演人员合影留念</p><p>　　（10）2016韩国大邱会议</p><p>　　2016年10月13日至16日，2016国际健康健美长寿论坛暨首届大健康运动会在韩国大邱市举行。本次大会是检阅10年成果，辞行划今年10年发展大计、开放创新的的盛会。会议将融学术交流、健身功法演示、健身产品推介展销与大健康运动会为一体，将强身健身、延年益寿、大众实用、易于普及，未列入国际和地区及行业运动会的健身项目囊括其中，充分体现了本次大会更高、更强、更健康的特色理念。</p><p align=\"center\"><img alt=\"\" src=\"file:///C:/DOCUME~1/ADMINI~1/LOCALS~1/Temp/ksohtml/wpsFA7.tmp.png\" width=\"372\" height=\"275\"></p><p>\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n</p><p>　　黄晴宜主席为大邱市市长权泳臻颁发奖牌</p><p><br></p>', '1', '0', '0', '1', '0', '2018-06-28 14:14:11');
INSERT INTO `ys_article` VALUES ('13', '十年成果回顾 精彩瞬间回放', '1', '推荐', '', '', '', '香港日报网', 'admin', '十年成果回顾 精彩瞬间回放', '<p><span style=\"display: inline !important;\">&nbsp;（1）2006泰国曼谷会议</span><br></p><p>&nbsp;2006年11月4日至6日，首届国际健康健美长寿论坛暨健康产业博览会在泰国曼谷隆重召开。来自中国、俄罗斯、美国、德国、日本、荷兰、澳大利亚、新西兰、瑞士、爱尔兰、中国台北等20多个国家和地区的300多名科学界、养生保健界的代表汇聚一堂，交流学术成果，演示健康养生功法绝技，规划未来发展远景。会议发表了著名的《曼谷宣言》。</p><p align=\"center\"><img src=\"/static/upload/20180628-22-19-37_13010_首届国际健康健美长寿论坛名誉主席、泰王国政府第22届总理差瓦立上将以泰民族最高礼仪——击鼓，宣布大会开幕.jpg\" style=\"max-width:100%;\"><br></p><p style=\"text-align: center;\">　　首届国际健康健美长寿论坛名誉主席、泰王国政府第22届总理差瓦立上将以泰民族最高礼仪——击鼓，宣布大会开幕。</p><p align=\"center\"><img src=\"/static/upload/20180628-22-19-48_10099_郭周礼秘书长在泰国大会上领读宣言.jpg\" style=\"max-width:100%;\"><br></p><p style=\"text-align: center;\">　　郭周礼副主席兼秘书长在大会上领读《曼谷宣言》</p><p>&nbsp;（2） 2007中国北京会议</p><p>&nbsp;2007年11月14日至17日，国际健康健美长寿学研究会主办的2007北京国际健康健美长寿论坛暨健康产业博览会隆重举行，来自中国、美国、俄罗斯、泰国、波兰、菲律宾、日本、澳大利亚、马来西亚、新加坡、新西兰、香港和台湾等国家和地区的著名健康学专家、医学美容学专家、人体科学专家、政府高级官员、著名企业家和从事健康健美长寿学研究人员600余人参加会议。会议围绕“推动全民健康，构建和谐社会”宗旨，广泛开展了健康健美长寿学学术探讨，养生术交流，健康产品展示。胡锦涛同志指示办公室工作人员发来贺电，温家宝同志题词祝贺。全国妇联、国家体育总局、国家卫生部、全国政协港澳台委员会、国家民政部给予了大力支持。新华社、人民日报、中央电视台、光明日报、北京电视台、香港日报、新华网、千龙网、新浪网、搜狐网等50余家新闻单位和网络媒体记者作现场报道。</p><p align=\"center\"><img src=\"/static/upload/20180629-13-43-27_10370_1530250833(1).png\" style=\"max-width:100%;\"></p><p align=\"center\"><span style=\"display: inline !important;\">冯理达主席宣布大会开幕</span><br></p><p align=\"center\"><img src=\"/static/upload/20180629-13-44-01_10195_1530250859(1).png\" style=\"max-width:100%;\"><br></p><p style=\"text-align: center;\">　　大会期间冯理达主席同著名科学家、诺贝尔奖获得者牛满江教授亲切交谈。</p><p>　　（3）2008中国北京会议</p><p>　　全国妇联批准并担任指导单位，由中国妇女报社、国际健康健美长寿学研究会共同主办的2008北京国际健康健美长寿论坛暨健康产业博览会于2008年10月7日至9日在北京举行。来自美国、加拿大、澳大利亚、英国、法国、西班牙、俄罗斯、日本、韩国、泰国、新加坡、马来西亚等20多个国家和港澳台地代表600余人参加大会。</p><p>　　大会以“弘扬传统文化，构建和谐世界”为主题，开展了专家讲座、养生术交流、保健产品推广、健康知识咨询等丰富多彩活动，展示了健康健美长寿学研究应用新成果。</p><p>　　伍绍祖主席在会上指出，健康长寿事业要以中国特色理论为指导，将健康事业融入市场，抓住机遇，遵守法纪，中西结合，贯彻“双百”方针，把论坛博览会办成像奥运会一样精彩。</p><p align=\"center\"><img src=\"/static/upload/20180629-13-44-14_10503_1530250887(1).png\" style=\"max-width:100%;\"><br></p><p style=\"text-align: center;\">　　国际健康健美长寿学研究会主席、论坛组委会主席伍绍祖，向时任全国妇联党组书记、副主席、书记处第一书记黄晴宜颁发名誉主席聘书。</p><p>　　（4）2009中国上海会议</p><p>　　2009年 \n10月29日至11月2日，2009上海国际健康健美长寿论坛暨健康产业博览会在上海友谊会堂和上海展览中心隆重举行。来自20多个国家和地区的3000余名代表，围绕“共享健康科学成果，共建人类和谐世界”这一主题，通过国际健康健美长寿主题报告、学术交流、专题研讨、成果展示、健身讲座、健康咨询、现场观摩、产业推介等系列活动，充分展示了推动健康事业发展，倡导科学健康生活方式取得的新成果。</p><p align=\"center\"><img src=\"/static/upload/20180629-13-44-29_11504_1530250914(1).png\" style=\"max-width:100%;\"><br></p><p style=\"text-align: center;\">　　伍绍祖主席与获得寿星奖的部分代表合影</p><p>　　（5）2010中国北京会议</p><p>　　全国妇联担任指导单位，由国际健康健美长寿学研究会、中国妇女报社、中国老年保健协会等共同主办的“振国杯”2010北京国际健康健美长寿论坛暨健康产业博览会，于2010年9月2日至5日在北京温都水城举行。论坛围绕健康健美长寿主旨，通过健康健美长寿学新成果展示、健康健美养生术表演、专题讲座、名家名医咨询、寿星表彰、养生名家书画展等系列精彩纷呈的活动，体现了共享健康科学成果，共建人类和谐世界这一主题。来自美国、加拿大、波兰、澳大利亚、俄罗斯、日本、新西兰、荷兰、马来西亚、泰国、德国、英国、台湾、香港等20多个国家和地区的2000余名政府官员、健康科学专家、国际贸易商、国际健康健美长寿学研究会各分会负责人和海内外友人参加了大会。</p><p align=\"center\"><img src=\"/static/upload/20180629-13-44-49_16140_1530250931(1).png\" style=\"max-width:100%;\"><br></p><p style=\"text-align: center;\">　　原全国人大副委员长、全国妇联主席、大会名誉主席顾秀莲宣布大会开幕（左）伍绍祖主席在闭幕式上讲话（右）</p><p>　　（6）2011中国济南会议</p><p>　　2011年11月5日至9日， \n国际健康健美长寿论坛暨健康产业博览会在山东济南隆重举行。全国妇联担任本次大会指导单位，由国际健康健美长寿学研究会、中国妇女报社共同主办，山东天地健生物工程有限公司、北京振国康美文化传播有限责任公司联合承办。 \n来自国内外20多个国家和地区的2000余名代表汇聚一堂，围绕“传承人类优秀文化遗产，推动健身文化发展繁荣”这一主题，通过举办健康健美长寿主题报告、学术交流、专题研讨、成果展示、健身讲座、健康咨询、现场观摩、产品推介等系列活动，充分展示了健身文化创新发展所取得的新亮点新成果。</p><p align=\"center\"><img src=\"/static/upload/20180629-13-45-06_16778_1530250947(1).png\" style=\"max-width:100%;\"><br></p><p style=\"text-align: center;\">　　原中国最高检察院检察长、大会名誉主席贾春旺宣布大会开幕</p><p>　　（7）2013中国通化会议</p><p>　　2013年8月17日至20日，2013国际健康健美长寿论坛暨健康产业博览会在吉林通化举行。本次论坛开展了学术交流、养生术交流、保健咨询、专家讲座、产品展示等交流研讨。 \n会议由国际健康健美长寿学研究会、中国妇女报社、北京振国集团主办。原全国妇联副主席、书记处第一书记黄晴宜，中国老年体育协会主席、原国家体委副主任张发强等领导出席。来自俄罗斯、新加坡、德国、法国、日本以及港澳台等十多个国家和地区的健康养生领域专家和企业代表汇聚长白山下，共商健康健美长寿事业发展大计。论坛博览会期间召开的国际健康健美长寿学研究会理事会上，一致推选黄晴宜女士为国际健康健美长寿学研究会主席，并担任2014莫斯科国际健康健美长寿论坛暨健康产业博览会主席。俄罗斯武术联合会会长基里延科、特鲁特涅夫向大会发来贺电。</p><p align=\"center\"><img src=\"/static/upload/20180629-13-45-23_14491_1530250966(1).png\" style=\"max-width:100%;\"><br></p><p style=\"text-align: center;\">　大会主席黄晴宜宣布论坛暨博览会开幕</p><p>　　（8）2014俄罗斯莫斯科会议</p><p>　　2014年8月16日至17日，2014国际健康健美长寿论坛暨健康产业博览会在莫斯科隆重举行。本次论坛博览会是一次规模最高、规模最大、影响最广、亮点最多的盛会。会议作为中俄文化年活动之一，受到中俄两国政府、联合国教科文组织和广大民众的高度重视和关注。俄罗斯副总理特鲁特涅夫出席开幕式并致辞。联合国教科文组织代表阿拉女士、中国驻俄罗斯大使馆李辉大使，肯尼亚驻俄罗斯大使馆库尔加特大使等出席并致辞，中国人民对外友好协会李小琳会长发来贺信。会议由国际健康健美长寿学研究会黄晴宜主席主持。来自30多个国家和地区的600余名代表参加会议。会议围绕医疗与健康、体育与健康、文化艺术与健康、武术气功与健康等内容展开交流探讨。</p><p align=\"center\"><img src=\"/static/upload/20180629-13-45-43_15569_1530250984(1).png\" style=\"max-width:100%;\"><br></p><p style=\"text-align: center;\">　　（1） 特鲁特涅夫副总理、黄晴宜主席、李辉大使出席会议</p><p align=\"center\"><img src=\"/static/upload/20180629-13-46-08_14043_1530250999(1).png\" style=\"max-width:100%;\"><br></p><p style=\"text-align: center;\">　　（2） 联合国教科文组织代表阿拉女士致辞</p><p>　　（9）2015韩国大邱会议</p><p>　　2015年10月12日至16日，“文登杯”2015韩国大（邱）国际健康健美长寿论坛暨健康产业博览会在韩国大邱市举行。韩国大邱市市长权永臻、副市长金延昶、中国驻韩国釜山总领事阎凤兰等致辞，中国对外友协李小林会长、中国外交学会杨文昌会长等发来贺电。来自10多个国家和地区600余名代表参加了会议。会议围绕健康、健美、长寿主题，交流科研学术成果，优秀健身功德绝技，展出出养生、养老、美妆等新产品。会议的最大亮点对加强中韩友好合作，推动自贸区试点工作产生了积极作用。</p><p align=\"center\"><img src=\"/static/upload/20180629-13-46-20_17491_1530251031(1).png\" style=\"max-width:100%;\"><br></p><p style=\"text-align: center;\">　　中韩之夜韩方表演人员合影留念</p><p>　　（10）2016韩国大邱会议</p><p>　　2016年10月13日至16日，2016国际健康健美长寿论坛暨首届大健康运动会在韩国大邱市举行。本次大会是检阅10年成果，辞行划今年10年发展大计、开放创新的的盛会。会议将融学术交流、健身功法演示、健身产品推介展销与大健康运动会为一体，将强身健身、延年益寿、大众实用、易于普及，未列入国际和地区及行业运动会的健身项目囊括其中，充分体现了本次大会更高、更强、更健康的特色理念。</p><p align=\"center\"><img src=\"/static/upload/20180629-13-46-37_14263_1530251046(1).png\" style=\"max-width:100%;\"><br></p><p>\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n</p><p style=\"text-align: center;\">　　黄晴宜主席为大邱市市长权泳臻颁发奖牌</p><p><br></p>', '1', '9', '0', '1', '0', '2018-06-28 14:18:47');
INSERT INTO `ys_article` VALUES ('14', '钱学森论中医：人体科学的方向是中医，不是西医', '11', '头条,推荐,加粗', '', '', '/static/upload/20180629-09-00-42_18994_200911910536835.jpg', '网络', 'admin', '从人体科学的观点，中医有许多比西方医学高明的地方，但将来的医学一定是集中医、西医各民族医学于一炉的新医学。\n', '<p style=\"text-align: center;\">&nbsp;<img src=\"/static/upload/20180629-08-58-01_11563_200911910536835.jpg\" style=\"max-width: 100%;\" class=\"w-e-selected\"></p><p>&nbsp;▲钱学森，院士、中国工程院院士，1911-2009，生于上海，祖籍浙江杭州。1934年毕业于交通大学。1939年获美国加州理工学院航空与数学博士学位。1957年被选聘为中国科学院学部委员(院士)。1994年被选聘为中国工程院院士。曾任第七机械工业部副部长、国防科学技术委员会副主任、中国科学技术协会主席和全国政协副主席。</p><p>&nbsp;医学的前途是中医现代化，而不在什么其他途径。人体科学的方向是中医，不是西医，西医也要走到中医的道路上来。</p><p>&nbsp;1、中医包含着科学真理</p><p>&nbsp;1、我们知道中医包含着科学真理，非常宝贵的科学真理，但人们“以貌取人”，怀疑中医有没有真理，或进而认为中医是封建糟粕。（1984年5月16日致李印生——《钱学森书信选（上卷）》0088页，国防工业出版社 \n2008，6）</p><p>&nbsp;2、中医理论托附于阴阳五行干支的思维框架，已经是辩证的了，比经典西医学强；但我想中医理论还是有点简单化，所以中医名医还要靠临床经验。（1988年11月21日致刘静和——《钱学森书信选（上卷）》0409页）</p><p>&nbsp;3、我的体会是，中医理论就是把几千年的临床经验用阴阳五行干支的框架来整理成唯象学理论。这个框架一方面有用，因为它把复杂的关系明朗化了；另一方面又有局限性，因为框架太僵硬了。你们搞中医唯象学就是一方面要发扬传统中医的优点，一方面补其不足。（1988年11月4日致邹伟俊——《钱学森书信选（上卷）》0402页）</p><p>&nbsp;4、老中医也知道，只阴阳五行也不够用，他们都根据自己临床经验和思维联想加以补充。我想主要是相生相剋。但这也就是辩证法！（1994年10月7日致于景元——《钱学森书信选（下卷）》0979页）</p><p>&nbsp;5、中医理论就是典型法，从病人各种典型概括出阴阳五行的理论。但就是对人体这一千百年事例中得出的中医理论也不能作为死教条，按病人实际情况还应做适当调整。中国有名的中医都是根据自己行医经验对医方做些适当变动，这才是名医，不是庸医。（1995年1月25日致李世辉——《钱学森书信选（下卷）》1010页）</p><p>&nbsp;6、医学不已有几千年的经验了吗？人的一生是有变化的，生、老、病、死，但模式基本一样，在几千年中重复了亿亿万万次，所以医学，特别是中医，是有经验基础的。 \n（1994年12月2日致王寿云等六同志——《钱学森书信选（下卷）》0992页）</p><p>&nbsp;7、中医的理论是系统观的，这是科学的。（《钱学森人体科学与当代科学技术发展纵横观》107页——此为1983年至1987年钱学森在507所学术讨论会上的历次讲话汇编）</p><p>&nbsp;8、中医的特点在于从整体、从系统来看问题。（《钱学森等论人体科学》297页，人民军医出版社1988，12）</p><p>&nbsp;9、中医理论中的阴阳说和五行说，中医理论的脏腑论和经络学说，中医理论的六淫、七情，中医讲究辨证论治，这些都强调了人体的整体观以及人和环境、人和工作的整体观。应该说，这是符合马克思主义哲学、辩证唯物主义的。中医理论的缺点是它和现代技术挂不上钩，语言、概念是两套。（《钱学森等论人体科学》7页）</p><p>&nbsp;10、信中所述中医“证”的观点我同意，“证”是高层次整体性的。但系统论也不是元气论，只强调整体，不考虑微观原子论、还原论，系统论是整体论与还原论的辩证统一。（1988年8月26日致黄建平——《钱学森书信选（上卷）》0389页）</p><p>&nbsp;11、经络是我们中医理论中基础性的东西，是个非常重要的问题。（《钱学森人体科学与当代科学技术发展纵横观》149页）</p><p>&nbsp;12、十二经络不能代表两个系统，是一个功能系统。（《钱学森人体科学与当代科学技术发展纵横观》241页）</p><p>&nbsp;13、系统的理论是现代科学理论里的一个非常重要的部分，是现代科学的一个重要组成部分，而中医理论又恰恰与系统科学完全融合在一起，所以还有个旁征，人的社会实践和科学的发展已经指出中医的这个方向是对的。不仅因为有中医在中国这块土地上的几千年和这么多人民的实践证明，而且另一方面，中医的看法又跟现代科学中最先进、最尖端的系统科学的看法是一致的。也就是我们在这个所里讲的人体科学的看法。我讲过，越来越证明我们这个观点错不了，我们的方向是正确的。（《钱学森人体科学与当代科学技术发展纵横观》301页）</p><p>&nbsp;14、从人体科学的观点，中医有许多比西方医学高明的地方，但将来的医学一定是集中医、西医各民族医学于一炉的新医学。（1990年12月11日致徐振林——《钱学森书信选（上卷）》0553页）</p><p>&nbsp;15、医学的前途是中医现代化，而不在什么其他途径。人体科学的方向是中医，不是西医，西医也要走到中医的道路上来。（《钱学森等论人体科学》277页）</p><p>&nbsp;16、我认为传统医学是个珍宝，因为它是几千年实践经验的总结，分量很重。更重要的是：中医理论包含了许多系统论的思想，而这是西医的严重缺点。所以中医现代化是医学发展的正道，而且最终会引起科学技术体系的改造——科学革命。（1985年9月23日祝世讷——《钱学森书信选（上卷）》0191页）</p><p>&nbsp;2、中医理论是经典意义的自然哲学，不是现代意义的自然科学</p><p>&nbsp;1、中医理论是经典意义的自然哲学，不是现代意义的自然科学。其实这里并没有贬低中医理论的意思，没有自然哲学也就没有自然科学；今天的中医理论正是将来中医现代化（或医学科学革命）的老祖宗。（1983年3月17日致黄建平——《钱学森书信选（上卷）》0044</p><p>&nbsp;2、人类认识自然界之初，必然有一个自然哲学的阶段。在经过了科学的巨大发展的今天，已经不再需要自然哲学了，而应该接受自然科学。我认为中医理论很像自然哲学。其中包含着人类智慧的珍宝。人体科学很需要这部分珍宝，要加以整理、提高，而绝不能丢掉。（《钱学森人体科学与当代科学技术纵横观》108页）</p><p>&nbsp;3、什么叫自然哲学？什么叫自然科学？我以为凡不是自然科学的、从经验概括起来的理论，都可称之为自然哲学，因为它必然包括一些猜测、臆想的东西。那么，什么叫自然科学？自然科学的学问是：一方面研究一种对象的学问，但一方面又和全部自然科学有机地结合成一个整体；如现代科学的物理、化学、生物学、地学、天文学、电子学、机械学、水力学、海洋学、气象学等，都是互相关联的一个整体。所以中医理论是自然哲学，它独立于现代科学之外。（1984年6月2日致黄建平——《钱学森书信选（上卷）》0092页）</p><p>&nbsp;4、中医是东方古代的自然哲学；而恩格斯早就说过，当马克思主义哲学出现后，再去恢复古代的自然哲学不但没有必要，而且是倒退！所以我们要实现中医现代化，也要把中医的哲学理论现代化；这就是在马克思主义哲学的指导下，用现代系统科学的语言改写中医的理论。而现代系统科学语言中最接近于这个东方古代的自然哲学的是武汉数字工程研究所吴学谋同志的“泛系元理论”。（1987年8月19日致肖汉明——《钱学森书信选（上卷）》0335页）</p><p>&nbsp;3、用马克思主义哲学及系统论为指导</p><p>&nbsp;1、用马克思主义哲学及系统科学来攻中医现代化这个关是可以肯定的了，这就是中医现代化的战略。（1985年12月30日致黄建平——《钱学森书信选（上卷）》0218页）</p><p>&nbsp;2、要注意现在已有苗头的一些很重要的哲学观点，这就是系统论，人天观。系统论，人天观实际是人与宇宙形成一个超巨系统的观点。是对马克思主义哲学已有的一些最核心的观点，如辩证唯物主义，自然辩证法，历史唯物主义，认识论等的一个新的补充。（《钱学森等论人体科学》302页）</p><p>&nbsp;3、我们在北京的同道近年来已明确地认为：1）有一类特殊复杂的系统，开放的复杂巨系统；2）人体是开放的复杂巨系统；3）研究开放的复杂巨系统不能用Prigogine的方法，也不能用Haken的方法，那些都不行，只能用从定性到定量综合集成法。因此中医系统论也必须用这一概念，老的一套是不能解决问题的。我以为中医理论其实已蕴育着我上述现代化的观点。（1991年3月30日致祝世讷——《钱学森书信选（上卷）》0578页）</p><p>&nbsp;4、什么是人天观？请注意在复杂巨系统前面的“开放的”这三个字。开放是说人体这一复杂巨系统是与其周围环境有不断交往的：小的是呼吸、饮食、排便，还有声光信息的受与发；大一点如人生活的环境既对人有影响，而环境又不断受人的影响与改造；再大一点的就是阳光，空间磁场，宇宙线对人体的作用。所以人体是对小到生活周围，大到宇宙，都有交往，都是开放的。这一观点，我称之为人天观。把人放在主动地位，不是我国古代的“天人观”。（1995年1月26日致邹伟俊——《钱学森书信选（下卷）》1011页）</p><p>&nbsp;5、18世纪开始的所谓“中西汇通”，大概脱不了当时的“西为中用”；也有点成绩，如中药引用了西洋参。五六十年代的“中西医结合”实际上是用西医的理论来改造中医，有成绩；但最后也走不通，中西医结合的老专家邝安堃就亲口对我表示困境。我想指导思想只能是马克思主义哲学、辩证唯物主义；再就是近年来在我国出现的开放复杂巨系统理论。医药是对人的，而人体就是开放的复杂巨系统。是复杂巨系统，不仅仅是系统，更不是简单系统。这些都是带根本性的，是新一代中医药学研究的出发点。</p><p>&nbsp;一要注意不搞机械唯物论，盲目相信仪器；二要注意不搞简单化，把人体当作简单系统。仪器读数是表象，深层实质是复杂的，决不能“一对一”。中医药研究要走人体科学的道路，也就是综合中医和西医等的成就，上升到更高层次的医学、21世纪的医学。而综合要靠开放的复杂巨系统理论。（1990年6月15日致宋健——《钱学森书信选（上卷）》0520页）</p><p>&nbsp;6、开放复杂巨系统概念的形成和从定性到定量总合集成法的创立是中国人的功劳，从此人认识世界的能力登上了一个新台阶。</p><p>&nbsp;既然人是一个开放的复杂巨系统，我们研究人体科学，就要应用“从定性到定量综合集成法”，这是一个根本的观点和方法论。现在我要说，我们的理论又上升了，上升到人体是一个开放的复杂巨系统这个观点，我们要以这个观点，来改造一切旧的、关于人体的学问。（钱学森《对人体科学研究的几点认识》——《中国人体科学》1991，1卷2期53页）</p><p>&nbsp;7、西医中的人知识面不广，尤其对今天人体科学的新发现不慎了了，而新的发现却说明经典西医理论局限性太大，好多现象讲不清。所以国外医学家倒反而对中医理论很感兴趣。要解决这个问题就必须启发诱导这些西医论者认识今天人体科学的实际；中医讲究意识、情绪的重要性，这又是西医论者的大忌！他们以为讲科学就不能讲意识，不能讲精神，这也是个误解。现在科学早已证明意识和精神不过是物质的大脑活动的表现而已，没有什么可以大惊小怪的；也因此意识和精神可以反作用与人体。</p><p>&nbsp;以上两个方面的事做得好，还可以打开一些中医的眼界，看到现代科学毕竟是进步的，不能死抱着几本中医经典不放，闭关自守。我们要在马克思主义哲学指导下，把中外医学的好东西结合起来，用系统科学来促使中医现代化，即医学现代化。这是由低到高的扬弃！</p><p>&nbsp;综合而扬弃是最难的。历史上的飞跃、科学革命都是如此得来的；马克思创立历史唯物主义，创立剩余价值论是综合而扬弃；爱因斯坦创立相对论是综合而扬弃！（1983年11月29日致邹伟俊——《钱学森书信选（上卷）》0052页）</p><p>&nbsp;8、把中医（包括气功，人体特异功能等）都纳入到科学技术的体系里，创立新的关于人的科学，我称其为人体科学。这样的科学一旦创立起来，必然会提高、改造现在已经有的科学技术体系，当然这一步应该是彻底的，不仅是现象的概括，不仅要知其当然，而且要能讲出其所以然。这才是真正的中医现代化；不，不止于现代化，甚至可以说是中医的未来化！这是一个伟大的任务，是改造整个科学技术体系，创立新的科学技术体系，所以是一次科学革命。（《钱学森等论人体科学》301页）</p><p>&nbsp;4、写出一套现代的中医书籍</p><p>&nbsp;1、要在我国保存并进而发展中医，必须在维护中医理论全部实质的前提下，把其表面形象改变一下：用马克思主义哲学，用现代科学的语言来表述，不用古医书中的词句，写出真正的现代中医书籍。这要一整套书，不是另星的一本、几本，要从人体理论到医理，到临床医学。</p><p>&nbsp;所以中医（当然包括藏医、蒙医等）的现代阐述是一件关系到祖国传统医学的生死存亡大事，而且时不我待呵！（1984年5月22日致崔月犁——《钱学森书信选（上卷）》0090页）</p><p>&nbsp;2、为什么中医教育办不好？后继乏人！</p><p>&nbsp;我想一个带根本性的问题是中医的面貌看起来好像不和现代意识形态相一致：古文而不是现代语文，讲什么阴阳五行……而不是对立统一和大系统等等，从而使现代的中国青年难于领会，学也学不进，更不要说掌握医理了。</p><p>&nbsp;中医理论的这一令人生畏的形象也使得不少人对它不理解，所以尽管《宪法》上明明写了，他还不愿支持，甚而打击。就连中医学院也有人反对中医！</p><p>&nbsp;怎么办？</p><p>&nbsp;我想只能对症下药，给中医换装！把中医理论、中医医理用1.现代语言，2.马克思主义哲学——辩证唯物主义——来阐述清楚，写出一套现代的中医书籍。这还不是中医现代化，不是用将来会出现的人体科学来提高中医，创造新医学，只是保留中医的真正科学内容，老老实实地阐明中医的道理。请老中医同志不要担心，我们是要把中医的珍宝保留下来！</p><p>&nbsp;我在四川科技出版社出版的《大自然探索》1983年3期上的《马克思主义哲学的结构和中医理论的现代阐述》讲的就是这项工作。我现在感到它已经十分迫切，不能拖延了！我们要培养下一代中医只能用这套书籍，不然培养不出今天的中医。</p><p>&nbsp;有了这套中医医书，反对中医的人也能说服了。因为我们是讲马克思主义的呀！</p><p>&nbsp;我也认为只要领导下这个决心，做这件工作的人是有的，集中起来干，五年就能出第一本书。</p><p>&nbsp;这件事也是贯彻执行《宪法》的条文，是大事；我不知向谁讲，先向您讲吧，请您考虑。（1984年5月16日致李印生——《钱学森书信选（上卷）》0088页）</p><p>&nbsp;3、我现在认为，中医所遇到的困难是个20世纪社会主义中国的意识形态问题。中医理论讲阴阳五行，令人望而生畏！我国的各级领导总是不大敢放手支持中医、发展中医，心里怕。怕封建迷信、会道门又来了。这当然是误解，但你的语言吓人呀。在旧社会，官僚们不禁封建迷信、会道门，所以中医反而可以生存！再就是：那一套阴阳五行，对青年格格不相入，即便他学会读古汉文，他也领会不了中医的精髓。</p><p>&nbsp;所以当务之急是要搞中医理论的现代阐述，用马克思主义哲学来除去中医理论中的糟粕，用现代科学语言来表述。这样中医理论就可以发放出它固有的光辉，丢掉它现在的吓人的假象。</p><p>&nbsp;为什么一定要讲“子午流注”，不直说“人体昼夜节律”？也因为中医理论还不是现代科学，所以就如写文章还不是现代科学一样，中医临床看病，多少是得靠老师带，以身教为主、言传为辅。中医教学最后要老师带着看病，一年、几年才能学成。（1984年6月2日致黄建平——《钱学森书信选（上卷）》0092页）</p><p>&nbsp;4、科学技术是一个整体，中医不能独立于世界科学技术体系之外；犹如今天的中国不能闭关自守一样。阴阳五行学说如是科学真理，就应能把全部自然科学都概括在内。如不能，那就只有用今天科学技术最高概括的马克思主义哲学来阐述中医了。马克思主义哲学能指导自然科学和社会科学。（1984年12月1日致王敬——《钱学森书信选（上卷）》0127页）</p><p>&nbsp;5、建立唯象中医学</p><p>&nbsp;1、古典中医理论提供了一个以阴阳、五行、干支启发出来的框架，这是一大发明，但我们还只能说是启发，不是结论。到底框架是什么，我们应该实事求是，用中医的临床实践去检验，不合适的地方要修改。最后达到合适的框架，这时唯象的中医学也就出来了。现在我想，中医的名医都实际在根据他自己的临床经验修订了医书上的框架的。</p><p>&nbsp;其实，建立这种根据人体是开放复杂巨系统认识的唯象医学，用的临床经验可不必限于中医，西医的也可以吸收，但不用西医的解释，而用我们的框架。这才是中西结合的新医学。（1989年1月14日致黄建平——《钱学森书信选（上卷）》418页）</p><p>&nbsp;2、医易、阴阳等等也是中医对人体功能状态变化运动的思考方法，也是中医思维的表现。但我认为真正要建立唯象中医学，还有深入下去的工作，即通过运用中医思维学来以现代语言构筑人体这个开放的复杂巨系统的模型，由此进而讲清人体功能状态的变化运动规律。最后建立用现代科学语言表达的唯象中医学。（1989年12月2日致邹伟俊——《钱学森书信选（上卷）》0480页）</p><p>&nbsp;3、发展中医只有这一条路，要用强大的现代科学技术体系来使中医从古代的自然哲学式的、思辨式的论述解脱出来。要换装，变成用现代科学语言表达的唯象中医理论，什么叫唯象理论呢？就是完全从现象来总结、概括，得出系统的理论。也就是说，只讲其当然，现在还讲不出其所以然。根据实践的经验，说明这是怎么回事，把它讲得有条有理，这就叫唯象中医学。（《钱学森等论人体科学》299页）</p><p>&nbsp;4、如何用定性与定量相结合的综合集成法去创建唯象中医学？医学已有几千年历史，医案记录浩如烟海,这都是定性的点滴认识，现在有了新的方法，加上电子自计算机的帮助，综合集成是可以实现的。不但传统医学的医案，而且西医的医案也可以用新观点（开放的复杂巨系统）归纳进来。这就是综合扬弃，创建21世纪的新医学。（1989年12月8日致杨学鹏——《钱学森书信选（上卷）》0484页）</p><p>&nbsp;5、有丰富临床经验的老中医都会根据自己的实践认识对理论框架做局部小调整，但一般不会著书立说，只授其亲近的门生。所以我们不能把中医理论看成是僵化了的死规律，它是在发展中的。</p><p>&nbsp;问题是怎样把老中医的智慧总结起来，汇集成唯象中医学？我想可以用现代科学技术，即：</p><p>&nbsp;（1）用专家系统、人工智能技术把每一位老中医大夫的学问经验记录成电子计算机程序。这项工作在几年前已开始了，只是扩广的问题。</p><p>&nbsp;（2）有了大量不同的老中医指导下制成的各有特长的专家系统，我们就可以将其汇总综合。这也已有具体方法，即我们现在称为从定性到定量综合集成法。</p><p>&nbsp;（3）综合的结果是唯象中医学。新的中医临床经验还可以不断补充进来，所以这样的唯象中医学是不断发展的。（1991年8月14日致邹伟俊——《钱学森书信选（上卷）》0602页）</p><p>&nbsp;6、如中医工程学会能把各家中医的专家系统用中医理论宗合成一个中医计算机辩证施治系统，将是一件大事。这个系统可以在临床实践中不断完善，那就是唯象中医学了。（1992年10月10日致谭高德——《钱学森书信选（下卷）》0700页）</p><p>&nbsp;7、对唯象中医学的研究您主张在中国古代医易的基础上构筑唯象中医学。而我认为中国古代医易的局限性太大，不适用于人体这一开放的复杂巨系统。不久前见到西安西北大学孟凯韬写的阴阳五行学说的数学论证，可见五行说的毛病在于把客观存在的复杂性简单化了。所以我不赞成您的主张，我认为您还没有从家传的中医学解放出来，真正进入现代科学。</p><p>&nbsp;我劝您要大胆创新、实事求是，千万不要把开放的复杂巨系统硬框在阴阳五行之中！阴阳五行不能解决社会科学问题，阴阳五行也不能解决人体科学问题。（1994年2月16日致邹伟俊——《钱学森书信选（下卷）》0886页）</p><p>&nbsp;8、所谓唯象学方法论，即一切从实践出发，先概括出若干经验规律，在进而构筑一个经验规律的体系。所以唯象中医学，即从中医临床经验构筑一个医学理论，也就是知其然而不知其所以然的中医理论体系。这将是人体科学研究的一个非常重要的组成部分。</p><p>&nbsp;在组建唯象中医学的过程中，当然可以而且应该参考医易理论；但易的理论体系太简单、太机械呆板，不能解决开放的复杂巨系统——人体——的全部学问。</p><p>&nbsp;我国老中医的行医理论就是唯象中医学，用了周易的思想方法，但又不拘泥于周易，而是根据经验加以变通。（1994年7月9日致邹伟俊——《钱学森书信选（下卷）》0935页）</p><p>&nbsp;9、古典中医学中的心，看来包括了血系统及脑系统。脑血关系密切，人脑只占全身重量的2%，但用血量却占全身的15%用空气量占吸入的25%。所以经典中医学认为心主神明，又主血脉。在今天我们建立唯象中医学则似应心、脑分为两的角色单元。即在唯象中医学，我们应根据今天我们对人体的认识，从新设计各角色单元，不必一定要从古。（1995年3月23日致邹伟俊——《钱学森书信选（下卷）》1036页）</p><p>&nbsp;10、所谓唯象理论是指一门学问，它是根据大量的实践，概括出来的；它没有深入到事物的深层次结构去找事物的本源。中医不讲人体生理学、不讲细胞等，所以中医理论是唯象（现象）理论。（1996年4月7日致钱文景——《钱学森书信选（下卷）》1134页）</p><p>&nbsp;6、创建人体科学的中医学</p><p>&nbsp;1、中医和医学要走人体科学的路子。（1987年6月25日致李志超——《钱学森书信选（上卷）》0324页）</p><p>&nbsp;2、中医这个宝库似只有用现代科学技术打开后，才能放出前所未有的光明，而这项工作又必须建立在对中医理论的正确理解。（1987年4月18日致徐宝源——《钱学森书信选（上卷）》0312页）</p><p>&nbsp;3、经典的中医学、西医学都有其不足之处。如：中医理论中没有人体人脑的结构和功能介绍，而西医实践中一般误诊率不下于1/3。中西医结合则也不解决问题。最重要的一个问题是人体潜能的发现。（1995年9月12日致吴邦惠——《钱学森书信选（下卷）》1086页）</p><p>&nbsp;4、关于第四医学、超越医学的问题，我看是个科学态度的问题。”“说第一医学就完全把病人看成是反映物，完全被动，这并不是临床医生的看法。吴阶平同志就亲口告诉我，他认为医生要激发病人心理功能，不然治不好病。</p><p>&nbsp;因此我认为我们要科学地对待问题：先规定什么叫常人，要有一系列指标；然后定义第四医学是：培养人明显地超越这些指标的医学。这也就亮出第四医学的旗帜，才能站稳脚。（1990年8月30日致王修璧——《钱学森书信选（上卷）》0532页）</p><p>&nbsp;5、从人体科学的观点，中医有许多比西方医学高明的地方，但将来的医学一定是集中医、西医各民族医学于一炉的新医学。所以请您也注意并收集国内、国外近年来对时间气象医学的研究成果，以发展并深化我传统的时间气象医学。（1990年12月11日致徐振林——《钱学森书信选（上卷）》0553页）</p><p>&nbsp;6、我近来总感到医学，治病的医学，总有一个没有解决的问题：为什么会得病？不是总有人不得病吗？这种病（指“无症状心肌缺血”）既然不是细菌、病毒感染，是人自身的问题，但问题在哪里？</p><p>&nbsp;这是我近年来一直在求答案的难题，医学界好像还没有来得及考虑这个问题。（1993年12月26日致王士雯——《钱学森书信选（下卷）》0863页）</p><p>&nbsp;7、中医与西医要真正结合，扬弃上升为新医学、人体科学的医学，的确很不容易，我曾说过：人体科学是现代科学技术工作中的珠穆朗玛峰！难的原因在于新医学是人体这样一个开放的巨系统的科学，除了中医、西医用的语言概念不同，还要用新的思维方法：从定性到定量综合集成法。对此我能想到的办法就是召集对此有兴趣的中医、像您和对此有兴趣的西医、像吴阶平大夫开讨论会。所以我寄厚望于中国人体科学学会。（1996年2月29日致邹伟俊——《钱学森书信选（下卷）》1126页）</p><p>&nbsp;8、“泛化”思想来源于“象”即意象、形象，也是从整体上认识事物。这一思维方法的优点在于宏观，能避免微观方法的因小失大。我国的中医就是用这个方法，所以我们称之为“唯象中医学”。</p><p>&nbsp;但用马克思主义哲学、辩证唯物主义来看问题，则“小”，微观认识不是可以不管的。也就是您还应该下点功夫学些西医学、生理学，使对人体的认识能落实到物质基础——细胞，以及细胞的内部物理与化学过程。然后把局部与整体、微观与宏观结合起来，即从定性到定量的综合集成。这是对人体这样的开放的复杂巨系统进行研究认识的方法。所以我几年前就向您宣传这个观点，唯象中医学是第一步，下面的任务是把唯象中医学与现代西医学辩证地统一起来，扬弃为更高级的医学，21世纪的医学。（1995年11月19日致邹伟俊——《钱学森书信选（下卷）》1098页）</p><p>&nbsp;9、从巫术到医学是人类历史的普遍发展规律，世界各地都如此，西方也是这样的。在西方，后来科学逐渐进步深化，就成为现在所谓西医学。但近年来又发现西医的一套也遇到困难，所以又找到中国的中医学。这从马克思主义哲学观点来看是一点不奇怪的，是实践中不断深化对客观世界，包括人体的认识。我们要从机械唯物论的西医走到辩证唯物主义的新医学；这是人体科学的任务。（1996年6月16日致陶先刚——《钱学森书信选（下卷）》1152页）■</p><p>\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n</p><p>&nbsp;【来源：《钱学森书信选》等】</p><p><br></p>', '1', '5', '0', '1', '0', '2018-06-29 00:52:53');
INSERT INTO `ys_article` VALUES ('15', '​内养功的临床辩治与气功处方的应用', '27', '推荐,头条', '', '', '', '网络', '刘亚非', '内养功在推广和应用实践中，始终采取”整体调整”和”辩证(病)施功” 相结合的方法,并取得满意的效果。', '<p style=\"text-align: center;\">河北省医疗气功医院　刘亚非</p><p>内养功在推广和应用实践中，始终采取”整体调整”和”辩证(病)施功” 相结合的方法,并取得满意的效果。</p><p>本文仅就内养功在辨治与气功处方的应用方面谈一点体会,和大家分享。</p><p style=\"text-align: center;\"><img src=\"/static/upload/20180629-09-15-47_12742_640.webp (2).jpg\" style=\"max-width:100%;\" class=\"\"><br></p><p>所谓辩证或辨病施功，是指在望、闻、问、切四诊合参和在医学检测诊断的基础上，运用“八纲辨证”、“脏腑经络辩证”等方法，针对患者的症候性质、患病部位的不同，开出具有“个性化特点”的“气功处方”。并指导患者正确掌握和练习，以达到缩短疗程，提高疗效的目的。</p><p>气功处方应具备的内容：</p><p>1、四诊合参，并结合现代医学检测的诊断，了解患者的全面情况。包括患者的性别年龄、工作职业、家庭婚姻、基因遗传、体质赢弱、气色声音、体位神态、病症轻重、病程长短、主症次症、舌象脉象、检测数据、疾病诊断等内容。是开出具有针对性的气功处方的参考依据 \n。</p><p>2、八纲辨证，根据其阴阳、虚实、寒热、表里的症候性质，结合经络脏腑和五行的生克规律，进行综合分析。根据病症的不同，虚证者，以精神内守、吸提固秘的方法而补之；实证者，以按摩疏导、拍打抖甩的方法而泻之；热症者，以吐故纳新、口出鼻入的方法而凉之；寒症者，以吸气闭息、意念红日或炉鼎生火而温之；功能低下者，以高位意守、补阳呼吸法而升之；功能亢奋者，以意守低位、向下导引滋阴呼吸法而降之；散泄者，以聚合内收、凝神注气的方法而固之；郁闭者，以开散融通、快乐拍打的方法而开之、解之。</p><p>3、功法配伍，根据内养功的整个功法系统，选择出适合患者的部分内容进行匹配，形成最佳治疗方案，进行施治和应用。</p><p>功法配伍的主要内容：</p><p>（1）静功：是从内养功初级静功“松静筑基法”的六种练习方法中选择或在中级静功“吐纳停闭调息法”的多种呼吸法中选择适合病症治疗的静功练习方法。</p><p>（2）保健按摩法：是内养功初级动功的内容，与静功的练习方法相配合，其方法比静功的运动幅度大，比动功的运动幅度小，简便易学，易于掌握。针对性强，既可以针对性治疗病症，又可以作为静功练习的预备功和收功练习。</p><p>（3）动功：主要选择内养功的中级动功易筋行气法和行气延寿法的内容，两套功法每套都由十二节组成，整套练习可以起到整体调整的作用，从中选择练习可以起到辨证施功的作用。</p><p>气功处方的配伍应掌握动静结合、练养相兼的原则。这样动静结合，相辅相成；练养相兼，相得益彰，能收到较好的治疗效果。使每个人都能从中获得适合自己身体状况的个性化内养功处方。可以用于保健预防，起到治未病的作用；也可以用于治疗疾病，起到治已病的作用；还可用于大病或手术后的康复以及健康者的养生延寿。</p><p>以下介绍一下具体临床应用。</p><p style=\"text-align: center;\"><img src=\"/static/upload/20180629-09-16-07_10876_640.webp (1).jpg\" style=\"max-width:100%;\" class=\"\"><br></p><p>病例一、闭经</p><p>安格丽卡，女，35岁，自然疗法医生，德国人，奥地利生活，已婚，有一个5岁女孩，闭经一年零八个月，血压105/65mmHg,身高1.59m,体重51公斤，面唇色白，神疲、声低，左腿假肢；失眠，常惊醒，难以入睡，或思虑难以入睡，食欲不佳，强迫自己吃东西以保持体力，大小便尚可，四肢不温，畏寒，时有耳鸣。脉沉细，尺不及，舌淡有齿痕，苔白。</p><p>第一诊处方 2001年6月7日</p><p>患者随德国柏林团队来到北戴河，下晚到达，入住后找到我，要求我看一看她的腿。患者头发散乱，面色苍白，声音低弱，神疲体倦的样子。初见时认为可能旅途劳累，晕车而出现的暂时现象。到诊室卸下假肢后，看到她残肢截面血肉模糊，是我意识到情况不那么简单。欲采取清创处理和必要时的西药处理。但受到患者的拒绝，且态度非常坚决，她说自己就是医生，伤口清理自己每天都做，也采取了很多的治疗方法，包括住院治疗，但两年多来一直没有痊愈。她之所以到北戴河来就是想用气功的方法来解决这个问题。在问诊的过程中，了解到她的不幸遭遇。两年半前，因为一场车祸她失去了丈夫和半条腿，当时受了伤的三岁女儿，由于惊吓而患了自闭症。精神的创伤，身体的残疾，女儿的疾患，使她难以支撑，33岁就闭经了。虽经多种方法治疗，均疗效不佳，特别是恐惧和心灵创伤，始终不能使她从痛苦中摆脱出来。她在德国听了我的内养功课，非常感兴趣，并认定是她要寻找的方法。故经过千辛万苦来到北戴河。根据她的情况，给她开具了第一个气功处方：</p><p>静功：练习1，命门开合闭息整体松静法，以此方法练习十分钟左右后，意守丹田五分钟为一个循环，重复三个循环为一个功时。初练时可由一个循环开始。</p><p>练习2：针对残肢截面特殊部位，采取聚散松静硬呼吸法。</p><p>以上静功的两种练习方法，可以分别单独练习，也可以结合起来练习。</p><p>保健按摩法：1、点按中脘软呼吸法。（强胃健脾）</p><p>2、温肾搓腰。（培补肾元）</p><p>3、和带脉，（调经带，和诸经脉）</p><p>4、开郁拍打法</p><p>动功：坐式练习内养功中级动功易筋行气法。特别是以 卧望星辰（滋补肾阴）、 \n托天按地（强胃健脾）、古树盘根（温补肾阳）、回身射虎（安神定志祛邪除障）为重点练习，每天练功4-6小时。</p><p>患者利用在北戴河学习培训的机会，合理安排集体培训和个人练习功的时间，除了吃饭和睡觉大小便之外，将一切可以利用的时间都用于练功。十一天后，患者主动要求我再看一看她的腿。第二诊时，患者气色红润，精神状态佳，有笑容，声音正常。残肢截面伤口在愈合，嫩嫩的肉芽上覆盖着象江米纸一样薄薄的皮，她让我看的意思就是要告诉我这个好消息。对练功充满信心，并告诉我食欲和睡眠都有所改善，她有一种发自内心的喜悦。</p><p>第二诊处方：在原气功处方的基础上增加动功站式练习。保健按摩法中又增加了“内和带脉”、和“环搓腰胯”并伴随提肛缩会阴等练习。这将对患者有更好的帮助。一个月的学习和治疗，她完成了全部的学习课程，并通过了考试。结业式上她含着热泪说，三年来我从没有像现在这样轻松和快乐。我对生活充满了信心。回国前又给她开具了第三个气功处方，1、嘱之腿的创面痊愈后，可将聚散松静法应用于疼痛部位或病灶部位，对消除疼痛和排除浊气有很好的作用。可享用终生。2、增加内养功中女性练习部分的内容，即女子气功中的“揉乳转肩”、 \n“疏导任脉”、“调和带脉”、及“气运丹田”四节。</p><p>2004年我在德国讲学时，收到了安格利卡的一张照片。她让柏林内养功培训班的组织者之一艾琳可转告我,她从北戴河回国后,很快开始了工作,并重新组建了家庭,在他们的精心照顾和治疗下,患自闭症的女儿也有所好转。抱歉并高兴地告诉我，她之所以不能来参加我的课，是因为有了七个多月的身孕行动不便。还特意强调说“我怀的是一个气功贝比”。希望我以后能有时间到奥地利的萨尔兹堡传授内养功。</p><p>2008年在她的邀请和组织下，我去萨尔兹堡开办了内养功培训，也高兴的看到了她的“气功贝比”，已经4岁多了，是一个聪明漂亮的男孩。转眼十年过去了，这个孩子已经是一个帅气的英俊少年了。安格利卡有一个非常爱她并对家庭很负责任的丈夫，有自闭症的女儿基本康复并已结婚有了一个可爱的女儿，一个幸福的家庭，其乐融融。安格利卡不仅是一个受患者喜爱的自然疗法医生，也是奥地利气功协会的负责人之一。每年邀请我到萨尔兹堡去讲学，我们已经成为了朋友。</p><p>病历二、子宫下垂</p><p>芭芭拉，女，48岁，是医院里指导老年人康复运动的老师。生育了两个男孩。子宫下垂的疾患困扰了她十三年而不能痊愈，医生对她的疾病也感无奈，每三个月一次的检查都很失望。时间长了，她和治疗检查的医生都成了朋友，有时开玩笑说：“不用查了，肯定还是老样子”。腹部的坠胀感、腰部酸痛、白带增多，在医院指导患者康复运动时有些动作完成困难。为此，忧心忡忡。她参加了柏林守中中医学校2012-2014年的内养功师资班，并要求根据她的情况开具一个气功处方。在正常工作的情况下，每天坚持练功两个小时以上。当她再一次进行检查时，医生惊奇的问“芭芭拉你的子宫哪去了”？她告诉医生说“你要向上找，向上找”。通过练功她的子宫完全恢复到了正常位置，子宫脱垂治愈了。医生既为她高兴又感到惊奇，问她采取了什么方法？她讲述了练习内养功的情况，大家都非常感兴趣。现在芭芭拉在她所工作的医院教授内养功，因为有效，很受大家喜爱。气功处方略</p><p>病历三、胃溃疡</p><p>艾廖沙，52岁，自然疗法医师，胃溃疡近3年，2个溃疡面，分别为2.0cm 和2.5cm。经药物等多种治疗疗效不佳，经常上腹部疼痛、嗳气、有压迫感 \n，特别是体重下降造成其很大压力。2014-2016年参加柏林守中中医学校的内养功医师班，学习内养功的辩证应用和开具气功处方时，为他治疗溃疡病开具了气功处方。他在坚持工作的情况下，每天坚持两个小时以上的练习，约6个月后痊愈。经两家医院X线钡餐和内镜检查，溃疡面愈合。</p><p>\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n</p><p>医生治病，解除患者病痛之苦是应尽的责任，本不想示之，但在一些医生和气功老师的要求下，为便于学习和应用，故加以整理和总结，并将陆续的和大家分享，以便更多的人在内养功的练习和应用中获得健康和快乐。</p><p><br></p>', '1', '7', '0', '1', '0', '2018-06-29 01:01:53');
INSERT INTO `ys_article` VALUES ('16', '试论内劲一指禅功法功能特点', '24', '头条,推荐,加粗', '', '内劲一指禅 传统体医结合项目 功法内容 功能特点 功理渊源', '', '网络', '余同元', '内劲一指禅功法动静结合、内外兼修、阴阳平衡、自成体系，既是气功百花园中的绚丽花朵，又是中医宝库中的瑰丽明珠。它不同于一般意守功法，在功法修炼和传承上别开生面，强调顺乎自然，体现自由自在的禅功理念。', '<p style=\"text-align: center;\"><b>苏州大学 &nbsp; 余同元</b></p><p>苏州体育非物质文化遗产项目“内劲一指禅”，亦称“少林内劲一指禅”，是长期流传于江南民间的优秀传统体育项目，是吴门医派动养卫生功法的杰出代表。其功法动静结合、内外兼修、阴阳平衡、自成体系，既是气功百花园中的绚丽花朵，又是中医宝库中的瑰丽明珠。笔者有幸跟随内劲一指禅文化遗产项目传承人阙巧生先生习练站桩功法，得以拜读阙先生大作《内劲一指禅》最新修订本（人民体育出版社 \n2018 \n年初出版）。</p><p>阙先生自幼随父习功，继承父业，成为内劲一指禅第十九代掌门人。他坚持弘扬内劲一指禅功法，长期主持内劲一指禅健身咨询门诊，擅长治疗各种疑难疾病，经其治疗康复者众多，常常应邀到各地讲学、传功和调病。&nbsp;</p><p>&nbsp;<b>1 \n内劲一指禅功法特点</b></p><p>内劲一指禅主要有三套功法构成。</p><p>第一套功法：由五个桩架组成，即罗汉推掌、掌心开合、罗汉出掌、罗汉戏球、手指扳动功法（1）等。既可以从始至终一气呵成，也可以选择其中一个桩架在马步站桩 \n30 \n分钟后锻炼，一个月后再换桩架。</p><p>第二套功法：包括弓步桩、虚步桩、丁字托塔、一字桩、童子拜佛以及手指扳动功法（2）等。此套功法增加了难度与强度，在经络开合、内劲蓄积、功力增长上更具特色。考究的桩架与程序，使人在练功时<span style=\"display: inline !important;\">进入身心最佳状态。</span></p><p><span style=\"display: inline !important;\">第三套功法：包括罗汉弓步推掌、虎形桩、蛇形桩、龙形桩、童子拜观音、顶天立地、和手指扳动功法（3）以及禅坐等。这套功法难度加大，其功力向更高层次发展。无论从足、腿、腰功的坚实程度，还是从内劲蓄积等方面，第三套功法都进入一个新阶段。这套功法使人足实、腿健、腰固、内劲充实、取用有余，因而精力更加充沛。</span></p><p><span style=\"display: inline !important;\">内劲一指禅奠基于一套特有的站桩功——即各种站桩功法。这些桩法以站立式为主，使躯干四肢保持一定姿势，而肌肉呈现出持续的静力性紧张状态，形成绝对运动与相对运动结合。内劲一指禅的静力性运动，既能促进血液循环，增强各个系统的新陈代谢，又能使中枢神经得到充分休息，以致思想集中，全身润泽，生机旺盛。所以通过桩功训练达到意识自然集中、呼吸自由顺达、气力生发自在，然后以心定力，以意运气，以气催力，最终形成以内气为核心动力，以神经、内脏、骨骼、肌肉、筋腱、气血灵敏协调运动为基础，以四肢躯干为整体运用形式，以意、气、力内劲融合为手段，在不断的功法套路训练中发挥内在能量。</span></p><p><span style=\"display: inline !important;\">历经发展演变，内劲一指禅功法传承基本要求如下：</span></p><p><span style=\"display: inline !important;\">第一，强调桩架的正确性、到位性和引发性，务求虚实结合、动静结合、松紧结合。桩架姿势是内劲一指禅练功中的第一关，目的是由此打开关窍、疏通经络。桩架的正确或错误，直接影响人体的血液循环和新陈代谢以及神经系统的种种变化。练功时，要分辨姿势与动作的起落、神态、用意、高低、轻重和虚实；要记清举动与呼吸的部位、手法、方向、次数、长度和深度。举动要灵活自然，不僵硬，不呆板。务求动静结合，有动必有静，有静必有动；静是动的基础，动是静的表现。桩架动作的引发性，包括经穴的开启与闭合，当经穴闭合时，气血运行舒缓；一经开启，气血便会活跃、旺盛。练功中经穴开合引发内气流转，使人产生舒适感。有节律的肢体屈伸、仰俯、转动，可以促进全身经穴开合和气血流畅。</span></p><p><span style=\"display: inline !important;\">第二，强调手指扳动的关键性、程序性和效果性。其要旨在于，按顺序要求正确扳动十个手指，借此推动内气加速运行，不断达到平衡阴阳，提高内劲。具体说来，手指足趾扳动又分“先阴后阳法”与“先阳后阴法”两步进行。“先阴后阳法”扳动手指同时按动足趾，其顺序是先扳动手太阴肺经的大拇指、手厥阴心包经的中指、手少阴心经的小指，然后再扳动手阳明大肠经的食指和手少阳三焦经的无名指；“先阳后阴法”扳动手指同时按动足趾，其顺序是先扳动手阳明大肠经的食指和手少阳三焦经的无名指，然后再扳动手太阴肺经的大拇指、手少阴心经的小指和手厥阴心包经的中指。</span></p><p><span style=\"display: inline !important;\">第三，内劲生发就是调动内脏潜力，不强调意念导引，也不强求意守某一体位。固执意守则容易打乱内气运行规律，而不强调意守就可以保证内气自由升降出入。正所谓“归根得旨，随照失宗；须臾返照，胜却前空”。在人体生理活动中，形体属阴主静，真气属阳主动，气离形则无所依附，形无气则成为死物。阴阳互根，动静相育，形气并存，才有一派生机。&nbsp;</span></p><p><span style=\"display: inline !important;\"><b>&nbsp;2 \n内劲一指禅功能特点</b></span></p><p><span style=\"display: inline !important;\">内劲一指禅不同于一般意守功法，在功法修炼和传承上别开生面，强调顺乎自然，体现“一无所守，心住虚空”的禅功理念。其严谨的结构，考究的桩架，分明的层次，系统的程序，显示出功法的系统、完整、安全、实效且简易的性格风貌。所谓“内劲”，指人体活动的内部能量。这种能量既是蕴藏在人体内部的某种潜力，也是指人的体内气力，亦称“内气”。它通过由浅入深且相互贯通的桩架训练，使练功者自然产生内气。这种内气与自然界的清气融为一体，构成循环之“内劲”。这种内劲可以通过功法训练激发出来，提高生命质量。内劲训练在禅门各大功法流派中都有自己的练习形式与过程，但一般都从站桩开始。科学实验表明，功力随练功时间的积累而日趋深厚，练功者体内潜力激发与功法练习质量成正比。只有坚持练习，达到外气内收或内气外放，才能蓄积内气内劲，强体祛病，健康长寿。所谓“一指禅”，即扳指运动，为手指扳动的指法特称。就是按顺序、有规律、按时间、有节奏地扳动手指，且同时按动十足趾，充分调动十二经脉的内气运行，促进经络疏通，使体内阴阳达到平衡。这套练习以手指缓慢升降的运动为中介，通过手指训练而达到激发身体潜能之目的，独具特色且具关键意义。</span></p><p><span style=\"display: inline !important;\">其中“禅”，原指安定止息排除杂念之意，但在内劲一指禅练习过程中，不强调意守和入静，不固执常理，而以源自自身的内气内劲升发，作为自我强健的方便法门。禅定功法树大根深，生生不息，原本是一种原态生命力的生发。“止动归止，止更弥动”；“莫逐有缘，勿住空忍”，内劲一指禅的“禅”，既是一种安定训练环境中的“锻炼状态”，又是一种自然而然的气劲“生发状态”。</span></p><p><span style=\"display: inline !important;\">处于这种“生发状态”中的练习者，其意念活动、呼吸运动、以及血液循环等，都以自然状态为主导 \n, 动静结合，阴阳平衡，遵循人体气血运行规律，自动产生大脑皮质抑制作用，调动脏腑运行潜力（即“内劲”）, \n再由内劲产生真气并培植元气。如《庄子》所说：“至人用心若镜，不将不迎，应而不藏。” \n真气即真元之气，由先天元气和后天元气结合而成。其从足下产生，走行十二经脉，打通奇经八脉，气机进入血脉，深入脏腑，注入筋骨，形成内外气循环。依中医理论，它是维持人体生命活动最基本的物质，人之有生，全赖此气。真气清静平淡，自然而然，不断补益脑髓，使大脑能量增强，内分泌协调而旺盛。功夫越深，对生理机能调节越好。所以传统中医视真气为“性命双修”之气，正如《素问》所云：“恬惔虚无，真气从之；精神内守，病安从来？”</span></p><p><span style=\"display: inline !important;\">内劲自然生发、轻松简明、老少皆宜，在众多动养保健功法中最具大众性、便捷性和实用性。人体各个部位在大脑皮层里都有相应的机能定位，手指足趾首先就是大脑皮层的感受器。从经络学角度讲，人的手指联系着三阴三阳六条经脉；同样，人的足趾也联系着三阴三阳六条经脉。每条经脉都有井、荣、输、经、合五种经穴，脉气正是从这里内行归合到脏腑。人的手指既是手三阳经脉的起点又是手三阴经脉的终点；人的足趾既是足三阴经脉的终点又是足三阳经脉的起点。由此可见内劲一指禅手指扳动功法的生理原理和按序练习的科学根据。正所谓“能随境灭，境逐能沉；境由能境，能由境能”是也。按序扳动某一手指，就会引起相应经络启动，可以充实、聚积内气，促进气血运行，联动脏腑，营养全身。凡是经过刻苦练功者，内气充足，气满督自开。此时扳动一手指同时按动一足趾，犹如按动生命开关，内劲活力自然生发。&nbsp;</span></p><p><span style=\"display: inline !important;\"><b>&nbsp;3 \n内劲一指禅功理渊源</b></span></p><p><span style=\"display: inline !important;\">内劲一指禅原由菩提达摩首创于嵩山少林寺，后传至南少林薪火不断，再传至江南吴地发展完善。苏州渭塘阙阿水（1918—1982）先生是内劲一指禅第十八代传人。以此向前推算，内劲一指禅功法形成时间，大约在中国传统养生文化高度发展的明代中后期。</span></p><p><span style=\"display: inline !important;\">明代中期到清代中期，江南社会经济发达，伴随养生保健市场的繁荣，先后出现了罗洪先的《万寿仙书》、周履靖的《赤风髓》、胡文焕的《养生保养法》、紫凝道人《易筋经》（后演为《少林易筋经》《练功图说》等）、尤乘的《寿世青编》、颜伟的《延年九转法》等养生专著。特别是吴门医派对传统中医理论进行发挥，结合明清之际传入的西方生命科学，探讨生命本源，创新气化生命科学，提出了“先后天元气互本论”“元质元神论”“血气互化论”“卫气营血论”及“体质类型论”等新生命学说，从而构建出一个完备的生命本体论及生命发展论体系，形成了有自身特色的动养静养中医理论技术体系。正是在这样的环境熏染下，内劲一指禅成为一门独特的动养保健功法。</span></p><p><span style=\"display: inline !important;\">内劲一指禅“内劲生发”与“培植元气”的认识，既与吴门医派“元气存亡论”及“卫气营血论”彼此互应，又有自己的返本开新和推陈出新精神。吴门医派徐大椿《元气存亡论》曾指出，元气就是真气。气在阳部即阳气，气在阴部即阴气，气在表层就是卫气，气在里层就是营气；在脾脏的气是充气，在胃部的气为胃气，在上焦的气是上气，在中焦的气是中气，在下焦的气是元阴和元阳之气。这些不同名称的气都是 \n“元气”的各种变化，这些变化的出现，是因为“元气” \n运行时途经人体不同部位和不同脏腑，所到之处都会加强这些部位与脏腑的运营力量。吴门医派叶天士认为，生命在不同阶段有不同主宰，先天元气无形无质，在胎儿时已经成形；人身成形后，以后天脾胃气血为本，后天可助先天。在此基础上，内劲一指禅认为，静力性运动是对脏腑的有效锻炼，使淋巴细胞增殖，产生免疫作用；练功者把身体重心放在涌泉穴上，促使足心毛细血管开放、充血代谢；站桩时间越长，毛细血管开放越多，下肢以及腹腔静脉血回流心脏加快，循环系统功能加强；同时由于副交感神经相对兴奋，脏腑循环系统运转加快，身体发热出汗，唾液分泌增多，血管容积增大，细胞电泳增高。经穴是经络、脏腑气血输注的地方，其开启与闭合运动原理，正应验了吴门医派气血在经脉中运行的盛衰变化理论。</span></p><p><span style=\"display: inline !important;\">内劲（内气）养生护生的长期实践证明，“站桩”“扳指”协调呼吸、消化、内分泌以及循环系统运营，使各个系统在神经系统支配下达到和谐，使循环系统参与营养和氧气吸收，推动代谢物的运输和排泄。作为中华民族的优秀体育文化遗产，内劲一指禅具有文化传承创新与健康健美长寿追求的双重价值。该功法秉承了中国传统的阴阳学说和气血学说，体现了中国传统生命哲学和生命科学的文化特色。它与儒家心性修养观、佛教调息调身理念和道教吐纳导引法都是一脉相承的关系，同时又融汇吴门医派的中医生命观，在传承中形成了自身独特的功理和功法。它通过合理的肢体动作编排，结合动静交替的训练过程，从而调动人体内气和内劲。它动作柔和、速度平缓，且结合手足指交替升降的扳指运动，来协调内脏器官活动，促进人体微循环扩张，加强血液流畅，改善身体内部循环。由此可见，通过内劲一指禅锻炼强化气血，使脏腑功能健壮和身体免疫力提高，从而促进阴阳平衡，必能获得健康健美长寿之效果。同时，内劲一指禅修炼中规定有明确的道德规范守则，强调身心双修，以良心善意为内气和内劲之源头活水，要求功法练习与诚实善良的心理修养相统一，这些无不渗透着中华民族优秀道德和生命信仰。</span></p><p><span style=\"display: inline !important;\">参考文献： \n[1] 阙巧生 , 著 . 内劲一指禅 [M]. 北京 : 人民体育出版社 ,2018. [2] 阙巧生 , 杨春和 , 张国平 , 编著 . 罗汉神功 \n[M]. 长春 : 长春出版社 1990:1-21. [3] 禅宗三祖僧璨《信心铭》, 引自宋僧道原《景德传灯录》卷三 , \n中国藏学出版社净慧主编《中国灯录全书》1993 年版 .</span></p><p><span style=\"display: inline !important;\"></span></p><p>作者简介：余同元 (1962~), 男 , 博士 , 教授。研究方向 : 民族传统体育学。</p><p><br></p>', '1', '7', '0', '1', '0', '2018-06-29 01:25:40');
INSERT INTO `ys_article` VALUES ('17', '任法融道长，道家思想与量子纠缠，所谓的“迷信”其实是尖端科学！', '10', '推荐', '', '', '', '网络', 'admin', '道家思想与量子纠缠', '<p>一</p><p>量子就是宇宙之间运化万物的唯一能量。每一个事物都有能量，运化在冥冥之中，它形迹微弱，摸不着、看不着。佛家讲的是“不垢不净，不生不灭，不增不减”；老子讲的是“视之不见，听之不闻，搏之不得”。当今科学界把它说为“暗物质”。暗物质就是看不着的意思，但实际存在。暗物质，这就是能量子物流。始终与事物之间有关系，相互牵扯，纠缠。自然界的能量，现在秋天，植物叶落归根萎缩。</p><p>这就是量子冥冥之中造化，不由自己。到了这个春暖以后，就要生旺、茁壮、到六月就是繁荣茂盛，就是自然界的能量促使着它的春生夏长秋生冬藏。人间社会也是一样，量子的造化促使着每一个人、每一个事物的发展变化。有起始，有发源、有兴旺、也有衰落。每一个动机都是量子，你的动机如果善良，你的结果就是福德；你的动机如果邪恶，你的结果就是祸殃。就是你本身的行一动，自然的量子功能就把你拉进程序里面，你就逃不脱了。</p><p>大到一个宇宙，小到一个人。人间社会的成败、兴衰、吉凶、祸福，都在量子功能里面运化的，“积善之家，必有余庆，积不善之家，必有余殃。”虽然自然世界这么复杂，但是这个定律是物极必反，所以正道最后都是胜利的，扬善的最后都是得到福果的，邪气的最后都是要败的，做恶事最后要付出代价的。这是规律一个，任何事情，虽然很复杂但是很简单的一个，大道至简的。都是自然界的量子造化的，虽然仪器观察不到的，但是人们已经认可了量子的存在。</p><p>二</p><p>《道德经》“道之为物，惟恍惟惚。惚兮恍兮，其中有象；恍兮惚兮，其中有物。窈兮冥兮，其中有精，其精甚真，其中有信。”“其中有精”就是量子，现在是科学认可的，是正确的，原来把这打到迷信里面，是错误的观点。</p><p>现在科技越发展就越认可《道德经》，“道德”不仅是个理论，理从情上现，文从纸上来，“道德”这个文化理念在情和知上的这个功能，功能就是现在称为量子。量子的意思就是有能量，但又是很小很小的东西，人用眼光去看，这是个方的圆的，这是错误的观点。老子说“大象无形”，大象就是没有形象，有形象就小了。“夫唯大，故似不肖。若肖，久矣其细也夫！”大象无形，这就是阐明那个量子的。指的是很小很小的意思，不容易看见。这现在看起来是，这几年来规律科学家才慢慢被认可的。</p><p>三</p><p>科学界说宇宙起源，又是什么大爆炸理论，都是复杂化了，其实大道至简，“一画开天”就说清楚了，就这么简单。中华民族的文化了不得啊，老子讲的“有无相生，难易相成，”中国人还没有弄清楚，18世纪德国的大哲学家一看《道德经》说了不得，老子讲的这个东西是尖端科学。一部《周易》就讲的“阴阳”问题， \n“阴阳”到底是个什么？很抽象。但是到现在计算机的原理还是脱离不了二进制，就是 \n《周易》讲的“阴阳”，老子讲的“有无相生”，二进制就是“一画开天”，一分为二。“有无相生”，就产生了无序数据。</p><p>植物从生长茂盛，到枝枯叶落，凋零归根；动物生老病死，人也是个动物，人也生老病死；时局的成败兴衰；处境的吉凶祸福，都在冥冥之中量子的功能造化中。这就是我修行多少年才感觉的，老子讲的冥冥之中在造化，“视而不见，听之不闻，搏之不得。迎之不见其首，随之不见其后” \n。一切事物都脱离不开量子的运化。儒家的思想也是阐述这些道理，道家阐述的只是比较高明些，佛家也是阐述这些道理，舍利子说的就是这个“道”的含量，它是一个宇宙的能量，能量就叫量子，虽然量子是很小很小的，但是任何事物都脱离不开它的。</p><p>四</p><p>人的心灵感应，比手机还强得多，更先进。比如母子连心，孩子出事了，妈妈就能感觉不对劲，感觉到孩子有事情。这种感觉是个什么东西？现在科学界就要研究这个心灵感应，这是个波，人的一行一动都有这个波，这个波也是个物质的，不是个唯心的，这个波快得很，比光速还快。这就是量子，人就是量子的一个发波器。你有焦虑的事，微波动了，你晚上可能就睡不着了。人在做事前，就先有“信息”，所以祸和福，好和坏都是提前决定了的。</p><p>不但人在做事前如此，大自然也一样。比如大的地震，大旱大涝，提前就有信息，现在咱们只是接收不上它。但是动物知道，为什么呢？动物吃饱了啥都不管，人不行啊，人还要害人呢，七情六欲太多，灵台都塞实了，信息就接收不上嘛，号码乱着呢。2004年印尼海啸严重得很，我在香港，香港人也很害怕，问我说咱们这儿会不会有事啊？我说任何事情都是有时间、空间的，时间就在冬至前后，现在过了；空间就在西南方向，而不会往东南方向来，不要害怕。</p><p>五</p><p>一个老板给我讲了个事，他有个朋友在英国，老两口冬天带着两个孙子到印尼旅游，酒店半夜有蟑螂，他们说这么高档的酒店怎么有蟑螂，酒店经理说半夜都住满了，不能换房子，把卫生重新打扫一下是可以的。他们说不行，把孩子叫起来，下楼走了。他们走了之后，海啸啪一下把楼掀倒了。为什么蟑螂都能知道海啸来了要跑了，人不知道？我说人是七情六欲塞实着，爱钱怕死没瞌睡，都想升官发财嘛，没有一点空余的地方。</p><p>人终身役役而疲劳不堪，咋能知道天机的造化嘛。和宇宙的密码、宇宙的信息、宇宙的能量都接触不上嘛。人到心灰意冷彻底绝望的时候量子功能才会出现，才能和宇宙那个密码、宇宙的能量结合。结合了就有无限的能量出现了。比如窦娥遭受无辜陷害，被受贿的贪官判处死刑，在临斩之前她发出誓愿：她的鲜血要溅在刑场的白绫上，六月天飘起漫天大雪，当地亢旱三年。后来真的就把天叫应了。这说明人的量子在万念俱灰，心灰意冷，绝望之时，啥都不想了，量子就出现了。“观自在菩萨”，咱们现在就不自在么！菩萨不在，为什么不在？七情六欲太多了！功名利禄太多了！</p><p>六</p><p>量子就是宇宙之间运化万物的唯一能量，人也有巨大的能量，人的能量出来以后，打仗可以挡千军万马。“道”有多大能量，人就有多大能量，人生随想安和天地啊！所以现在国际上也好，中国也好，科技界现在慢慢认可了量子纠缠，现在“道”文化要和高科技接轨，不接轨就没有生命力。</p><p>\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n</p><p>诺贝尔奖巴黎宣言指出：“寻找人类的文明，寻找高科技发展，还要寻到两千六百年前的老子去。”《道德经》五千多字，面面俱到，不看不知道，一看真是了不得，那个时候的人和现在社会不一样，条件不一样，智慧能达到那程度真是了不得。</p><p><br></p>', '1', '4', '0', '1', '0', '2018-06-29 01:45:26');
INSERT INTO `ys_article` VALUES ('18', '四位院士力挺中医：中医解决了很多西医解决不了的问题，不能把老祖宗的东西丢了！', '2', '推荐', '', '', '', '网络', '占豪', '四位院士力挺中医', '<p>几年前，国外抢注中医古方的新闻曾引起不少热议，2001年，日本一家公司向美国申请了治疗溃疡性结肠炎的专利，明确对以芍药为活性成分的包括加味逍遥散、当归芍药汤、芍药甘草汤、桂枝茯苓丸4个复方进行保护，并且获得了授权。日本还无偿商业化开发中国《伤寒杂病论》、《金匮要略方》中的210个古方。在这些古方被别人逐渐申请专利、针灸被很多西方国家接受的同时，国内很多人还在纠结中医是不是真的管用？根据公开报道和演讲，国内四位现代医学大家力挺中医！”</p><p><b>中国科学院院士 韩启德（分子药理与心血管专家）</b></p><p><b>中国工程院院士 钟南山（呼吸内科专家）</b></p><p><b>中国工程院院士 汤钊猷（肿瘤专家）</b></p><p><b>中国工程院院士 樊代明（消化外科专家）</b></p><p style=\"text-align: center;\"><img src=\"/static/upload/20180629-09-56-26_16206_1530237310(1)副本.jpg\" style=\"max-width:100%;\" class=\"\"></p><p><b>韩启德院士：中医不科学不代表不正确</b></p><p>我不太同意中医是科学。中医是人的艺术，是我们要大力推崇的，中医能看好病，无可非议；中医要大力推广，要继承发扬，毫无问题。中医凭感觉、凭经验，但是说有没有经过实证？有。经过两三千年绵延不绝，中华民族繁荣昌盛，大量的病都看好了，这是事实。</p><p>我在农村当医生的时候，也学了中医，我给人看病，学了九个月就用中医，后来找我看中医的人不比找我看西医的人少。中医是好的，但不一定是科学的。科学并不在于正确，不科学不说明它不正确，不好。</p><p>我们对科学要有正确的理解，不要把科学跟绝对正确联系起来。科学只是我们人类文明发展到公元1500年以后，在这几百年里面，一部分地球人所认定的一种体系。而中医是我们中华民族几千年来所认定的体系，为什么一定要把两个体系去完全等同起来呢？我们应该有这个自信，也应该吸收互相之间的长处。</p><p>要学习中医的接诊之道</p><p>现在的西医几乎已经不碰病人了，裘法祖先生在世的时候提到一个故事，说一个病人肚子疼，找到他，他让到检查床摸了一下他肚子，这个病人就感动得掉眼泪，说你是我看的第六个医生，前面五个医生都没有碰过我一下肚子。我们西医已经到了这个地步！完全靠设备，完全靠人为确定的指标来解决问题。尽管有它很好的能解决问题的地方，但是确实离开病人越来越远，这就容易造成矛盾。</p><p>不说别的因素，中医来说首先要把脉，医生的手搭到了病人的手，如果再加上一个笑容，病人的感觉跟我等了几个小时，看到医生头都不抬，化验单一开检查去，感觉很不一样。</p><p>我看中医的体会就是中医容易跟病人搞好关系。西医来问你怎么不舒服？我也不知道怎么不舒服，就是难受。西医看所有检查都没有问题，然后说你没病，病人不生气吗？我要你干吗？我就是难受，你说我没病？中医不会这样，我当中医，病人刚走进来我就知道他大概是什么问题，然后坐下来，在农村的时候，妇女坐下来给你一个胳膊，什么也不说，我就知道她要我把脉是不是怀孕了。</p><p>中西医不要互相排斥</p><p>西医因为是现代科学，确实解决了很多问题，跟一百多年前比解决了很多很多的问题。但是，一方面离解决人类所有的疾病，预防、治疗还差得很远。另一方面，整个现代医学有点忘记了医学的最根本是人，我们开始把人当作机器，当作局部，忘了人是会思想的，什么东西都看片子，看化验，所以现代技术的发展使我们开始远离人文，这是非常危险的倾向。</p><p>中国人应该有这个志气，得天独厚的是我们有中医这个宝库，尽管它很不完善，需要有现代科学不断使它更好的发展，但是就现代医学方面，我们跟现代西方国家有什么差别呢？我们有自己独特的东西为什么不能应用呢？</p><p>我们中国人应该有志气，把中医和西医里面最优秀的成份给它结合起来，创造出真正的现代医学，什么东西都要往前发展，如果不发展，不管是现代医学还是中医都是没有出路的，都要往前发展。</p><p style=\"text-align: center;\"><img src=\"/static/upload/20180629-09-58-42_14285_1530237497(1)副本.jpg\" style=\"max-width:100%;\"><br></p><p><b>樊代明院士：中医比肩现代医学且不可替代</b></p><p>中医不用“挺”，它自己“挺”了几千年，需要我们好好去学。学中医不是否定西医；就像说西医好，一定不要随便说中医不好。对此，我有四句话：</p><p>一是在人类历史上，中医药学从未像今天这样受到强调和尊重；</p><p>二是在世界医学领域中，中医药学已发展成唯一可与现代医学（西医药学）比肩的第二大医学体系；</p><p>三是中医药解决了很多西医解决不了的问题，显示其不可替代性；</p><p>四是中医药学必然成为未来医学发展和整合医学时代的主要贡献者。</p><p>第一句话，屠呦呦研究员获2015年诺贝尔医学或生理学奖，是当今国际主流医学界对中医药学价值的认可，这种认可程度前所未有！</p><p>第二句话。在人类文明发展史上，各种医学不断产生又不断消亡，唯有中医药学有完整的理论基础与临床体系，历经风雨不倒，不断发展完善，为中华民族繁衍壮大做出巨大贡献。即使在西医占主导地位的当下，中医药依然以其显著疗效和独特魅力，在越来越多国家掀起了经久不息的“中医热”。</p><p>甚至在有的领域，中医药学远远走在了现代医学的前面。比如，对于顽固性腹泻，西医一直没有什么有效手段，直到近几年在国外兴起的用肠菌移植治疗法，才明显提升了疗效。而在几千年前的中国医学典籍如《肘后方》《黄帝内经》，甚至更早时期，即有记载“口服胎粪”等类似疗法。</p><p>再如，现代医学认为，人的生命力中午12点最强，夜里12点最弱。我年轻时当住院医生值夜班时就发现，半夜去世的病人最多。这不就是国际上已经认可的我们中医的“子午流注”么？</p><p>再看第三句话，临床中这方面例子不胜枚举。比如，西医急腹症手术能解决急症救人性命，但术后肠胀气有时很难解决，严重影响手术效果。针灸就能搞定！当年尼克松访华，有一位美国记者来华打前站，不巧突发阑尾炎，在协和医院做了手术。手术本身很成功，但术后肠胀气解决不了，后来是针灸给解决的。这位记者回国后写了篇报道反响很大，直接推动了针灸进入美国等很多西方国家。</p><p>第四句话，中医药学必然成为未来医学发展和整合医学时代的主要贡献者。现代医学发展之路有些走偏了，离“科学”越来越近，离“病人”越来越远；医学研究越来越纠结于微观，离整体越来越远。现代医学需要向中医药学学习，来帮助自己“纠偏”。在此基础上，两者整合可以形成一个从整体出发、重点关注“人”的、真正有效保证人类健康的新的医学体系。</p><p>我们西医不能也不应该看不起中医！至于有些既不太懂科学、又不太懂医学的议论，不要太在意。有人说真理越辩越明，我看还要以实践说话、疗效说话！</p><p style=\"text-align: center;\"><img src=\"/static/upload/20180629-10-00-46_11589_1530237666(1)副本.jpg\" style=\"max-width:100%;\"><br></p><p><b>钟南山院士：中医药可贵之处在于治未病</b></p><p>中医药最可贵之处是“治未病”，调节改善全身的功能状态，最突出的是注重整体的理念，广东的国医大师邓铁涛是我的老朋友，他100岁了，每天还在打八段锦。中医保健是很可取的。</p><p>不过中医保健要多做临床观察，举个例子，不少人喜欢广东新会的陈皮茶，认为喝这种茶有助于将吸入体内的灰霾排出，它是否真的有用？我们最近在做动物实验，观察它们服食前后气道反应和分泌物的情况，从而进行对比。这样的临床观察既有趣，也能促进药物研发。</p><p>现在越来越多的西医开始临床使用中医药，相信这是一个有效的治疗方法，在某些方面，可能比西医要好。</p><p>中医药在呼吸系统疾病中的作用体现在两方面，一个是慢性阻塞性疾病，如气道阻塞、哮喘病，一个是病毒感染性疾病。对慢性气道阻塞，有不少传统的很好的中药，即使到现在，许多西药提取基本上来自两个中药，其中一个是麻黄，另外一个是曼陀罗，也叫洋金花，目前已发展出很多化合药物。</p><p>我们在研究中发现，中药单方也好，复方也好，像板蓝根、连花清瘟胶囊，作用不是单纯的抗病毒。中药可以预防病毒进入体内，进入细胞内，如果病毒进入细胞内，中药可以预防它繁殖。中药跟西药的抗病毒概念不一样，中药还有一个全身性调节作用，这是中药的特色和长处。现在有比较多的西医相信中药，因为中医药研究在方法、技术和思维上，更多地采用了现代科研方法。</p><p>社会上常有所谓的灵丹妙药，打着中医药的幌子，骗了不少人，对中医药声誉是严重损害。同时，也要看到中医药研究在一些方面仍缺少现代科研方法支持。</p><p style=\"text-align: center;\"><img src=\"/static/upload/20180629-10-02-19_14300_1530237759(1)副本.jpg\" style=\"max-width:100%;\"><br></p><p><b>汤钊猷院士：西医临床40年，一直同步用中医</b><br></p><p>中医学从大量实践出发，不断修正，经历了千百年考验，是祖先留给我们后人的宝贵财富和深刻启示。作为一名西医，我在40多年的临床诊疗生涯中，一直在使用中医，并不断总结着应用中的得失，如使用攻补兼施的“消积软坚方”和六味地黄丸补法等。</p><p>中医西医就像一枚硬币的两面是可以互补的。中西医结合治疗癌症，前景非常值得期待。在治癌方面，中医有不少思路已经被西医所证实，比如“清热解毒”，现代医学越来越多的证据提示癌症与炎症有着密切关系，而且已证明抗炎药（如阿司匹林等）有助治癌。</p><p>我一直在思考有没有可能建立 \n“中国特色的医学”。过去我出国，还有癌症的早期发现、早期诊断可以说说，如果没有这些，在国际会议上我们就没有多少话语权了。我们说的搞医学规范，这规范是谁定的？都是外国人。有没有我们自己的东西加进去？基本没有。但是我们中国的东西，比如说中医，它是不符合西方“规范”的，但实践证明，中西医结合对治疗癌症确实是有效的。</p><p>\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n</p><p>要想提高，就得出奇招。我觉得，这就是用辩证的眼光来看规范。规范只是相对一个时期内有用的，而变化、发展才是绝对的。毛泽东早在上世纪50年代就提出“中西医结合，创立我国新医学派”，我觉得这是我国医学发展值得长远思考的问题和方向。</p><p><br></p>', '1', '3', '0', '1', '0', '2018-06-29 01:47:56');
INSERT INTO `ys_article` VALUES ('19', '积极推进气学说的科学研究——庞鹤鸣教授在“气—意识与人体生命科学国际研讨会”发表主旨演讲', '11', '推荐,加粗', '', '', '/static/upload/20180629-11-49-28_16213_1530244059(1)副本.jpg', '江西中医药大学气功科学研究所', '', '江西中医药大学举办了为期两天的“气—意识与人体生命科学”国际学术研讨会。', '<p>2018年6月16日—17日，江西中医药大学举办了为期两天的“气—意识与人体生命科学”国际学术研讨会。</p><p>17日下午，江西中医药大学特聘教授庞鹤鸣教授来到图书馆报告厅，进行了题为《积极推进气学说的科学研究》的主旨演讲。</p><p>首先，庞鹤鸣教授对本次会议的主题——“气-意识与人体生命科学”给予了高度的肯定，它深刻的揭示了人体生命的本质，创新性的阐述了传统气学说与现代意识科学与人体生命科学之间关系，为实现中国传统气学说与现代科学的融合开辟了新途径，为人体生命科学的发展打开了新境界。</p><p align=\"center\"><img src=\"/static/upload/20180629-11-47-30_14255_1530244059(1).png\" style=\"max-width:100%;\"><br></p><p>接着，庞教授讲到中医气学说的应用。中国传统文化包括知识和功夫体系，中医气学说的发展既要有中医学知识和科学知识，还要有临床实践经验和体察气的功夫。回顾古代医家中医之路，中医气学说的精华在诊断和治疗上体现的最为明显，如《黄帝内经·素问》里讲到守形和守神。守形：病在人体里真实情况是不清的，我们需要用气作为根本，来探索疾病。守神：望而知之谓之神。气学说中的气分为有形之气和无形之气，无形之气中的元气，是宇宙的根本。意识可以调动内气外放，外气内收。当意识与某一种气相结合则气就在哪个层面发生作用。疾病可以表现为气机的不流畅和壅滞，故气充足、气通畅、功能恢复正常是疾病治愈的关键所在。</p><p>庞教授还指出我们运用中医气学来学习锻炼，要体察人体之气（躯体混元气、脏真混元气和意元体），满心而发，诚心学习中医气学说，时刻关注我们做每件事的初心，成功而不骄，失败而不馁。</p><p align=\"center\"><img src=\"/static/upload/20180629-11-47-46_16873_1530244032(1).png\" style=\"max-width:100%;\"><br></p><p>在验证“三层物质理论假说”的科研方面，庞教授强调了三点：</p><p>一是把验证三层物质理论的基础实验做得更加扎实、广泛。</p><p>二是不失时机地抓各学科、各领域的应用研究，比如医学上的应用；教育上的速读、速记的应用；以及在工、农、林、牧、渔生产中的应用，这就必须与有关学科的专业人员进行合作，共同研究。这些研究仍应遵循“确定现象，摸索规律、探讨应用、建立理论（各科应用的理论）”的原则大胆谨慎的进行。这是又一个回合的从实践 \n→理论，须知“三层物质理论”是一崭新领域，只有多次实践 →理论反复深入研究，才能展现其伟大生命力。</p><p>三是抓紧对“三层物质理论”科研人才的培养与发掘。</p><p align=\"center\"><img src=\"/static/upload/20180629-11-47-59_17307_1530244098(1).png\" style=\"max-width:100%;\"><br></p><p>\n\n\n\n\n\n\n\n\n\n\n\n</p><p>17日晚上，庞鹤鸣教授又来到基础医学院305教室，会见了江西中医药大学气功科学研究所团队的研究生们。庞教授不仅解答了同学们在科研和学习上的疑惑，他还勉励同学们，珍惜大好青春，努力学习，在中医科研和临床方面取得更大的突破。同学们倍受鼓舞，纷纷表示一定努力拼搏，奋发图强，为我国人体生命科学的发展贡献力量。</p><p><br></p>', '1', '2', '0', '1', '0', '2018-06-29 03:44:16');
INSERT INTO `ys_article` VALUES ('20', '盘古文化', '10', '推荐,头条,加粗', '', '', '/static/upload/20180629-11-59-35_15358_1530244829(1).png', '雅言雅颂悦己怡人', 'admin', '盘古文化以“仁慈友爱，健康快乐” 为核心，倡导以“平静，宽容，谦恭，勤奋，坚定”的态度在人生道路上正心修身', '<p>世界上不同地域、不同种族的文明都始于远古的神话传说。 \n中国的远古文明也具有这个鲜明的特点，且显得更为丰富和深刻。盘古开天地的传说，很可能是向人类揭示天地万物及人类的形成和因果循环的总根源。 \n盘古是中国古代传说中开天辟地的神，也是人类的始祖；盘古文化是世界文化的源头之一。人类历史长河中出现过种种丰富多彩的优秀文化，其精髓都源此，其中，“以顺应天道为根基，以品德至上为灵魂”的中华古文化更是其中的光辉典范。</p><p align=\"center\"><img src=\"/static/upload/20180629-11-55-31_13080_1530244493(1).png\" style=\"max-width:100%;\"><br></p><p>区文伟先生自少喜欢古文化，又因缘际会经历了传奇的人生旅程，历数十年孜孜不倦的探索、钻研与实践，整理出盘古文化这个概念，以一系列的著作和特殊的方法向世人阐述宇宙和人生的真谛——“世间上、宇宙中，一切事物之间的关系都是相对而存在、相容而稳定、相和而共鸣的”！作为盘古的子孙，人类应对大自然“常怀感恩之心，恭谨之情，崇敬之意”，自觉地把“以仁慈善良为基本，以坦率友爱为胸怀”作为一切行动的标准，努力学会以宽容博大的情怀去对天、对地、对人、对物，与其相容相和、共存共荣 \n，以期进入天地人和之最高境界。”</p><p align=\"center\"><img src=\"/static/upload/20180629-11-55-41_13617_1530244535(1).png\" style=\"max-width:100%;\"><br></p><p>盘古文化以“仁慈友爱，健康快乐” \n为核心，倡导以“平静，宽容，谦恭，勤奋，坚定”的态度在人生道路上正心修身，主张以“善字当头，爱在其中，萌发于心，体现于行”的准则在日常生活中抑恶扬善，潜移默化地使人磨炼出克己适众的高尚品格，逐渐达至“心和、意善、气畅、体健”之最佳状态。</p><p align=\"center\"><img src=\"/static/upload/20180629-11-55-50_10396_1530244569(1).png\" style=\"max-width:100%;\"><br></p><p align=\"center\"><img src=\"/static/upload/20180629-11-56-05_12191_1530244603(1).png\" style=\"max-width:100%;\"><br></p><p>\n\n\n\n\n\n</p><p>盘古文化包罗万有，其广博的知识和深刻的哲理涵盖人类社会各个领域和日常生活方方面面。通过雅言吟诵、音乐疗法、书法养生等方式学习盘古文化，可以陶冶性情、洗涤心灵、调节身心、激发潜能。迄今为止，越来越多的人们在盘古文化的亲切指引下走上了仁慈友爱、健康快乐的人生道路！</p><p><br></p>', '1', '2', '0', '1', '0', '2018-06-29 03:53:16');
INSERT INTO `ys_article` VALUES ('21', '中国北戴河医疗气功国际学术交流会圆满成功', '1', '推荐', '', '', '', '网络', 'admin', '5月26日-27日，由河北省医疗气功医院主办，上海气功研究所、德国波恩气功养生学会协办的2018年中国北戴河医疗气功国际学术交流会在位于北戴河的河北省医疗气功医院举行。', '<p style=\"text-align: center;\"><img src=\"/static/upload/20180629-13-04-55_17588_1530248665(1).png\" style=\"max-width:100%;\"><br></p><p>5月26日-27日，由河北省医疗气功医院主办，上海气功研究所、德国波恩气功养生学会协办的2018年中国北戴河医疗气功国际学术交流会在位于北戴河的河北省医疗气功医院举行。河北省中医药管理局综合处副处长刘麟韵、秦皇岛市卫生计生委主任刘瑞玲、河北省医疗气功医院党委书记、院长刘波等人出席大会并发表讲话，来自德国波恩气功养生学会、上海市气功研究所以及全国各地的气功研习者共计二百余名参加了会议。在为期2天的活动中，气功养生专家和气功爱好者围绕大会论文及个人功法心得展开了广泛地交流讨论。</p><p>河北省中医药管理局综合处副处长刘麟韵受姜建明局长委托，代表河北省中医药管理局，向远道而来的海内外各界友人表示热烈的欢迎，讲话中谈到河北是当医疗气功的起源地，河北省医疗气功医院更是全国唯一一家医疗气功专业医院，为发扬医疗气功事业、弘扬中华民族传统文化做出了有益探索和卓越贡献。秦皇岛市卫生计生委主任刘瑞玲发表讲话，对大会的成功及气功事业的发展寄予厚望。随后上海市气功研究所副所长许峰、德国波恩气功养生学会内养功学习团副团长克劳斯·威切尔、比利时健身气功协会代表克雷蒙娣为大会纷致贺词，并向大会赠送礼物;法国巴黎东方文化中心主任、世界健身气功协会副主席柯文，德国太极道协会常务主席罗冰在大洋彼岸发来录像预祝大会圆满成功。</p><p align=\"center\"><img src=\"/static/upload/20180629-13-05-34_14637_1530248685(1).png\" style=\"max-width:100%;\"><br></p><p>\n\n会上就上海市气功研究所许峰先生的《传统太极哲学视野下的大健康——对“太极健康”的若干思考》、德国波恩气功养生学会《气功与养生》、河北省医疗气功医院内养功传人刘亚非专家的《内养功的临床辩治与气功处方的应用》、河北省医疗气功医院李经悟弟子冯益建的《李经悟太极内功临床应用》等11项课题进行了交流探讨。河北省医疗气功医院首席专家内养功传人刘亚非女士主讲的内养功公开课得到了与会人员的一致好评与赞誉。 \n\n</p><p align=\"center\"><img src=\"/static/upload/20180629-13-05-46_13779_1530248719(1).png\" style=\"max-width:100%;\"><br></p><p>此次中国北戴河医疗气功国际学术交流会的成功举办，有力推动了气功国际化的传播，对于进一步弘扬中医药文化，提高人类健康水平发挥了积极的促进作用。 &nbsp; &nbsp;<br></p><p><br></p>', '1', '0', '0', '1', '0', '2018-06-29 05:03:51');
INSERT INTO `ys_article` VALUES ('22', '一大波俄罗斯人来到了天柱山……潜山皖水中俄情', '1', '头条,推荐,加粗', '', '', '', '网络', '《看安徽》', '潜山皖水中俄情', '<p>大家好，我叫吉尼斯，来自俄罗斯。今天我和咱们的主持人李琳一起，来到了位于安徽省潜山县的天柱山风景区。在我们的身后即将举行一场非常重要的活动，赶紧跟我去看看吧！</p><p style=\"text-align: center;\"><img src=\"/static/upload/20180629-13-52-25_11144_640.gif\" style=\"max-width:100%;\"><br></p><p>今年是中俄地方合作交流年，安徽省安庆市潜山县为主动融入一带一路倡议，加强中俄之间文化旅游交流，首届中俄天柱山文化旅游交流节正在这里举行。俄罗斯联邦远东发展部部长亚历山大·加卢什卡等一行80多位俄罗斯嘉宾参加了这一活动。</p><p style=\"text-align: center;\"><img src=\"/static/upload/20180629-13-52-45_13747_64.gif\" style=\"max-width:100%;\" class=\"\"><br></p><p>在安徽省潜山县，这座海拔1489.8米的山，天柱山，又名潜山，山名之意是天之柱石，它以其雄奇灵秀的山水、独特的花岗岩地貌，令人赞叹的文化、成为旅游观光的胜境，陶冶情操的圣地。</p><p style=\"text-align: center;\"><img src=\"/static/upload/20180629-13-54-22_14093_0.gif\" style=\"max-width:100%;\"><br></p><p>得益于优良的生态和神奇的气场，天柱山孕育出源远流长的养生文化，每年都有许多俄罗斯游客来天柱山观光度假、休闲养生。据统计，2017年天柱山就接待俄罗斯游客15009人次。</p><p style=\"text-align: center;\"><img src=\"/static/upload/20180629-13-54-34_12843_40.gif\" style=\"max-width:100%;\"><br></p><p>天柱山荣膺“世界地质公园”称号，这位老人热泪盈眶的见证了这一历史性时刻，他便是为天柱山一直奔走努力的本土乡贤郑炎贵先生。今天，郑老给吉尼斯和李琳布置了一个小任务——寻找天柱山上也有很多奇形怪状的石头！</p><p style=\"text-align: center;\"><img src=\"/static/upload/20180629-13-55-42_14939_6  40.gif\" style=\"max-width:100%;\"><br></p><p>在寻找石头的过程中，吉尼斯不仅感受到天柱山山石的无穷魅力，还遇到很多来自家乡的游客。看着天柱山的美景，他们激动地唱起了最喜爱的歌曲。</p><p style=\"text-align: center;\"><img src=\"/static/upload/20180629-13-55-57_14469_64  0.gif\" style=\"max-width:100%;\"><br></p><p>地处天柱山景区大龙窝索道附近的卧龙山庄又被称为：安徽作家村。数十处木屋掩映于溪山之间。山庄秉持绿色无污染天人合一的观念。</p><p style=\"text-align: center;\"><img src=\"/static/upload/20180629-13-56-22_15104_640k.gif\" style=\"max-width:100%;\"><br></p><p>听说今天师傅烧制天柱山当地美食，吉尼斯早早也做了准备，带了很多原材料前来，准备露一手。</p><p style=\"text-align: center;\"><img src=\"/static/upload/20180629-13-56-35_12096_640ii.gif\" style=\"max-width:100%;\"><br></p><p>\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n</p><p>想知道在天柱山还发生了哪些有意思的事情吗？敬请关注今晚的《看安徽》！</p><p><br></p>', '1', '4', '0', '1', '0', '2018-06-29 05:50:20');
INSERT INTO `ys_article` VALUES ('23', '安徽多美景 爱在天柱山', '1', '推荐', '', '', '', '微信', '任璐璐', '潜山皖水中俄情', '<p>对于我这样的外省人来说，当接到天柱山选题的时候，心情是兴奋的，因为我想去探寻天柱山到底有什么样的魅力吸引着来自全世界的游客。\n</p><p align=\"center\"><img src=\"/static/upload/20180629-14-02-48_12758_1530252141(1).png\" style=\"max-width:100%;\"><br></p><p>正巧，第一天的拍摄就赶在了天柱山文化旅游交流节活动期间，我们采访到了俄罗斯远东发展部部长亚历山大·加卢什卡，之后我们还邀请俄罗斯团队一同游览天柱山的美景。期间在下山的时候，这些俄罗斯游客沿途唱起了他们家乡的名曲，让我们感到无比激动，同时他们又唱起了脍炙人口的中文歌，达到了全场的高潮，这一刻，歌声在天柱山间回荡，跨越了国界，我激动的要落下泪水，到底是什么样的名曲如此感人，就留给观众朋友们自己去看节目倾听啦。\n</p><p align=\"center\"><img src=\"/static/upload/20180629-14-02-59_17201_1530252166(1).png\" style=\"max-width:100%;\"><br></p><p>由于之前跟天柱山管委会有着默契的沟通，又加之天公作美，拍摄当中时不时会有一些惊喜，我们的主持人李琳在与嘉宾的活动环节中也充满了欢乐和感动。万水千山总是情，潜山皖水中俄情，就像俄罗斯嘉宾主持人吉尼斯所说的，天柱山擎天一柱的豪迈和山民们对美好生活的向往都留在了我们的画面里，也刻画在了我们的脑海中。\n</p><p align=\"center\"><img src=\"/static/upload/20180629-14-03-08_16131_1530252188(1).png\" style=\"max-width:100%;\"><br></p><p>这是一部呕心沥血的跨年制作，集结了所有人们的期待和热情。在此，再次感谢天柱山管委会的工作人员以及这次参与拍摄的各相关人员，正是有了他们的全力支持配合才成就了我们高质量的拍摄。\n</p><p align=\"center\"><img src=\"/static/upload/20180629-14-03-17_11253_1530252227(1).png\" style=\"max-width:100%;\"><br></p><p>你若盛开，清风自来，天柱山静静的向着远方的朋友招手，这里形态各异的花岗岩塑造成巨石的乐园，这里海拔1489.8米的主峰形成了一柱擎天的豪迈，这里的摩崖石刻留下了历代名人骚客的诗篇佳作，山脚下的卧龙山庄又会给您原生态的生活体验，很多游客止步于此不愿离开。\n</p><p align=\"center\"><img src=\"/static/upload/20180629-14-03-26_12589_1530252247.png\" style=\"max-width:100%;\"><br></p><p>好了，今天就剧透这么多，详细节目请锁定今晚《看安徽》之潜山皖水中俄情。 &nbsp;&nbsp;<br></p><p><br></p>', '1', '6', '0', '1', '0', '2018-06-29 06:00:01');
INSERT INTO `ys_article` VALUES ('24', '首届中俄·天柱山文化旅游交流节（视频）', '29', '加粗,头条', '', '', '/static/upload/20180629-14-43-47_10685_微信图片_20180625140614副本.jpg', '安庆网', 'admin', '中俄天柱山文化旅游交流节', '<p style=\"text-align: center;\">&nbsp;<img src=\"/static/upload/20180629-14-06-23_16280_1530252467(1).png\" style=\"max-width: 100%;\" class=\"\"></p><p>https://vzan.com/live/tvchat-668379?ver=636638281619713883&amp;shareuid=82635312&nbsp;</p><p style=\"text-align: center;\"><img src=\"/static/upload/20180629-14-49-48_11664_1530253068副本.jpg\" style=\"max-width:100%;\"><br></p><p style=\"text-align: center;\"><img src=\"/static/upload/20180629-14-46-48_18180_1530254773.png\" style=\"max-width:100%;\"><br></p><p style=\"text-align: center;\"><br></p>', '1', '13', '0', '1', '0', '2018-06-29 06:06:46');
INSERT INTO `ys_article` VALUES ('25', '大健康文化旗舰登陆澳门', '1', '头条,推荐,加粗', '', '', '/static/upload/20180629-14-36-44_16077_1530254045(1)副本.jpg', '香港日报', '王文长 ', '国际健康健美长寿论坛•博览会暨首届世界大健康运动会，沿着一带一路指引的航线，首次抵达澳门。', '<p><span style=\"display: inline !important;\">4月的澳门。碧水蓝天，莲花争艳。</span><br></p><p>享誉全球的大健康文化旗舰————国际健康健美长寿论坛•博览会暨首届世界大健康运动会，在健康中国战略的驱动下，沿着一带一路指引的航线，首次抵达澳门。</p><p><b>大健康文化十年探索路</b></p><p>健康健美长寿，世界的语言，人类的理想，永恒的追求。</p><p>有这样一组数字：1949年，我国人的平均寿命45 \n岁，1980年，我国人的平均寿命61岁，2017年，我国人的平均寿命76.3岁。而目前世界人的平均寿命70岁。</p><p>同在一片蓝天下，为什么三个不同年代里的中国人平均寿命有这么大的不同？</p><p>沿着铺展在共和国大地上的脚印寻觅，我们更加清晰地看到，中国人平均寿命的增加，与站起来、富起来、强起来的国家振兴之路相同步，与民族复兴轨迹相重叠。</p><p>中国人平均寿命超过世界人平均寿命7.6岁，这已经远远超越了统计学上的意义，他是中国共产党领导下的中国特色社会主义制度的必然结果，是我们这个国家、这个民族、这个十三亿多群体幸福指数不断提高的历史见证。</p><p>数字的背后，颠覆的是千百年来“人的命，天注定”的天规定律。</p><p>人民对美好生活的向往，就是我们的奋斗目标。从发展体育运动，增强人民体质，到将全民健身上升为国家意志，从没有健康就没有小康的认知，到把人民的健康放在优先发展地位，中国共产党人始终把以人民为中心作为立党为公、执政为民的第一要务。</p><p>为中国人民谋幸福，为中华民族谋复兴，不忘初心，方得始终。大力开展全民健身和健康养生，到2030年，使中国人平均寿命达到79岁，这是健康中国的重要指标，也是人们享受美好幸福生活的新追求新目标。</p><p>理论创新，往往会带来一场深刻的变革。上世纪八九十年代，我国导弹之父钱学森院士创立了人体科学新理论，我国著名免疫学家冯理达教授，首次提岀了健康健美长寿学新概念，我国人体科学和体育事业的卓越领导人伍绍祖主任，为发展我国人体科学事业呕心沥血。</p><p>气功热的迅猛升温，考验着政府的社会管理能力和社团组织的管理自觉。如何让几亿人群参与的活动由无序变有序，使练功群体成为社会团结和谐的重要力量？在市场经济多元多样多变中，如何做到政府主导，全民参与，群团配合，社会监督，服务“两个一百年”奋斗目标？社团组织如何坚持正确方向，与时代同步，与政府同心，与民众同行，成为绕不开、躲不过，需要迫切回答的课题。</p><p>投身社会实践，回答时代命题。在冯理达教授、伍绍主任的领导和主导下，郭周礼等一批致力于传播健身养生功法的仁人志士，发起成立了国际健康健美长寿学研究会，立志在合拍、合力、合作中，打造具有中国特色的囯际一流品牌的健康养生文化旗舰，在同步、同心、同行中，为传播中华健康养生文化做出新业绩新贡献。</p><p>2006年，健康养生文化旗舰出征曼谷，开启了扬帆远航的第一站。11月4日，首届国际健康健美长寿论坛·博览会在泰国曼谷举行，伍绍祖主任主持通过了《曼谷宣言》，成为健康巨舰驶向远方的定海神针。</p><p>2007、2008、2009、2010年，在伍绍祖主任、冯理达教授的主持下，研究会在北京、上海连续举办了四届论坛·博览会，通过了《北京宣言》和《上海宣言》。</p><p>宣言是灯塔，指引着健康养生文化旗舰沿着正确航向驶向辽阔的远方。</p><p>宣言是承诺，时刻在考问着我们怎样做才能上不愧对天，下不愧对民。</p><p>此后的几年中，在黄晴宜主席主持下，健康养生文化旗舰分别驶向济南、吉林通化，登陆莫斯科和韩国大邱，加速了中华健康文化走出去、走进去的传播力度，引起了社会各界的广泛关注和称赞。</p><p>新世纪的第一个十年里，以论坛·博览会为平台和抓手，国际健康健美长寿学研究会为健身养生文化传播机制建设、社团组织管理、国际交流合作做了大量探索实践，走出了政府主导、民众参与、社会监督、以我为主、规范运行的传播新路子。</p><p><b>十年征程，铸就丰碑。</b></p><p>健康养生文化走向国际的十年间，是举旗正路的十年，是开拓创新的十年，是合作共赢的十年。</p><p>十年风雨兼程，健康养生文化旗舰在传播中华文化的道路上取得了重要成果，牢牢占据了国际健康健美长寿领域的主导权，赢得了政府和民众的信任支持，探索出了一条正确的方向和道路，取得了一大批科研学术新成果，拓展了传播交流新途径，提升了办会新质量新高度。</p><p>十年的实践证明，传播中华健康养生文化，社团组织只有坚持服务大局有灵魂，践行践诺有动力，政府支持有保证，以我为主有中心，集成整合有抓手，才能天地更广阔，奋斗有作为。</p><p><b>大健康文化旗舰开启新征程</b></p><p>2018年4月20日至21日，澳门渔人码头国际会议中心座无虚席。</p><p>20日上午10时，在庄严的国际歌声中，第十届国际健康健美长寿论坛·博览会暨首届世界大健康运动会隆重开幕。</p><p>威风锣鼓震天响，手持彩练舞起来。澳门鲜鱼行总会40人组成的表演队第一个闪亮登场，激情表演了具有渔家人特色的舞蹈《舞醉龙》。身着艳丽传统服饰的中老年队员们用欢快的舞蹈语言，将人间甘甜美酒献给大海，与海狮、海鱼、海龙同欢乐、同畅饮，一招一式表达了人与大自然和谐相处，对健康养生文化旗舰登陆澳门的满心喜悦，对“一国两制”和澳门美好明天的更加自信。</p><p>按照渔家人的传统习俗，出席大会的贵宾们登台，为全体舞蹈队员们热情点睛，表达了对澳门同胞的深情祝福。</p><p>中华文化源远流长，伟大祖国地久天长。由陕西省延安市老年人体育协会16人组成的安塞腰鼓队威风登场，展示了大西北黄土地文化的苍劲雄浑。安塞腰鼓名扬海内外，曾经参加了香港回归、澳门回归、2008年北京澳运会等重大庆典演出，向世界展现了中华文化的激情与活力。</p><p>国际健康健美长寿学研究会主席、第十届国际健康健美长寿论坛·博览会暨首届世界大健康运动会组委会主席郭周礼，本次大会组委会副主席盛志国、李深、罗悠真、陈敬德、吴若梅、王振国、区文伟等出席并致辞。</p><p>来自美国、俄罗斯、日本、德国、澳大利亚、新西兰、新加坡、爱尔兰、韩国、香港、澳门、台湾等23个国家和地区的1200余名代表参加了大会。</p><p>国家体育总局、澳门特区政府、俄罗斯联邦公共社团和俄罗斯武术联盟、中国老年人体育协会、香港气功与武术协会等38个政府机构、社会团体发来贺信贺电表示热烈祝贺。</p><p>郭周礼主席以“互联互通打造健康养生新业态”为题致开幕辞，代表大会组委会向来自全球23个国家和地区的政府官员、人体科学工作者、优秀健身功法传人、健康健美养生群团代表、国内外媒体记者、健康产品研发企业代表，以及参加首届世界大健康运动会的全体中老年朋友们，表示热烈欢迎和衷心感谢。他说，顺应时代潮流，满足人民意愿，共享健康幸福生活，是本次大会的主题。传承创新健康养生文化，只有通过多元化的交流互鉴，多样化的兼收并蓄，才能让健康文化更多更好地惠及民众。他强调，投身健康中国的伟大实践，落实一带一路发展战略，新时代赋予了我们发展繁荣大健康的新使命，为推动大健康发展，从现在起，一年一度的论坛·博览会暨大健康运动会，将在一带一路沿线国家和地区举行。</p><p>大会副主席盛志国先生在致辞中，高度赞扬了论坛博览会十年来取得的丰硕成果，对第十届大会的召开表示热烈祝贺。他说，发展繁荣大健康文化事业，是大势所趋，是时代潮流，是健康中国战略的重要内容，也是一带一路战略合作交流的题中之意，作为以传播中华健康文化为宗旨任务的社团组织，要带头落实党和国家的战略决策，为民众健康幸福提供更好更多的服务。</p><p>罗悠真、陈敬德、区文伟、依格里、张永祥等在致辞中表示，中华文化是人类的宝贵财富，作为海外华人和国际健康健美长寿学会海外分会组织负责人，要积极担当起健康养生文化走出去、走进去的光荣使命，作中华健康文化的积极传播者、中外文化交流的促进者，为服务民众健康作出新成绩。</p><p>“这是一次促进国际间团结合作的大会，是增进中俄两国人民深厚友谊的大会，相信通过交流健康养生、开展健康运动、传播健康健美长寿经验，必将对大众健康产生重要影响和作用。我们深信，这项弘扬传统、福泽民众的事业一定会持久下去。”俄罗斯公共社团主席、俄罗斯联邦武术联盟主席、俄罗斯联邦政府副总理特鲁特涅夫、俄罗斯政府办公厅主任吉里延科，在贺电中表达了对这项事业的信任支持和期望。</p><p>澳门特区政府对大健康文化旗舰首度登陆澳门高度重视，从财力、物力、人力上给予了全方位支持，力度之大前所未有。澳门鲜鱼行总会、澳门无极健身球体育协会、澳门长者体育协会、澳门活力回春康体会、澳门江门同乡会、澳门西环耆青文艺康体会、澳门中老年艺术总会、澳门太极拳社等社团组织，积极响应澳门政府号召，全力配合，精心准备，排心排练，为大会准备了精彩节目和健身功夫，充分体现了对大健康文化事业的关心支持。一群年轻志愿者活跃在场内场外，热情、周到、仔细、微笑，为大会提供了全程优质高效服务，充分体现了澳门同胞的无私奉献精神。</p><p>中国老年人体育协会把协助办好首届世界大健康运动会视为自己的职责，向各省市区县发出通知，动员基层组成表演队参加大健康运动会项目展示。盛志国常务副主席专门指派具有组织指挥大型运动会经验的专家亲临指导，为大运会的圆满成功提供了有力保证。</p><p>本届大会分为学术论坛、产品博览、大健康运动会三个专题分别展开。</p><p>在学术论坛板块，分为基础理论研究、应用试验研究、功法功理探讨三个部分。收到学术论文100多篇，大会交流30多篇，内容涉及古籍整理、科学实验、养生功法、传统武术、太极功夫、中医中药等。</p><p>李深会长的“活力长者初探”、王振国教授的“预防肿瘤是健康长寿的保障”、宋新红教授的“太极运气法功法介绍”、余同元教授的“试论内劲一指禅功法功能特点”、刘超教授的“健身气功用于脏像未病诊疗系统保健处方初探”等优秀论文，代表了学术研究新水平新成果，具有很高的学术和实践价值。</p><p>本次大会内容之丰富、规模之恢宏、参会人数之多，刷新了文化旗舰启航十年来的新记录。</p><p><b>首届大健康运动会精彩纷呈</b></p><p>21世纪是人类追求健康的世纪。</p><p>2016年8月，党的十八届五中全会通过了“健康中国2030”规划纲要，从此，大健康新概念走进了中华民族的现代语言辞典。</p><p>大健康，就是围绕人的衣食住行、生老病死，对生命实施全程、全面、全要素的呵护。其核心既追求人的生理、身体健康，也追求心理、精神等方面健康，实现从单一救治模式转向“防—治—养”一体化防治模式。</p><p>“生得优、活得长、不得病、少得病、病得晚、质量高、走得安”，这是自古以来人们追求健康的核心期望。中国古典养生学倡导的“治未病”，是对实现核心期望给出的最经典答案。</p><p>以大健康为主题，以“治未病”为关键点，在广泛调研论证基础上，国际健康健美长寿学研究会郭周礼主席，率先提出举办世界大健康运动会这一新主张新模式，并进行了周密的顶层设计。</p><p>“凡是有利和有益于健康健美长寿的一切活动，均属于大健康运动范畴。广义上包括体育运动、精神修养、心理健康、生理康复、社会文明、环境优美、道德高尚、天人合一等等。”郭周礼主席对大健康运动给出了自己的见解。</p><p>大健康运动会，是一种与竞技体育比赛规则有所不同的、又与强身健体目标完全一致的、倡导大众化的、汇集各国家各民族健康养生理念和特色方式方法为一体的文化传播平台。</p><p>显而易见，大健康运动会就是要将奥运会、洲际和地区运动会、行业运动会等项目以外的、具有强体健体功能作用的活动囊括其中，目的在于将深藏于民间、喜闻乐见、潜力无限，广受群众喜爱的健身养生方法传承下来，展示出来，推广开来，共享优秀健身健美长寿成果。</p><p>不难理解，大健康运动会之所以“大”，主要凸显在放眼国际视野大，海纳百川胸怀大，开放包容气度大，兼收并蓄成果大。</p><p>劲歌热舞讴歌崭新时代，全民健身缔造健康中国。</p><p>首届世界大健康运动会展现出了激情洋溢的活力和前所未有的吸引力。</p><p>“我祝祖国三杯酒，一杯酒举过手，两杯酒举过头。满怀深情歌一曲，祝福江南稻花香，祝福塞北小麦熟”。由珠海市离退休干部活动中心舞蹈队18名老干部表演的秧歌舞《盛世欢歌》，以欢快优美的舞姿，拉开了大运会舞蹈表演的序幕。</p><p>安徽凤阳市老年人体协队梅周成等10人表演的《凤阳花鼓》，湖北武穴市老年体协耿邦兴等8人表演的《莲湘舞》，将爱党爱国爱民情怀融入艺术，表达了对祖国的无限热爱，对美好生活的舒心惬意。陕西咸阳市老年人体育协会杨光多等15名中老年妇女，和着欢快的乐曲,跳起了橡筋操舞，她们潇洒的舞姿、柔婉的动作、优美的线条、舒心的娱乐，展示了健康健美活动所迸发出的热情与活力。</p><p>“我们走在大路上，意气风发斗志昂扬”。这首传唱了半个多世纪的经典歌曲，表达了中国人民的豪情壮志。由深圳市福田秋韵操舞艺术团表演的健康球操、澳门无极健身球协会表演的双球操、陕西安康市老年人体协表演的花绳操，澳门长者体育协会25人表演的柔力球操，热情奔放，活力四射，超越了年龄，演绎出了健康健美艺术化，文化艺术生活化。</p><p>“五千年的黄河水，流淌着一个梦，河两岸的五色土，长出了梦中景。听这滚滚春潮，举起澎湃的心声，国家富强，民族振兴”。由深圳市宝安沙井街道舞蹈队表演的民族舞蹈《共圆中国梦》，珠海晚晴艺术团表演的《炫彩排舞》，深圳福田区新华社区老年人协会表演的健身腰鼓等，艺术地表达了中华儿女决胜小康志在必得，对站在世界舞台中心的祖国，即将实现“两个一百年”奋斗目标充满了欢欣鼓舞。</p><p>“我们意气风发走进新时代，我们唱着东方红当家做主站起来，继往开来的领路人，带领我们走进新时代”。高歌一曲，豪情万丈。由澳门长者体育协会25人阵容表演的舞蹈《走进新时代》，以磅礴气势表达了澳门同胞与伟大祖国同呼吸、共命运，与全国十三亿多人民一道，意气风发迈进中国特色社会主义新时代的精神风貌。</p><p>传统性，现代性，群众性、民族性，地域性特色，在首届世界大健康运动会上得到了淋漓尽致的显现。</p><p>中华气功，民族瑰宝。舒缓柔和的乐曲回旋，深圳幸福人文化传播公司代表队表演的“幸福养生法”，湖北武穴市代表队表演的“四十二式拳竞赛套路”，将人们的注意力引入了以调息、调身、调心为目的的强身健体、防病治病、开发潜能的气功态。</p><p>一花引来百花艳。场上拉开了名符其实的国际气功大比赛。美国盘古文化国际研究所代表团表演的“盘古功夫”，德国代表队表演的“养生功法”，日本代表队表演的“健身气功”，法国代表团表演的“东方气舞”，俄罗斯联邦及独联体国家代表队表演的“意力拳”、“空手道”、“跆拳道”，让上千名观众亲眼目睹了中华气功文化的吸引力穿透力。</p><p>北京郭林新气功，一个名扬海内外的气功功法称谓。</p><p>百余人组成的厐大表演队，精神矍铄地登台和漂亮转身，让会展中心1200多人的目光齐刷刷地聚焦到了这群“特殊人”的身上，也把大运会表演赛掀向了高潮。</p><p>他们和她们表演的“郭林新气功抗癌功法”、“郭林新气功慢性病功法”、“五禽戏”等功法，动中有静，静中有动，动静结合，透过一招一式，表达了他们与疾病顽强搏斗，把命运牢牢掌握在自己手里中的自信。当大家得知他们全部都是癌症患者，通过练习郭林新气功战胜了病魔的事迹时，场上响起了雷鸣般的掌声。一叶知秋，透过郭林新气功所展现出的强大震撼力，让大家再次领略了中华气功文化的博大精深。</p><p>极具柔和性、缓慢性、圆活性、连贯性、完善性特点的太极拳，富含着东方哲学思想的智慧光芒。</p><p>湖北武穴市代表队表演了《二十四式简化太极拳》和《花拳》、《太极功夫扇》、《四十二式拳竞赛套路》；澳门大同太极社表演了《传统太极拳》和《杨氏太极拳》；中华气功澳门研究会表演了《28式杨式太极拳》、《杨式太极剑51式》。以及中国东北代表队表演的《易筋经》，苏州市代表团表演的《内劲一指禅功法樁架》，方圆太极拳内家拳学会代表团表演的《精简陈式太极拳》。这些充满着养生特点、武术内涵、音律审美、交际娱乐为一体的健身文化大餐，让观众眼界大开，陶冶了性情，提升了精气神。</p><p>冷先锋，当代太极拳界的名家。全国武术太极拳冠军、香港太极拳锦标赛冠军、香港优秀人才获得者。</p><p>冷先锋率领香港先锋武术学院十余名学员组成的代表队，表演的《陈式太极拳》，一招一式表现出了太极拳王的深厚功底和不同凡响，将大运会太极拳表演赛掀向了高潮。“让太极拳普及世界百姓体育运动，实现太极拳走进世界百姓家庭”。以此为愿景和使命，冷先锋团队在本次大会上的出色表演，赢来大家热烈掌声，也成为他们在即将召开的首届世界太极拳交流大会上的精彩热身。</p><p>香港善意养生堂代表队表演的《太极拳功夫扇》，澳门江门同乡会表演的《武当太极功夫扇》，透过漂移洒脱的舞姿，拳功与扇舞的柔和转换，配以轻盈的旋律，表达出了东方哲学思想的丰富内涵。</p><p>紫荆花四季清香，水莲花艳丽芬芳。21日下午5时，作为首届世界大健康运动会的压轴表演项目，由澳门西环耆青文艺康体会表演的旗袍舞《春天的故事》、《时装秀》，由澳门中老年艺术总会奉献的欢乐秧歌，展示出了中国老年人老有所乐，老有所为，老有所爱，青春永驻的潇洒气质和夕阳无限好的满腔情怀。</p><p><b>大健康时代的历史方位</b></p><p>健康中国，强国方略。</p><p>当代中国进入了新时代，大健康文化旗舰开启了第二个十年。集合在构建人类命运同共体的战略布局下，如何突出全民健身这个健康中国主题，如何围绕全民健康这个健康中国根本目的，找准国际健康健美长寿学研究会在新时代的历史方位，事关大健康文化旗舰驶向何方？</p><p>“高举科学旗帜，践行核心价值。以人民健康为中心，以构建人类命运共同体为牵引，爱党爱国爱民，创新、协调、绿色、开放、共享，服务国家，服务社会，服务群众，服务行业。</p><p>传播健康文化，贡献养生真知。弘扬传统，敬重生命，科学至上，传递真知，崇尚美德，平等互鉴，开放包容，兼收并蓄，保护环境，绿色低碳，清洁美丽，增进民心相通。</p><p>汇聚优势资源，促进发展繁荣。肩负主责，整合人脉，活跃科研学术，做强互通网通，培植主力团队，打造名优品牌，用正能量普及健康养生，用正形象参与国际交流。</p><p>遵守法律法规，从严修身自律。遵守宪法，自觉守法，率先垂范，热心公益，立言立行，远离恐暴，杜绝黑色，不染黄毒，反对迷信，与真理同向，与政府同心，与民众同行。”</p><p>《澳门共识》，成为新世纪开启健康养生文化旗舰第二个十年的庄严宣示。</p><p>4月20日晚8时，郭周礼主席主持召开国际健康健美长寿学研究会和世界大健康运动联盟理事会，审议并通过了研究会工作报告，与会全体理事以鼓掌方式通过了《澳门共识》。</p><p>《澳门共识》是一部宣言书，向国际社会宣示了新时代发展繁荣大健康事业的指导原则、目标任务和线路图。这是继《曼谷宣言》《北京宣言》《上海宣言》之后，国际健康健美长寿学研究会向国际社会作出的又一个庄严承诺。</p><p>找准方位，谋篇布局，让健康养生文化旗舰行稳致远。</p><p>郭周礼主席在理事会上作了题为“发展繁荣大健康事业新使命”工作报告，明确了大健康文化旗舰在第二个十年里的目标任务。</p><p>用新思想引领方向，锻造忠诚可靠魂魄；紧贴健康中国发力，全面践行《澳门共识》；沿着一带一路布局，拓展大健康多元空间；肩负起主体责任，牢牢掌握话语权；做大做強互联网十，分会组织覆盖全球。</p><p>郭周礼主席从五个方面对今后工作作出了安排部署，强调指出，一定要把习近平中国特色社会主义思想作为研究会的旗帜和方向，以新思想为灵魂，以构建人类命运共同体为站位，以“一带一路”为牵引，以发展繁荣大健康文化为使命，主动担当，当好主力，做好旗手，掌好旗舰，续写大健康文化旗舰第二个十年新篇章。</p><p>新时代要有新作为，大健康要做大贡献。</p><p>坚定信念，走正道路；以我为主，率先发声；民本至上，全球覆盖；整合资源，打造名牌；遵守宪法，修身自律。出席第十届国际健康健美长寿论坛·博览会暨首届大健康运动会的23个国家和地区分会组织负责人，一致表示，要牢记使命，践行共识，不负众望，团结奋进，在新时代新征程为发展繁荣大健康文化事业做出新贡献。</p><p>路，就在脚下！</p><p>\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n</p><p>路，向前延伸！</p><p><br></p>', '1', '7', '0', '1', '0', '2018-06-29 06:28:01');
INSERT INTO `ys_article` VALUES ('26', '医学气功学', '4', '头条,推荐,加粗,跳转', '', '医学  气功', '/static/upload/20180629-15-07-28_10578_t0163f3fa7d0a994d92副本.jpg', '喜马拉雅FM', '宋新红 夏双全', '医学气功是研究探索气功在医学领域的应用规律及其机理的一门科学', '<p style=\"text-align: center;\"><img src=\"/static/upload/20180629-15-03-49_11803_t0163f3fa7d0a994d92.jpg\" style=\"max-width:100%;\" class=\"\"></p><p style=\"text-align: left;\"><span style=\"display: inline !important;\">《</span><span style=\"display: inline !important;\">医学气功学》——</span><span style=\"display: inline !important;\">医学气功是研究探索气功在医学领域的应用规律及其机理的一门科学。本教材由广州中医药大学气功研究室宋新红教授、夏双全教授编著，由浅入深介绍医学气功的基本理论、基本功法、气功调治疾病和发放外气治疗方法，具有实用性和可操作性。本书作为广州中医药大学的气功课专用教材，已由吉林大学出版社正式出版发行。</span><span style=\"display: inline !important;\">&nbsp;&nbsp;</span></p><p style=\"text-align: left;\"><span style=\"display: inline !important;\"><b>有声书 http://www.ximalaya.com/youshengshu/15396263/</b></span><br></p><p><br></p>', '1', '6', '0', '1', '0', '2018-06-29 07:00:32');
INSERT INTO `ys_article` VALUES ('27', '我喜爱习练秦重三先生的三圆式站桩功', '5', '推荐', '', '', '', '网络', '李春生', '静默吐纳八字法', '<p><span style=\"display: inline !important;\">1966年文革时学校不上课，我每天早上去地坛公园散步。在公园里认识了一位习练站桩功的张先生。跟张先生认识百日之后，他送给了我一本书，书名是秦重三先生著的《气功疗法和保健》。</span><br></p><p>闲暇时，我每日都翻阅《气功疗法和保健》一书。因为喜爱书中的内容，我便想找到这本书的作者。有一天，我直接到了北京中医医院办公室去打听秦先生的住处。办公室的工作人员听说我的来意后，给了我一个秦先生的地址。就这样，我在1966年的9月9日去拜访了秦重三先生。</p><p>在北京朝阳门内大方家胡同46号，我在一间普通的平房中见到了红光满面的秦重三先生。跟秦先生交往了180天之后，他把密传的“静默吐纳八字法”传给了我。他要求我每日找一块松柏树林，在那里静静的站桩。呼吸动作都用“静默吐纳八字法”严格要求。通过10个月的锻炼，我感到吃的好、睡的香，身体慢慢的强壮起来。</p><p>我的师娘叫闫淑清，她生前曾跟我说，我师父秦重三站桩的地点是在景山公园，他在公园站了好几年，很有功夫。1959年，北戴河气功疗养院的院长刘贵珍到北京来邀请我师父去北戴河开会。在征得秦重三先生的同意后，刘贵珍院长买了火车票，陪同我师父一同去了北戴河。秦重三先生在北戴河气功疗养院做了有关用气功的方法养心、养肝、养肾的大道理。当会议闭幕后，秦重三先生就回到了北京。这时，北京中医医院热情邀请秦重三先生到北京中医医院气功科工作。以上说明刘贵珍院长和秦重三先生是好朋友。</p><p>\n\n\n\n\n</p><p>听说5月25日在北戴河要召开气功医学会议，我作为秦先生的义子，我向大会表示祝贺。我预祝大会召开的圆满成功。</p><p><br></p>', '1', '2', '0', '1', '0', '2018-06-29 07:14:00');
INSERT INTO `ys_article` VALUES ('28', '江西中医药大学将主办“气——意识与人体生命科学”国际学术研讨会及专念操作技术培训班', '14', '推荐', '', '', '', '江西中医药大学气功科学研究所', 'admin', '江西中医药大学携同哈佛大学Ellen Langer教授，将于2018年6月15日—22日举办“气——意识与人体生命科学”国际学术研讨会及专念操作技术培训班。', '<p style=\"text-align: center;\"><img src=\"/static/upload/20180629-15-21-05_18073_1530256629(1).png\" style=\"max-width:100%;\" class=\"\"><br></p><p>气和意识在中医学中有着密切的联系，它们涉及到人体生命的奥秘，是人体生命科学研究的重要内容。在现代心理学中，积极心理学的许多操作手段和方法与中国中医气功学有着许多相近的地方。</p><p>江西中医药大学气功科学研究所及“气学说现代科学研究平台”深入研究了人体之气的实质和气功锻炼的机理，创新了一套体察人体之气及意识调控优化人体生命的方法，在近几年的培训和临床实践中取得了良好的效果，为揭示人体生命奥秘提供了内证的途径和方法。</p><p align=\"center\"><img src=\"/static/upload/20180629-15-21-22_12403_1530256683(1).png\" style=\"max-width:100%;\"><br></p><p style=\"text-align: center;\">气学说思维导图</p><p>美国哈佛大学积极心理学奠基人之一的Ellen \nLanger教授从心身合一的角度，研究积极意识和专念对人们的身心健康以及生活各个方面的影响，在世界心理学界享有盛誉。</p><p>为促进中医气学说与现代心理学融合发展，江西中医药大学携同哈佛大学Ellen \nLanger教授，将于2018年6月15日—22日举办“气——意识与人体生命科学”国际学术研讨会及专念操作技术培训班。此次学术研讨会将对气和意识本质及其与人体生命的关系等重大问题进行深入的探讨，我们将向您展现东西方文化在最前沿的科学领域的深度交融，揭示人体生命的奥秘。</p><p style=\"text-align: center;\"><img alt=\"\" src=\"https://mmbiz.qpic.cn/mmbiz_jpg/r8xXHMEPaY5CZC8zRHdCT9vXNggNuVBMXwqViaNoLskFDKhbwpZE6fkSw3iaj2g8aL77UKX95ggUibGLaWl3ENxNw/640?wx_fmt=jpeg&amp;tp=webp&amp;wxfrom=5&amp;wx_lazy=1\" width=\"180\" height=\"206\">&nbsp;&nbsp;<span style=\"display: inline !important;\">&amp; &nbsp;</span><img alt=\"\" src=\"https://mmbiz.qpic.cn/mmbiz_jpg/r8xXHMEPaY5CZC8zRHdCT9vXNggNuVBMXaYibNu4qO9eS4fU0icGLjDIZl31ZWLHvZGsU4xtlAtT1Gx0vweqxECA/640?wx_fmt=jpeg&amp;tp=webp&amp;wxfrom=5&amp;wx_lazy=1\" width=\"174\" height=\"199\"></p><p style=\"text-align: center;\">Ellen Langer教授和其著作《专念》</p><p>现将研讨会和培训班有关事项通知如下：</p><p><b>一&nbsp;<span style=\"display: inline !important;\">会议主题</span></b></p><p>探索中医气学说和积极心理学专念的本质内涵，揭示气、意识与人体生命的奥秘。</p><p><b>二&nbsp;<span style=\"display: inline !important;\">会议及培训内容</span></b></p><p>1.专家报告：国内外中医学、气功学、心理学等领域的知名专家学者将对气、意识的实质，及其与人体生命的关系进行深入探讨，并展示他们的最新研究成果。</p><p>2.培训学习：将中医气功学与现代心理学相结合，把气功修炼的方法和手段与积极心理学专念的具体操作相结合，创制出一套内容丰富、超价实效的培训课程，使学员更快掌握意识调控人体生命及专念的操作技能和手段，并通过切实体验领悟其实质，具体内容如下：</p><p><b>培训内容</b></p><p></p><h3><h5><h4><ol><li>1.气功修炼实质及专念机理。</li></ol><ol><li>2.气功专一心念及心理学专念技能和操作方法。</li></ol><ol><li>3.专念量表在临床中的应用。</li></ol><ol><li>4.意识对生物及人体生命影响的科研思路及研究方法。</li></ol><ol><li>5.专念及气功效用的现代科学仪器检测。</li></ol></h4></h5></h3><p></p><p>3.研究展示：会议现场将进行意识对外在物质及人体生命特殊效应的展示，以开拓我们的研究思路。</p><p><b>三&nbsp;<span style=\"display: inline !important;\">培训对象</span></b></p><p>1.高校及科研机构从事心理学、中医气功的研究者人员。</p><p>2.从事大健康产业、养生保健中心的执业人员。</p><p>3.从事健康教育的培训人员。</p><p><b>四&nbsp;<span style=\"display: inline !important;\">培训时间地点</span></b></p><p>1.培训时间：2018年6月15日—6月22日</p><p>2.报名截止时间：6月5日</p><p>3.报到日期：6月14日</p><p>4.报到地点：江西中医药大学湾里校区得月楼</p><p><b>五&nbsp;<span style=\"display: inline !important;\">咨询电话</span></b></p><p>江西中医药大学气功科学研究所</p><p>邹老师：18070146897</p><p>夏老师：15070828614</p><p><b>六&nbsp;</b><span style=\"display: inline !important;\"><b>缴费事宜</b></span></p><p>（一）费用：</p><p>1.会议费1200元（单独参加会议而不参加培训者）</p><p>2.培训班收费标准：(参加会议且参加培训班者）</p><p align=\"center\"><img src=\"/static/upload/20180629-15-29-01_14045_1530256756(1)副本.jpg\" style=\"max-width:100%;\"><br></p><p>（二）汇款方式：</p><p>1、银行汇款方式：</p><p><b>开户名称：江西中医药大学</b></p><p><b>汇款账号：14984001040000018</b></p><p><b>开户银行：中国农业银行江西省南昌市青云谱支行新苑支行</b></p><p>2.支付宝汇款方式：</p><p>打开支付宝扫码功能，扫描“江中气功科学研究所”二维码，即可跳转至付款页面，完成付款。</p><p align=\"center\"><img alt=\"\" src=\"https://mmbiz.qpic.cn/mmbiz_jpg/r8xXHMEPaY5XHVyEkoZeE4mcTP3DgoPK5q42Sjl5PmQ1vWK0wFPR1CSs1Ojc5L26u2WoFJ5iaANban7K0LVNr5A/640?wx_fmt=jpeg&amp;tp=webp&amp;wxfrom=5&amp;wx_lazy=1\" width=\"186\" height=\"213\"></p><p>3.微信付款方式</p><p>打开支付宝扫码功能，扫描“江中气功科学研究所”二维码，即可跳转至付款页面，完成付款。</p><p align=\"center\"><img alt=\"\" src=\"https://mmbiz.qpic.cn/mmbiz_jpg/r8xXHMEPaY5XHVyEkoZeE4mcTP3DgoPKDSbJdhQebZS4zerfdR2bISlQuSk5rSxgUdCpyZ8pH1GxBwCQfY3kIg/640?wx_fmt=jpeg&amp;tp=webp&amp;wxfrom=5&amp;wx_lazy=1\" width=\"191\" height=\"218\"></p><p>4.汇款说明：</p><p>（1）汇款时，银行转账附言处注明：“专念+姓名+单独参会”或“专念+姓名+培训”；支付宝和微信转账备注中注明：“姓名+单独参会”或“姓名+培训”。</p><p>（2）汇款后请及时与会务组联系确认，并向会务组说明姓名、收费标准类型、电话、衣服尺码等。另需开发票者，请向会务组提供参加人员单位和社会信用代码或纳税人识别号。</p><p>（3）通过银行账号转账的：将转款凭单电子稿通过微信或彩信形式发给程老师（微信和短信为程老师手机号15807002880）以确认到款，或者将转款凭单打印出来于报到当日拿来。</p><p>5.会务组联系方式</p><p>程老师：15807002880</p><p>徐老师：18146629443</p><p><span style=\"display: inline !important;\"><b>七 乘车路线</b></span></p><p>1、在南昌火车站（南昌站）下车的人员</p><p>（1）到达南昌火车站后从西广场南公交站乘坐高铁巴士1线到嘉言路口站下，换乘522路到岗下村站下车，路对面即是。</p><p>（2）直接乘坐出租车，到江西中医药大学湾里校区下车。</p><p>2、在南昌西站（高铁站）下车的人员</p><p>（1）乘坐522路公交车到江西中医药大学站下车，路对面即是。</p><p>（2）直接乘坐出租车，到江西中医药大学湾里校区下车。</p><p>3、乘坐飞机人员</p><p>（1）可在南昌昌北国际机场乘坐机场公交3线到南大前湖校区下车后，换乘170路到江西中医药大学站下车，路对面即是。</p><p>\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n</p><p>（2）直接乘坐出租车，到江西中医药大学湾里校区下车。</p><p><br></p>', '1', '2', '0', '1', '0', '2018-06-29 07:16:36');
INSERT INTO `ys_article` VALUES ('29', '发展繁荣大健康事业新使命 ', '1', '头条,推荐', '', '', '', '国际健康健美长寿学研究会', '郭周礼', '在国际健康健美长寿学研究会理事会上的报告', '<p>各位理事：&nbsp;\n</p><p>第十届国际健康健美长寿论坛博览会暨大健康运动会明天就要开幕了，这是十年来我们举办的规模最大、规格最高、内容最新、参加人员最多的一次会议，也是开创下一个十年新成绩新辉煌的誓师会。开幕式前，我们召开研究会理事会，主要议题有三项，一是宣布第十届理事会组成人员，二是审议《澳门承诺》，三是总结工作，展望未来，明确今后几年发展繁荣大健康事业的目标任务和线路图。现在，我作工作报告，请审议。&nbsp;</p><p>大健康事业十年成绩回顾&nbsp;\n</p><p>从2006年至今，我们共同从事的健康健美长寿事业已经走过了十二年。本来，第十届论坛博览会2016年在韩国大邱举行，因众所周知的原因，推迟到了今年。尽管会议延期，我们的事业没有停顿，一直在推进之中，各位理事做了大最工作，特别是为成功举办本次会议付出了艰苦努力，才有了今天这个好形势。&nbsp;\n</p><p>之前，我们对论坛博览会十年来的工作已经有过总结，梳理岀了六个方面的成绩和五个方面的基本经验，这里再作个简单回顾。&nbsp;\n</p><p>六个方面的成绩，概括为六句话，这就是：占据了国际健康养生领域的主导权；赢得了政府和民众信任支持；走岀了一条正确的方向和道路；取得了一大批科研学术新成果；拓展了传播交流新途径；提升了办会质量新高度。&nbsp;\n</p><p>基本经验和启示，概括为五句话：服务大局有灵魂；践行践诺有动力；政府支持有保证；以我为主有中心；集成整合有途径。&nbsp;</p><p>大健康事业新目标新任务&nbsp;\n</p><p>当今社会已经进入了新时代，顺应时代潮流，顺应广大民众对健康养生的热切期望，促进中华健康文化走向囯际，发展繁荣大健康事业，是囯际健康健美长寿学研究会今后长期的奋斗目标和主要任务。&nbsp;</p><p><span style=\"display: inline !important;\">（一），用新思想引领方向，锻造忠诚可靠魂魄。把习近平新时代中国特色社会主义思想，作为本会的根本指南，让新思想进头脑进论坛进工作，知行统一见行动。深刻理解新思想新精神要义，用新理论武器头脑，校正办会宗旨方向，在服务囯家、服务社会、服务群众、服务行业上有作为。编印岀版新论述新政策学习读本，以健康中国战略观、健康文化传播观、全球健康领域合作观、健康养生转化观等为主要内容，在传播健康文化上有作为。制定国际大健康行动纲要，组织编制今后五年线路图，在推动大健康养生走岀去走进去上有作为。&nbsp;\n</span></p><p><span style=\"display: inline !important;\">（二），紧贴宏观战略发力，全面践行《澳门承诺》。以新思想为灵魂，以构建人类命运共同体为站位，以“四个服务”为宗旨，以“一带一路”为牵引，以传播健康文化为使命，《澳门承诺》阐明了本会新时代新方略新主张，明确了发展繁荣大健康事业的指导原则和目标任务。这是继《曼谷宣言》《北京宣言》《上海宣言》之后，本会向国际社会作岀的又一个庄严承诺。践行践诺，本会将在今后的所有活动中，全要素立体式体现《澳门承诺》内涵要求。&nbsp;\n</span></p><p><span style=\"display: inline !important;\">（三），沿着一带一路布局，拓展大健康多元空间。每年一届以大健康运动会为主要内容的论的坛博览会，将继续在一带一路沿线国家和地区举办。充分借助国内国际两个大局深度互动，让国外境外民众在与中华健身文化零距离接触中亲身体验获得感，是发展繁荣大健康文化的重要举措和途径。首次大健康运动会，是除了将奥运会等全球和地区及行业竞技体育之外的、在民间传承且具有鲜明特色的健康养生项目纳入其中，通过多元化兼收并蓄，使交流平台更多元，传播时空更宽广，文明色彩更丰富。&nbsp;\n</span></p><p><span style=\"display: inline !important;\">（四），肩负起主体责任，牢牢掌握话语权。主动担当，在发展繁荣大健康事业中当好主力，做好旗手，掌好旗舰。传递健康养生技巧与传播高尚道德相融合，开展全民健康与建设全民小康相结合，互动之中讲好健康中国故事，互鉴之中彰显中国智慧力量。率先发声，将人类命运共同体、一带一路等理论与实践列入论坛交流；敢于发声，从话语权中传递思想智慧；善于发声，从主动权中传播正能量。在平等互鉴、开放包容、多方参与中，贡献中国方案。&nbsp;\n</span></p><p><span style=\"display: inline !important;\">（五）做大做强“互联网十”，分会组织覆盖全球。牵头重构大健康文化四大版图，即大健康文化协同创新中心、五大洲会员分会中心、国际大健康文化传播协同指挥中心、国际大健康文传播主力团队、国际健康产品企业家联盟中心。用三至五年时间，会员国和网络覆盖五大洲，新增分会达到50个，会员人数达到上万人。&nbsp;\n</span></p><p><span style=\"display: inline !important;\">续写大健康事业新篇章&nbsp;\n</span></p><p><span style=\"display: inline !important;\">我们已经走进了更加昂扬的新时代，大健康事业也迈进了前景广阔的第二个十年。知其所来，识其所在，明其所往，借助健康健美长寿这个全球共同语言拓展未来，以永不懈怠的精神状态和一往无前的奋斗姿态续写新篇，成为摆在本会和各位理事面前的迫切任务。&nbsp;\n</span></p><p><span style=\"display: inline !important;\">一，坚定信念，走正道路。大健康活动是公益事业，又是服务行业，把准公益方向，走正服务道路，最根本的是要把习近平中国特色社会主义思想作为旗帜和方向。深刻领会构建人类命运共同体深刻内涵，吃透一带一路方略核心要义，践行健康中国部署，积极投身中华文化走向世界伟大实践。围绕发展繁荣大健康这个中心，树牢“四个意识”，发挥好“四个作用”，把理事会建成推进大健康事业发展繁荣的协调指挥中枢。&nbsp;\n</span></p><p><span style=\"display: inline !important;\">二，以我为主，率先发声。用正能量传播健康养生，用正形象参与囯际交往，用诚信贏得政府和民众支持信任，牢牢掌握国际健康养生界话语权和主导权，使本会的特色优势更明显，更具吸引力。优秀健身养生功法只所以在国际上风声水起，走岀去与走进去，双重发力与以我为主，是最有力的证明。我们必须坚定自信，在囯际大健康舞台上站中心，唱主角，献方案，提升新位势。&nbsp;\n</span></p><p><span style=\"display: inline !important;\">三，民本至上，全球覆盖。人民大众是养生智慧的动力源泉，高人在民间，绝技在市井。走发展繁荣大健康道路，必须坚持以人民为中心和国际化大视野。以人民为中心，知其所来，明其所往，不遗余力将藏于民间、潜力无限的养生之道之法发掘岀来，用于民众；国际化大视野，兼收并蓄，将传统的、现代的、民族的、特色的养生之法之技上升为共享资源，展示岀来，推广开来，让全球70亿民众体验到大健康带来的收获感、充实感、满足感。&nbsp;\n</span></p><p><span style=\"display: inline !important;\">四，整合资源，打造名牌。扭住中心任务不放松，突出服务宗旨不离航，汇聚国际人脉优势，精心打造含金高的囯际品牌。整合理顺囯际囯内优势资源，协调与政府部门、社会团体、专业英才之间的融洽关系，构建良好的内外环境；整合升级产学研合成资源，与国际科研学术院所和企业协同创新，囯际大健康联盟、囯际健康产品联盟、香港日报集团等五个实体，组团主打拳头产品，厚实财力物力基础；强化会员囯分会横向互通互补，借助外力，借用外脑，借嘴传声，借力扬帆。&nbsp;\n</span></p><p><span style=\"display: inline !important;\">五，遵守宪法，修身自律。把宪法法律举过头顶，自觉用法律法规约束言行，事关本会命运，事关事业成败。理事会成员、分会组织负责人、功法传人，要带头遵守所在囯法律法规，自觉修身，从严律已，传业授道，为人示范，厚德载物。面对诱惑，保持清醒，面对挑战，保持定力。坚决远离黑色，不染灰色，抵制黄色，以正形象正能量做忠诚可靠、合格放心的大健康事业的实践者和推动者。&nbsp;\n</span></p><p><span style=\"display: inline !important;\">新时代要有作为，大健康要有大贡献。健康中国战略号角已经吹响，人类命运共同体和一带一路为发展繁荣大健康事业指明了方向，牢记光荣使命，不负民众期望，让我们继往开来，团结奋斗，在新征程上为人类健康事业做岀新贡献！</span></p><p><br></p>', '1', '5', '0', '1', '0', '2018-06-29 07:50:13');
INSERT INTO `ys_article` VALUES ('30', '世界大健康运动联盟章程', '2', '头条,推荐', '', '世界 大健康  运动  联盟', '', '世界大健康运动联盟', 'admin', '世界大健康运动联盟章程', '<p style=\"text-align: center;\"><span style=\"display: inline !important;\">第一章 总则</span></p><p>第一条 本组织定名为世界大健康运动联盟(以下简称联盟)。英文为WORLD BIG HEALTH SPORT UNION。总部设在中国香港。</p><p>第二条 \n本联盟的宗旨是发展繁荣大健康文化事业，促进一切有利于和有益于人类健康健美长寿文化活动的开展。中心任务是整合世界大健康文化资源，集中力量举办好一年一度的世界大健康运动会，将奥运会、洲际运动会、地区和行业运动会等竞技运动会比赛项目以外的、具有强身健体功能作用的活动囊括其中，把深藏于民间、流传于市井、喜闻乐见、潜力无限、广受群众喜爱的健身养生方式方法挖掘出来，传承下来，展示出来，推广开来，共享优秀健康健美长寿成果，造福世界民众。</p><p>本联盟所有创办发起单位必须签订联盟章程协议，同意接受章程的约束，承担章程规定的义务，同时享受章程规定的权力。</p><p style=\"text-align: center;\"><b>第二章 业务范围</b></p><p>第三条 \n世界大健康运动联盟的主要业务范围为，以举办大健康运动会为平台载体，构建一种新型的与竞技体育规则和模式有所不同的、又与强身健体目标完全一致的、倡导大众化的、汇集各国家各民族健康健美长寿文化为一体的文化传播交流平台。搭建产学研平台；建立联合研发中心，或者联合产品开发中心；与官方、国内外非盈利组织、非政府组织在健康养生文化方面的合作交流沟通；人才培训、健康知识教育服务等。</p><p>第四条 具体业务活动包括：</p><p>（一）大健康运动从广大义上包括体育运动、精神修养、心理健康、生理健复、社会文明、环境优美、道德高尚、天人合一等等。从狭义上包括，人类创造的一切有利和有益于健康健美长寿活动的功法技法方法，包括体育、舞蹈、医学、气功、武术、太极等等诸多方面。创造联盟成员之间相互尊重、互利互惠、共同发展繁荣大健康文化的环境，形成联盟成员之间的品牌互动、联合开发、合作经营、资源互补的运行机制，推进联盟内外资源互补共享和深度有效合作，建立联盟成员合作平台；</p><p>（二）举办学术论坛、专题研讨会、产品博览会等，搭建会员交流平台，为会员提供创意与健康产业相关的教育培训服务；</p><p>（三）建立网站、举办刊物、出版研究报告等形式，为社会提供非盈利性的产业数据和信息服务；</p><p>（四）联合大学、科研机构发布产业调查报告，通过公益性活动，为社会公众提供了解产业信息的机会，让社会公众了解产业发展状况，增进社会公众对不同产业发展的正确、理性评价；</p><p>（五）接受会员和政府委托，作为社会第三方机构，对产品服务评价，为会员提供机构绩效考核等非盈利性顾问咨询服务；</p><p>（六）为会员企业成立产业投资基金、私募股权基金、证券投资基金等业务提供专业性、非盈利性咨询服务。</p><p>（七）构筑创意产业集群、园区及创新空间发展平台，围绕市场需求和发展趋势，针对各联盟成员在技术、服务、市场等方面不同优势，有序组织、协调联盟成员完成创意与健康产业化及市场推广等工作；</p><p>（八）搭建世界健康国际网络和世界健康公共技术服务平台、世界健康产权交易融资平台、世界健康产业发展中介服务平台，支持各类世界健康产业机构发展，加强相互联系和沟通，使之成为文化与经济领域之间的桥梁。</p><p style=\"text-align: center;\"><b>第三章 会员</b></p><p>第五条 本联盟会员种类</p><p>世界大健康运动联盟会员分为两种类型，一是非盈利会员，主要吸收有志于从事世界大健康产业的非赢利性组织，包括大学、研究机构、民间社团、政府机构等非盈利性组织；二是盈利性会员，主要是世界大健康产业的研发单位、企业、投资机构、金融机构等。</p><p>非盈利性会员参加本联盟，需提出申请，并由该组织负责人在申请表及联盟章程上签字盖章（也可以网上签字），经联盟理事会批准通过，非盈利性会员不需要缴纳会员费用；盈利性会员单位的入会程序，需由该团体(或个人)提出申请，并由该组织(或个人)负责人在申请表及联盟章程上签字盖章(也以网上签字)，由联盟理事会批准通过，盈利性会员需要缴纳会员费用。</p><p>联盟会员同时分为机构会员和个人会员，所有个人会员均属于盈利性会员，需要缴纳一定的会员费用。</p><p>第六条 申请加入本联盟的团体会员，必须具备下列条件：</p><p>（一） 拥护本联盟章程；</p><p>（二） 有加入本联盟的意愿；</p><p>（三） 在世界大健康产业领域内具有一定的业绩和工作成果；</p><p>（四）积极参加本联盟活动，能够完成联盟成员一致同意实施的各项工作安排和任务。</p><p>第七条 会员入会程序</p><p>（一） 提交入会申请书，并在联盟章程签字盖章同意；</p><p>（二） 经联盟理事会讨论通过；</p><p>（三） 盈利性会员完成本联盟会员费的缴纳；</p><p>（四）由联盟理事会或理事会授权的机构发给会员证，会员证包括非盈利性会员证和盈利性会员证。</p><p>第八条 联盟会员享有下列权利：</p><p>（一） 本联盟的选举权、被选举权和表决权；</p><p>（二） 参加本联盟的活动；</p><p>（三） 获得本联盟服务的优先权；</p><p>（四） 对本联盟工作的批评建议权和监督权；</p><p>（五）参加联盟自愿、退出联盟自由。</p><p>第九条 联盟会员履行下列义务：</p><p>（一）执行本联盟决议；</p><p>（二）维护本联盟合法权益；</p><p>（三）完成本联盟交办的工作；</p><p>（四）盈利性组织会员需按规定交纳会费；</p><p>（五）向本联盟反映情况，提供有关资料。</p><p>（六）联盟会员有义务自愿参与联合研发中心、联合产品研发中心的发起与建设，同时有义务以创意设计、技术支持、现金和股权回报的方式，为本联盟的公共平台提供公共运作资金和技术资源，以保证联盟公共平台的不断壮大发展。涉及到会员为本联盟贡献创意设计、技术支持、现金和股权回报的数量与比例事宜，由本联盟理事会讨论，以事理会决议的方式公布，并由贡献相关创意设计、技术支持、现金和股权回报的会员单位与联盟签订捐助贡献协议正式确定。</p><p>（七）保护本联盟的商业秘密和知识产权，不参与从事危害本联盟权益的活动。联盟会员在退出联盟后的五年内，不得以从本联盟获取的商业机密、创意设计、技术诀窍、专利知识谋求商业利益，也不得将本联盟的商业机密、技术诀窍、专利知识泄露给联盟以外的机构。违反本章程者，联盟有权力诉诸法律手段，追究其相关法律责任。</p><p>第十条 \n联盟会员退出时应书面通知本联盟，并交回会员证。盈利性会员超过一年不交纳会费或不参加本联盟活动的，视为自动退费；非盈利性会员超过一年不参加本联盟活动的，视为自动退会。</p><p>第十一条 \n联盟会员如有严重违反本章程的行为，经理事会表决通过，予以除名。如违反国家有关法律法规、或严重侵害其他会员权益的，由联盟依法向司法机构提起诉讼。</p><p style=\"text-align: center;\"><b>第四章 组织机构和负责人产生、罢免</b></p><p>第十二条 本联盟的最高权力机构是联盟会员大会，联盟会员大会的职权是：</p><p>（一）制定和修改章程；</p><p>（二）选举和罢免理事；</p><p>（三）审议理事会工作报告和财务报告；</p><p>（四）决定终止事项；</p><p>（五）决定其他重大事宜。</p><p>第十三条 联盟会员大会须有2 / 3以上会员出席方能召开，其决议须经到会会员半数以上表决同意，方能生效。</p><p>第十四条 会员大会每届两年。因特殊情况需要提前或延期换届时，须经理事会表决通过。</p><p>第十五条 理事会是联盟会员大会的执行机构，在闭会期间负责本团体日常工作，对会员大会负责。</p><p>第十六条 理事会的职权是：</p><p>（一）执行会员大会决议；</p><p>（二）选举和罢免联盟主席、秘书长；</p><p>（三）筹备召开会员大会；</p><p>（四）向会员大会报告工作和财务状况；</p><p>（五）决定会员的吸收和除名；</p><p>（六）决定设立办事机构、分支机构、代表机构和实体机构；</p><p>（七）批准副秘书长、各机构主要负责人的聘任；</p><p>（八）领导本联盟各机构开展工作；</p><p>（九） 制定内部管理制度；</p><p>（十）决定其他重大事项。</p><p>第十七条 理事会须有2 / 3以上理事出席方能召开，其决议须经到会理事2 / 3以上表决同意，通过后方能生效。</p><p>第十八条 理事会每年至少召开一次会议；情况特殊时，也可采取通讯形式召开。</p><p>第十九条 本团体理事人数超过50名时，可设立常务理事会，常务理事人数不超过理事人数的1 / \n3。常务理事会由理事会选举产生，在理事会闭会期间对理事会负责。</p><p>第二十条 常务理事会须有2 / 3以上常务理事出席方能召开，其决议须经到会常务理事</p><p>2 / 3以上表决同意，通过后方能生效。</p><p>第二十一条 常务理事会至少半年召开一次会议；情况特殊的也可采用通讯形式召开。</p><p>第二十二条 本理事会最高领导为理事会主席，由联盟机构会员负责人轮流担任，秘书长由理事会选举任命。</p><p>第二十三条 本联盟主席、秘书长必须具备下列条件：</p><p>（一） 坚持党的路线、方针、政策，政治可靠；</p><p>（二） 在本团体业务领域内有较大影响；</p><p>（三） 联盟主席、秘书长最高任职年龄不超过75岁，秘书长为专职；</p><p>（四） 身体健康，能坚持正常工作；</p><p>（五） 未受过剥夺政治权利的刑事处罚者；</p><p>（六） 具有完全民事行为能力。</p><p>第二十四条 本联盟主席每任任期为一年。秘书长由理事会选举任命，可以长期担任。</p><p>第二十五条 本联盟主席行使下列职权:</p><p>（一） 召集和主持理事会（或常务理事会）；</p><p>（二） 检查会员大会（或会员代表会）、理事会（或常务理事会）决议的落实情况；</p><p>（三） 处理其他日常事务。</p><p>第二十六条 本联盟秘书长行使下列职权：</p><p>（一） 在联盟主席领导下主持办事机构开展日常工作，组织实施年度工作计划；</p><p>（二） 协调各分支机构、代表机构、实体机构开展工作；</p><p>（三） 提名副秘书长以及各办事机构、分支机构、代表机构和实体机构主要负责人，交理事会或常务理事会决定。</p><p>（四） 决定办事机构、代表机构、实体机构专职工作人员的聘用；</p><p>（五） 代表本团体签署有关重要文件；</p><p>（六） 处理其他日常事务。</p><p style=\"text-align: center;\"><b>第五章资产管理、使用原则</b></p><p>第二十七条 本联盟团体经费来源：</p><p>（一） 会费；</p><p>（二） 会员捐赠；</p><p>（三） 政府资助；</p><p>（四） 在国家许可业务范围内开展活动或服务性收入；</p><p>（五） 利息；</p><p>（六） 国家法律许可的其他合法收入。</p><p>第二十八条 本联盟按照国家有关规定收取会员会费。</p><p>第二十九条 本联盟经费必须用于本章程规定的业务范围和事业的发展，不得在会员中分配。</p><p>第三十条 本联盟建立严格的财务管理制度，保证会计资料合法、真实、准确、完整。</p><p>第三十一条 \n本联盟配备有专业资格的会计人员。会计不得兼任出纳。会计人员必须进行会计核算，实行会计监督。会计人员调动工作或离职时，必须与接管人员办理清交接手续。</p><p>第三十二条 \n本联盟的资产管理必须执行国家规定的财务管理制度，接受会员大会（或会员代表大会）和财务部门的监督。资产来源属于国家拨款或者社会捐赠、资助的，必须接受审计机关的监督，并将有关情况以适当方式向社会公布。</p><p>第三十三条 本联盟的资产，任何单位、个人不得侵占、私分和挪用。</p><p>第三十四条 本联盟专职工作人员的工资和保险、福利待遇，参照国家对事业单位的有关规定执行。</p><p style=\"text-align: center;\"><b>第六章 章程的修改程序</b></p><p>第三十五条 对本联盟章程的修改，须经理事会表决后报会员大会审议，经法定到会会员半数以上表决同意通过，方能生效。</p><p>第三十六条 本联盟章程的修改，须在会员大会通过后，需经法定到会会员半数以上在章程上签字同意，以会员协议的方式形成。</p><p style=\"text-align: center;\"><b>第七章 终止程序及终止后财产处理</b></p><p>第三十七条 本联盟完成宗旨或自行解散或由于分立、合并等原因需要注销时，由理事会或常务理事会提出终止动议。</p><p>第三十八条 本联盟终止动议须经会员大会表决通过，经法定到会会员半数以上表决同意通过，方能生效。</p><p>第三十九条 本联盟终止前，须由联盟理事会组成清算小组，清理债权债务，处理善后事宜。清算期间，不开展清算以外的活动。</p><p>第四十条 本联盟终止后的财产，经会员大会表决同意，按照国家有关规定，用于发展本联盟宗旨相关的事业。</p><p style=\"text-align: center;\"><b>第八章 附则</b></p><p>第四十一条 本章程经2018年 4月 20 日由所有发起创办单位签字盖章同意通过。</p><p>第四十二条 本章程的解释权属于本联盟团体理事会。</p><p>第四十三条 本章程自所有发起创办单位签字盖章之日起生效。</p><p>\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n</p><p style=\"text-align: right;\">2018年4月20日</p><p><br></p>', '1', '7', '0', '1', '0', '2018-06-29 08:12:04');
INSERT INTO `ys_article` VALUES ('32', '澳门承诺', '1', '头条,推荐,加粗', '', '', '', '国际健康健美长寿学研究会', 'admin', '澳门承诺', '<p style=\"text-align: center;\">世界大健康运动联盟 国际健康健美长寿学研究会</p><p style=\"text-align: center;\">澳门承诺</p><p>高举科学旗帜，践行核心价值。以人民健康为中心，以构建人类命运共同体为牵引，爱国爱民，创新、协调、绿色、开放、共享，服务国家，服务社会，服务群众，服务行业。</p><p>传播健康文化，贡献养生真知。弘扬传统，敬重生命，科学至上，传递真知，崇尚美德，平等互鉴，开放包容，兼收并蓄，保护环境，绿色低碳，清洁美丽，增进民心相通。</p><p>汇聚优势资源，促进发展繁荣。肩负主责，整合人脉，活跃科研学术，做强互联互通，培植主力团队，打造名优品牌，用正能量普及健康养生，用正形象参与国际交流。</p><p>\n\n\n\n\n</p><p>遵守法律法规，从严修身自律。遵守宪法，自觉守法，率先垂范，热心公益，立言立行，远离恐暴，杜绝黑恶，不染黄毒，反对迷信，与真理同向，与政府同心，与民众同行。</p><p><br></p>', '1', '3', '0', '1', '0', '2018-06-29 08:22:49');
INSERT INTO `ys_article` VALUES ('33', '用太极打出人生新高地', '0', '推荐', '', '', '/static/upload/20180629-16-33-38_18404_5b336e6d8b8d4 (1).jpg', '网络', '朱修林', '当代太极拳名家、全国武术太极拳冠军、香港全港公开太极拳锦标赛冠军，香港太极拳研究会永远名誉会长、香港中华内家拳总会名誉顾问、香港陈式太极拳科研理事、香港冷先锋武术学院院长兼总教练、深圳市冷先锋太极养生馆馆长兼总教练冷先锋', '<p>他自幼习武，27岁夺得国际大赛冠军，先后在国际、国内大型武术比赛中获得太极拳、太极剑、推手冠军，在多项集体和个人比赛中获得冠军及金牌300多枚，弟子遍及世界各地。当代太极拳名家、全国武术太极拳冠军、香港全港公开太极拳锦标赛冠军，香港太极拳研究会永远名誉会长、香港中华内家拳总会名誉顾问、香港陈式太极拳科研理事、香港冷先锋武术学院院长兼总教练、深圳市冷先锋太极养生馆馆长兼总教练冷先锋——</p><p style=\"text-align: center;\"><img src=\"/static/upload/20180629-16-30-56_10043_5b336e6d8b8d4 (1).jpg\" style=\"max-width:100%;\"><br></p><p align=\"center\" style=\"text-align: left;\"><span style=\"display: inline !important;\">6月18日至20日,以“传承中华文化，弘扬太极精髓”为主题的首届世界太极拳交流大会在香港举行，来自美国、加拿大、日本、印度尼西亚、马来西亚等10多个国家和地区的数十支代表队3000多名太极拳名家及爱好者参会。太极名家陈小旺、陈世通、王培琨、李德印、游玄德等，著名太极文化学者、世界太极拳网总编余功保，功夫影视明星梁小龙，武术冠军阮丹萍、宗巧珍、张龙等参加交流大会。此次交流大会由当代太极拳名家、全国武术太极拳冠军、修水人冷先锋发起，香港先锋太极学会主办。三天时间，大家以武会友，相互学习，为弘扬中华武术，传承中华文化产生了积极的推动作用。</span></p><p>十七年前，年仅27岁的冷先锋参加珠海国际太极拳交流大会，一人获得8枚奖牌，其中金牌2枚。十七年来，冷先锋离开家乡前往深圳,在太极的路上一路坚持，辛勤耕耘。</p><p>今年四月初，受市委、市政府邀请，冷先锋回九江参加“浔商回家”2018春季茶叙会。其间，他与来自全国各地的九江籍商界精英代表欢聚一堂，共话桑梓，共叙乡情，共谋发展，受到市委副书记、市政府代市长谢一平的亲切接见。会议结束后，冷先锋抽空回家。在君豪大酒店，记者见到了阔别十七年之久的冷先锋，他依旧是那么的神采奕奕，虎虎生威。</p><p>冷先锋师从太极拳发源地中国河南省陈家沟第十八代正宗传人、国家非物质文化遗产传承人、国际太极拳大师陈世通，以及原中国国家武术队总教练、世界太极拳冠军、太极王子王二平，擅长陈式、杨式、吴式、武式、孙式太极拳、太极剑、太极推手等。2002年底，载誉归来不久的冷先锋怀揣着梦想，南下深圳发展。“那个时候并不知道什么是机遇与挑战，带着比赛获得的4000元奖金，凭着热情和想象去闯荡世界，自然也碰了一鼻子的灰。”冷先锋说。</p><p>原本想在深圳开办一家武馆，收收徒弟练习太极，然而没有人相信他。要想获得大家认可，首先就得让大家知道自己。为此，每天早晚，冷先锋都要到公园和小区，免费为那些太极拳爱好者传授技艺，扩大影响。“行家一出手，便知有没有。”这期间不断有师傅前来挑战他，试试他的功夫，均被他深厚的功底、过硬的功夫折服。</p><p>初到深圳的几年时间，冷先锋还参加全国武术大赛太极拳比赛，先后获得几项冠军，影响较大。慢慢地，大家认可了这个从江西修水远道而来的师傅，不仅是深圳的太极拳爱好者前来他处学习太极，而且还有广东其他城市的爱好者慕名前来拜师学艺，特别是临近香港的爱好者前来学习。</p><p>“是鸟儿总要高飞的。”随着自己名气越来越大，身边集聚的太极爱好者越来越多，冷先锋当初的梦想再一次点燃。2005年初，他投资100多万元，租赁300平方米办公和活动场所，创办“冷先锋太极馆”，先后聘请太极教练20余名，招收弟子1000余名。通过不断的学习交流比赛，冷先锋在深圳、香港等地影响越来越大。2008年，通过“香港优秀人才入境计划”，获批移居香港。</p><p>在香港，他投资1000多万元创办“香港冷先锋武术学院”，招收徒弟1000多名。他潜心钻研，遍访国内外名师高手，把中国古典哲学、医学、生理学、养生学、儒家、道家思想等融进太极拳学术里，其武学修为已臻入佳境，太极拳、器械、推手、套路自成一格，沉稳洒脱，在行内独树一帜。他本人以及弟子多次在国内外各种武术大赛中夺冠。冷先锋在世界各地太极领域反响很大，每年他都要到很多国家进行授课、交流，尤其是日本、印度尼西亚、马来西亚等国家，他不仅能够熟练的掌握香港、深圳本地方言，还能够用英语、日语、印尼语授课。</p><p>近年来，在弘扬武术，强身健体，发扬光大的过程中，冷先锋还把目光瞄准服装鞋帽、太极器械等太极产业，注册“香港冷先锋武术学院有限公司”和“深圳市先锋体育发展有限公司”，集专业太极武术培训、太极武术用品开发为一体，下设武术服装厂、冷兵器生产厂。先后新加坡、香港、广州、深圳等地开办10多家专卖店，每年产值超过亿元。去年元旦，冷先锋还以“先锋太极”品牌正式启动企业上市工作。</p><p>为让太极拳这一传统文化发扬光大，让越来越多的武术爱好者认识太极、走近太极、爱上太极，他不断努力、不断创新，出版发行《陈式太极拳竞赛套路》和《二十四式简化太极拳》两本专著和一系列传统、竞赛套路中英文DVD教学片，自己还被国内外几十家团体机构聘为荣誉顾问、总教练。冷先锋的优秀事迹已编入《中国太极名人词典》《太极之光》《精武百杰》《当代艺术家文学集》《深圳名人录》《香港优秀人才》《特区少年》《荣誉杂志》等重要典籍。</p><p>\n\n\n\n\n\n\n\n\n\n\n\n\n</p><p>虽然拥有全国太极拳冠军的盛誉，冷先锋并无大师架子，处处彰显大师风范，深得太极爱好者的信耐和尊重。长期以来，冷先锋坚持在一线从事专业的武术太极拳教学，开设十几间武术太极拳产品专卖店和高级太极养生馆，致力于用太极打出人生新高度，打造一流的“冷先锋”品牌，将这一民族体育推向全世界。</p><p><br></p>', '1', '1', '0', '1', '0', '2018-06-29 08:25:57');
INSERT INTO `ys_article` VALUES ('34', '用太极打出人生新高地', '6', '推荐,头条', '', '', '/static/upload/20180629-16-42-22_13099_5b336e6d8b8d4.jpg', '网络', '朱修林', '', '<p>欢<span style=\"display: inline !important;\">他自幼习武，27岁夺得国际大赛冠军，先后在国际、国内大型武术比赛中获得太极拳、太极剑、推手冠军，在多项集体和个人比赛中获得冠军及金牌300多枚，弟子遍及世界各地。当代太极拳名家、全国武术太极拳冠军、香港全港公开太极拳锦标赛冠军，香港太极拳研究会永远名誉会长、香港中华内家拳总会名誉顾问、香港陈式太极拳科研理事、香港冷先锋武术学院院长兼总教练、深圳市冷先锋太极养生馆馆长兼总教练冷先锋——</span></p><p style=\"text-align: center;\"><span style=\"display: inline !important;\"><img src=\"/static/upload/20180629-16-39-46_14839_5b336e6d8b8d4.jpg\" style=\"max-width:100%;\"><br></span></p>\n<p align=\"center\" style=\"text-align: left;\"><span style=\"display: inline !important;\">6月18日至20日,以“传承中华文化，弘扬太极精髓”为主题的首届世界太极拳交流大会在香港举行，来自美国、加拿大、日本、印度尼西亚、马来西亚等10多个国家和地区的数十支代表队3000多名太极拳名家及爱好者参会。太极名家陈小旺、陈世通、王培琨、李德印、游玄德等，著名太极文化学者、世界太极拳网总编余功保，功夫影视明星梁小龙，武术冠军阮丹萍、宗巧珍、张龙等参加交流大会。此次交流大会由当代太极拳名家、全国武术太极拳冠军、修水人冷先锋发起，香港先锋太极学会主办。三天时间，大家以武会友，相互学习，为弘扬中华武术，传承中华文化产生了积极的推动作用。</span></p>\n<p>十七年前，年仅27岁的冷先锋参加珠海国际太极拳交流大会，一人获得8枚奖牌，其中金牌2枚。十七年来，冷先锋离开家乡前往深圳,在太极的路上一路坚持，辛勤耕耘。</p>\n<p>今年四月初，受市委、市政府邀请，冷先锋回九江参加“浔商回家”2018春季茶叙会。其间，他与来自全国各地的九江籍商界精英代表欢聚一堂，共话桑梓，共叙乡情，共谋发展，受到市委副书记、市政府代市长谢一平的亲切接见。会议结束后，冷先锋抽空回家。在君豪大酒店，记者见到了阔别十七年之久的冷先锋，他依旧是那么的神采奕奕，虎虎生威。</p>\n<p>冷先锋师从太极拳发源地中国河南省陈家沟第十八代正宗传人、国家非物质文化遗产传承人、国际太极拳大师陈世通，以及原中国国家武术队总教练、世界太极拳冠军、太极王子王二平，擅长陈式、杨式、吴式、武式、孙式太极拳、太极剑、太极推手等。2002年底，载誉归来不久的冷先锋怀揣着梦想，南下深圳发展。“那个时候并不知道什么是机遇与挑战，带着比赛获得的4000元奖金，凭着热情和想象去闯荡世界，自然也碰了一鼻子的灰。”冷先锋说。</p>\n<p>原本想在深圳开办一家武馆，收收徒弟练习太极，然而没有人相信他。要想获得大家认可，首先就得让大家知道自己。为此，每天早晚，冷先锋都要到公园和小区，免费为那些太极拳爱好者传授技艺，扩大影响。“行家一出手，便知有没有。”这期间不断有师傅前来挑战他，试试他的功夫，均被他深厚的功底、过硬的功夫折服。</p>\n<p>初到深圳的几年时间，冷先锋还参加全国武术大赛太极拳比赛，先后获得几项冠军，影响较大。慢慢地，大家认可了这个从江西修水远道而来的师傅，不仅是深圳的太极拳爱好者前来他处学习太极，而且还有广东其他城市的爱好者慕名前来拜师学艺，特别是临近香港的爱好者前来学习。</p>\n<p>“是鸟儿总要高飞的。”随着自己名气越来越大，身边集聚的太极爱好者越来越多，冷先锋当初的梦想再一次点燃。2005年初，他投资100多万元，租赁300平方米办公和活动场所，创办“冷先锋太极馆”，先后聘请太极教练20余名，招收弟子1000余名。通过不断的学习交流比赛，冷先锋在深圳、香港等地影响越来越大。2008年，通过“香港优秀人才入境计划”，获批移居香港。</p>\n<p>在香港，他投资1000多万元创办“香港冷先锋武术学院”，招收徒弟1000多名。他潜心钻研，遍访国内外名师高手，把中国古典哲学、医学、生理学、养生学、儒家、道家思想等融进太极拳学术里，其武学修为已臻入佳境，太极拳、器械、推手、套路自成一格，沉稳洒脱，在行内独树一帜。他本人以及弟子多次在国内外各种武术大赛中夺冠。冷先锋在世界各地太极领域反响很大，每年他都要到很多国家进行授课、交流，尤其是日本、印度尼西亚、马来西亚等国家，他不仅能够熟练的掌握香港、深圳本地方言，还能够用英语、日语、印尼语授课。</p>\n<p>近年来，在弘扬武术，强身健体，发扬光大的过程中，冷先锋还把目光瞄准服装鞋帽、太极器械等太极产业，注册“香港冷先锋武术学院有限公司”和“深圳市先锋体育发展有限公司”，集专业太极武术培训、太极武术用品开发为一体，下设武术服装厂、冷兵器生产厂。先后新加坡、香港、广州、深圳等地开办10多家专卖店，每年产值超过亿元。去年元旦，冷先锋还以“先锋太极”品牌正式启动企业上市工作。</p>\n<p>为让太极拳这一传统文化发扬光大，让越来越多的武术爱好者认识太极、走近太极、爱上太极，他不断努力、不断创新，出版发行《陈式太极拳竞赛套路》和《二十四式简化太极拳》两本专著和一系列传统、竞赛套路中英文DVD教学片，自己还被国内外几十家团体机构聘为荣誉顾问、总教练。冷先锋的优秀事迹已编入《中国太极名人词典》《太极之光》《精武百杰》《当代艺术家文学集》《深圳名人录》《香港优秀人才》《特区少年》《荣誉杂志》等重要典籍。</p>\n<p>虽然拥有全国太极拳冠军的盛誉，冷先锋并无大师架子，处处彰显大师风范，深得太极爱好者的信耐和尊重。长期以来，冷先锋坚持在一线从事专业的武术太极拳教学，开设十几间武术太极拳产品专卖店和高级太极养生馆，致力于用太极打出人生新高度，打造一流的“冷先锋”品牌，将这一民族体育推向全世界。</p><p><br></p>', '1', '10', '0', '1', '0', '2018-06-29 08:35:17');
INSERT INTO `ys_article` VALUES ('35', '首届世界太极拳交流大会在香港举行', '1', '推荐', '', '首届 太极拳 交流大会 香港', '/static/upload/20180629-17-02-00_10954_37ce620e8a5044789671c4b7160ae157.jpg', '搜狐', 'admin', '2018年6月18日-20日, 首届世界太极拳交流大会在香港伊丽莎白体育馆举行。', '<p pingfang=\"\" sc\',=\"\" arial,=\"\" 微软雅黑,=\"\" 宋体,=\"\" simsun,=\"\" sans-serif;=\"\" counter-reset:=\"\" list-1=\"\" 0=\"\" list-2=\"\" list-3=\"\" list-4=\"\" list-5=\"\" list-6=\"\" list-7=\"\" list-8=\"\" list-9=\"\" 0;=\"\" white-space:=\"\" normal;=\"\" orphans:=\"\" 2;=\"\" letter-spacing:=\"\" color:=\"\" rgb(25,25,25);=\"\" font-size:=\"\" 16px;=\"\" border-top:=\"\" 0px;=\"\" font-weight:=\"\" border-right:=\"\" word-spacing:=\"\" padding-top:=\"\" font-variant-ligatures:=\"\" font-variant-caps:=\"\" -webkit-text-stroke-width:=\"\" 0px\"=\"\">东方之珠光耀璀璨，功夫国学润泽四海，香港这座现代化的美丽城市，近年来成为中华武术传播的重要“港口”，如今，又增添了一个新的赛事。</p><p pingfang=\"\" sc\',=\"\" arial,=\"\" 微软雅黑,=\"\" 宋体,=\"\" simsun,=\"\" sans-serif;=\"\" counter-reset:=\"\" list-1=\"\" 0=\"\" list-2=\"\" list-3=\"\" list-4=\"\" list-5=\"\" list-6=\"\" list-7=\"\" list-8=\"\" list-9=\"\" 0;=\"\" white-space:=\"\" normal;=\"\" orphans:=\"\" 2;=\"\" letter-spacing:=\"\" color:=\"\" rgb(25,25,25);=\"\" font-size:=\"\" 16px;=\"\" border-top:=\"\" 0px;=\"\" font-weight:=\"\" border-right:=\"\" word-spacing:=\"\" padding-top:=\"\" font-variant-ligatures:=\"\" font-variant-caps:=\"\" -webkit-text-stroke-width:=\"\" 0px\"=\"\"><span style=\"display: inline !important;\">2018</span><span style=\"display: inline !important;\">年</span><span style=\"display: inline !important;\">6</span><span style=\"display: inline !important;\">月</span><span style=\"display: inline !important;\">18</span><span style=\"display: inline !important;\">日</span><span style=\"display: inline !important;\">-20</span><span style=\"display: inline !important;\">日</span><span style=\"display: inline !important;\">,</span><span style=\"display: inline !important;\">&nbsp;</span><span style=\"display: inline !important;\">首届世界太极拳交流大会在香港伊丽莎白体育馆举行。本次活动以“传承中华文化，弘扬太极精髓”为主题，由著名太极拳冠军冷先锋发起，香港先锋太极学会主办。</span></p><p pingfang=\"\" sc\',=\"\" arial,=\"\" 微软雅黑,=\"\" 宋体,=\"\" simsun,=\"\" sans-serif;=\"\" counter-reset:=\"\" list-1=\"\" 0=\"\" list-2=\"\" list-3=\"\" list-4=\"\" list-5=\"\" list-6=\"\" list-7=\"\" list-8=\"\" list-9=\"\" 0;=\"\" white-space:=\"\" normal;=\"\" orphans:=\"\" 2;=\"\" letter-spacing:=\"\" color:=\"\" rgb(25,25,25);=\"\" font-size:=\"\" 16px;=\"\" border-top:=\"\" 0px;=\"\" font-weight:=\"\" border-right:=\"\" word-spacing:=\"\" padding-top:=\"\" font-variant-ligatures:=\"\" font-variant-caps:=\"\" -webkit-text-stroke-width:=\"\" 0px\"=\"\" style=\"text-align: center;\"><img src=\"/static/upload/20180629-16-56-02_17013_93f568e925f3454f9354f277208dd6fe.jpg\" style=\"max-width:100%;\" class=\"\"><br></p><p pingfang=\"\" sc\',=\"\" arial,=\"\" 微软雅黑,=\"\" 宋体,=\"\" simsun,=\"\" sans-serif;=\"\" counter-reset:=\"\" list-1=\"\" 0=\"\" list-2=\"\" list-3=\"\" list-4=\"\" list-5=\"\" list-6=\"\" list-7=\"\" list-8=\"\" list-9=\"\" 0;=\"\" white-space:=\"\" normal;=\"\" orphans:=\"\" 2;=\"\" letter-spacing:=\"\" color:=\"\" rgb(25,25,25);=\"\" font-size:=\"\" 16px;=\"\" border-top:=\"\" 0px;=\"\" font-weight:=\"\" border-right:=\"\" word-spacing:=\"\" padding-top:=\"\" font-variant-ligatures:=\"\" font-variant-caps:=\"\" -webkit-text-stroke-width:=\"\" 0px\"=\"\" style=\"text-align: center;\"><span data-filtered=\"filtered\"></span>冷先锋在开幕式致辞</p><p pingfang=\"\" sc\',=\"\" arial,=\"\" 微软雅黑,=\"\" 宋体,=\"\" simsun,=\"\" sans-serif;=\"\" counter-reset:=\"\" list-1=\"\" 0=\"\" list-2=\"\" list-3=\"\" list-4=\"\" list-5=\"\" list-6=\"\" list-7=\"\" list-8=\"\" list-9=\"\" 0;=\"\" white-space:=\"\" normal;=\"\" orphans:=\"\" 2;=\"\" letter-spacing:=\"\" color:=\"\" rgb(25,25,25);=\"\" font-size:=\"\" 16px;=\"\" border-top:=\"\" 0px;=\"\" font-weight:=\"\" border-right:=\"\" word-spacing:=\"\" padding-top:=\"\" font-variant-ligatures:=\"\" font-variant-caps:=\"\" -webkit-text-stroke-width:=\"\" 0px\"=\"\">来自世界各地的数十支代表队参会，太极名家陈小旺、陈世通、王培琨、李德印、游玄德、杨志芳、穆秀杰、李文钦、戈春艳、傅清泉、闫素杰、黄鑫、赵海鑫、陈军团、朱学峰，功夫影视明星梁小龙，武术冠军阮丹萍、宗巧珍、张龙等参加了交流大会。</p><p pingfang=\"\" sc\',=\"\" arial,=\"\" 微软雅黑,=\"\" 宋体,=\"\" simsun,=\"\" sans-serif;=\"\" counter-reset:=\"\" list-1=\"\" 0=\"\" list-2=\"\" list-3=\"\" list-4=\"\" list-5=\"\" list-6=\"\" list-7=\"\" list-8=\"\" list-9=\"\" 0;=\"\" white-space:=\"\" normal;=\"\" orphans:=\"\" 2;=\"\" letter-spacing:=\"\" color:=\"\" rgb(25,25,25);=\"\" font-size:=\"\" 16px;=\"\" border-top:=\"\" 0px;=\"\" font-weight:=\"\" border-right:=\"\" word-spacing:=\"\" padding-top:=\"\" font-variant-ligatures:=\"\" font-variant-caps:=\"\" -webkit-text-stroke-width:=\"\" 0px\"=\"\">著名太极文化学者、世界太极拳网总编余功保先生应邀出席活动。</p><p pingfang=\"\" sc\',=\"\" arial,=\"\" 微软雅黑,=\"\" 宋体,=\"\" simsun,=\"\" sans-serif;=\"\" counter-reset:=\"\" list-1=\"\" 0=\"\" list-2=\"\" list-3=\"\" list-4=\"\" list-5=\"\" list-6=\"\" list-7=\"\" list-8=\"\" list-9=\"\" 0;=\"\" white-space:=\"\" normal;=\"\" orphans:=\"\" 2;=\"\" letter-spacing:=\"\" color:=\"\" rgb(25,25,25);=\"\" font-size:=\"\" 16px;=\"\" border-top:=\"\" 0px;=\"\" font-weight:=\"\" border-right:=\"\" word-spacing:=\"\" padding-top:=\"\" font-variant-ligatures:=\"\" font-variant-caps:=\"\" -webkit-text-stroke-width:=\"\" 0px\"=\"\">【众多武术界名流与会】：</p><p pingfang=\"\" sc\',=\"\" arial,=\"\" 微软雅黑,=\"\" 宋体,=\"\" simsun,=\"\" sans-serif;=\"\" counter-reset:=\"\" list-1=\"\" 0=\"\" list-2=\"\" list-3=\"\" list-4=\"\" list-5=\"\" list-6=\"\" list-7=\"\" list-8=\"\" list-9=\"\" 0;=\"\" white-space:=\"\" normal;=\"\" orphans:=\"\" 2;=\"\" letter-spacing:=\"\" color:=\"\" rgb(25,25,25);=\"\" font-size:=\"\" 16px;=\"\" border-top:=\"\" 0px;=\"\" font-weight:=\"\" border-right:=\"\" word-spacing:=\"\" padding-top:=\"\" font-variant-ligatures:=\"\" font-variant-caps:=\"\" -webkit-text-stroke-width:=\"\" 0px\"=\"\" style=\"text-align: center;\"><img src=\"/static/upload/20180629-16-56-23_10628_a959dcb8077845939aa3c1611f61752c.jpg\" style=\"max-width:100%;\"><br></p><p pingfang=\"\" sc\',=\"\" arial,=\"\" 微软雅黑,=\"\" 宋体,=\"\" simsun,=\"\" sans-serif;=\"\" counter-reset:=\"\" list-1=\"\" 0=\"\" list-2=\"\" list-3=\"\" list-4=\"\" list-5=\"\" list-6=\"\" list-7=\"\" list-8=\"\" list-9=\"\" 0;=\"\" white-space:=\"\" normal;=\"\" orphans:=\"\" 2;=\"\" letter-spacing:=\"\" color:=\"\" rgb(25,25,25);=\"\" font-size:=\"\" 16px;=\"\" border-top:=\"\" 0px;=\"\" font-weight:=\"\" border-right:=\"\" word-spacing:=\"\" padding-top:=\"\" font-variant-ligatures:=\"\" font-variant-caps:=\"\" -webkit-text-stroke-width:=\"\" 0px\"=\"\" style=\"text-align: center;\"><img src=\"/static/upload/20180629-16-56-50_10823_37ce620e8a5044789671c4b7160ae157.jpg\" style=\"max-width:100%;\"><br></p><p style=\"text-align: center;\"><img src=\"http://47.92.75.233/static/upload/20180629-16-46-36_16319_%E5%BE%AE%E4%BF%A1%E5%9B%BE%E7%89%87_20180625140235%E5%89%AF%E6%9C%AC.jpg\" box-sizing:=\"\" border-box;=\"\" border:=\"\" 0px;=\"\" display:=\"\" inline-block;=\"\" padding:=\"\" margin:=\"\" cursor:=\"\" pointer;=\"\" box-shadow:=\"\" rgb(51,=\"\" 51,=\"\" 51)=\"\" 0px=\"\" 5px;=\"\" max-width:=\"\" 100%;\"=\"\" class=\"\"><br></p><p style=\"text-align: center;\">陈小旺先生为大会题写会名</p><p pingfang=\"\" sc\',=\"\" arial,=\"\" 微软雅黑,=\"\" 宋体,=\"\" simsun,=\"\" sans-serif;=\"\" counter-reset:=\"\" list-1=\"\" 0=\"\" list-2=\"\" list-3=\"\" list-4=\"\" list-5=\"\" list-6=\"\" list-7=\"\" list-8=\"\" list-9=\"\" 0;=\"\" white-space:=\"\" normal;=\"\" orphans:=\"\" 2;=\"\" letter-spacing:=\"\" color:=\"\" rgb(25,25,25);=\"\" font-size:=\"\" 16px;=\"\" border-top:=\"\" 0px;=\"\" font-weight:=\"\" border-right:=\"\" word-spacing:=\"\" padding-top:=\"\" font-variant-ligatures:=\"\" font-variant-caps:=\"\" -webkit-text-stroke-width:=\"\" 0px\"=\"\" style=\"text-align: center;\"><img src=\"/static/upload/20180629-16-57-43_10019_微信图片_20180625140258副本.jpg\" style=\"max-width:100%;\"><br></p><p pingfang=\"\" sc\',=\"\" arial,=\"\" 微软雅黑,=\"\" 宋体,=\"\" simsun,=\"\" sans-serif;=\"\" counter-reset:=\"\" list-1=\"\" 0=\"\" list-2=\"\" list-3=\"\" list-4=\"\" list-5=\"\" list-6=\"\" list-7=\"\" list-8=\"\" list-9=\"\" 0;=\"\" white-space:=\"\" normal;=\"\" orphans:=\"\" 2;=\"\" letter-spacing:=\"\" color:=\"\" rgb(25,25,25);=\"\" font-size:=\"\" 16px;=\"\" border-top:=\"\" 0px;=\"\" font-weight:=\"\" border-right:=\"\" word-spacing:=\"\" padding-top:=\"\" font-variant-ligatures:=\"\" font-variant-caps:=\"\" -webkit-text-stroke-width:=\"\" 0px\"=\"\">赛事发起人冷先锋先生表示，通过这一赛事活动，力争打造一个太极拳的国际化共享传承的中国平台，让各国在传承中创新，在共享中发扬。</p><p pingfang=\"\" sc\',=\"\" arial,=\"\" 微软雅黑,=\"\" 宋体,=\"\" simsun,=\"\" sans-serif;=\"\" counter-reset:=\"\" list-1=\"\" 0=\"\" list-2=\"\" list-3=\"\" list-4=\"\" list-5=\"\" list-6=\"\" list-7=\"\" list-8=\"\" list-9=\"\" 0;=\"\" white-space:=\"\" normal;=\"\" orphans:=\"\" 2;=\"\" letter-spacing:=\"\" color:=\"\" rgb(25,25,25);=\"\" font-size:=\"\" 16px;=\"\" border-top:=\"\" 0px;=\"\" font-weight:=\"\" border-right:=\"\" word-spacing:=\"\" padding-top:=\"\" font-variant-ligatures:=\"\" font-variant-caps:=\"\" -webkit-text-stroke-width:=\"\" 0px\"=\"\" style=\"text-align: center;\"><img src=\"/static/upload/20180629-16-58-14_18048_b0dc5fde91bf4dd58c2ecee03eedd39c.jpg\" style=\"max-width:100%;\"><br></p><p pingfang=\"\" sc\',=\"\" arial,=\"\" 微软雅黑,=\"\" 宋体,=\"\" simsun,=\"\" sans-serif;=\"\" counter-reset:=\"\" list-1=\"\" 0=\"\" list-2=\"\" list-3=\"\" list-4=\"\" list-5=\"\" list-6=\"\" list-7=\"\" list-8=\"\" list-9=\"\" 0;=\"\" white-space:=\"\" normal;=\"\" orphans:=\"\" 2;=\"\" letter-spacing:=\"\" color:=\"\" rgb(25,25,25);=\"\" font-size:=\"\" 16px;=\"\" border-top:=\"\" 0px;=\"\" font-weight:=\"\" border-right:=\"\" word-spacing:=\"\" padding-top:=\"\" font-variant-ligatures:=\"\" font-variant-caps:=\"\" -webkit-text-stroke-width:=\"\" 0px\"=\"\" style=\"text-align: center;\"><span style=\"display: inline !important;\">冷先锋先生率领学生练太极</span></p><p style=\"text-align: center;\"><br></p>', '1', '5', '0', '1', '0', '2018-06-29 08:47:45');
INSERT INTO `ys_article` VALUES ('36', '千古不泄之秘，丹道万法之基，炼目归源法！', '12', '推荐', '', '炼目 丹道', '', '道教（ID：daoismcn）', 'admin', '道在目前，丹道万法之基，炼目归源法！道在目前，丹道万法之基', '<p>炼目归源法乃丹道万法之基，更是南宫法派之秘密天机。千古以来，从未记载于纸上，我今打破万世之铁律，而书于纸上，望学者精研而体之，方不负此因缘。</p><p>筑基者，筑丹之基。如人建房，要将地基打牢。炼己者，即是炼去杂质，使心身变得清明，清明则能找到我之主人。筑基在形身精气上下功夫，而炼己则是在气质心神上下功夫。二者相辅相成，缺一则不可成其功。此一己清修之筑基炼己之含义也。</p><p>炼己者，炼去气质之性，显现本来天性。天性显，炼己成，则万物不能惑乱我心，得失皆可不放在心上。比如，你有一最心爱的外物忽然失去，当时虽心痛，但转眼即不伤心。处理万事皆不慌不忙而心无犹豫。此炼己之效验，随时可以验证。若无此等，则炼己还需痛下功夫，直到要达至自然方为完全尽功。</p><p>丹道万法之基：炼目归源法</p><p>此法为后面所有炼己功法之基础，此法又叫炼目归源之法，又叫炼罡法、炼剑法，乃丹道万法之基，更是南宫法派之秘密天机。千古以来，从未记载于纸上，我今打破万世之铁律，而书于纸上，望学者精研而体之，方不负此因缘。</p><p>仙圣皆云：道在目前。人之一身，精华上注于目，目不炼，则是盲修瞎炼，我见世人一炼功，就将双目全闭，实是一个睁眼瞎，双目要来何用！吕祖云：瞎子不好修道，聋子不妨。此即“道在目前”之义！</p><p>双目者，乃人之阳也，就如天上之太阳。双目全开则是夏天，半开下视则是秋天，半开上视则为春天，双目全闭则为冬。春生、夏长、秋收、冬藏，皆如外天地相似。</p><p>以上乃秘密天机，宝之！宝之！</p><p>需仔细体会，反复寂寂静心行施于日常之际，方能全明，否则书归书，你归你也，终不能完全领会也。</p><p>炼目具体功法心法</p><p>第一层：松目收神</p><p>此一层分为四步。</p><p>1第一步，春生。</p><p>双眼用力紧闭，越紧越好，到紧得不能再紧，再顺势用意慢慢放松整个眼部，至越松越好，越柔越好，越和越好，在松柔之时，两眼珠最好一动不动，渐将眼慢慢睁开，边开边松，渐至于半开。行此功时，最好在一间三明七暗之静室内，在与鼻尖齐平，且离眼三尺之处，放一点燃之香头，双目初开，则以此点燃之香头为系念系心之处。</p><p>初稍用心看之，一有杂念，即将杂念放归香头，此时眼微开，随着松柔，渐至似看非看，则眼半开。随着越松越空，而将心意收归眉心前大约一尺之处，虽然眼半开，但视而不见，眼前渐生出微白之光，或如白云片片，自眼前飞过，如是我更加松空心身和两眼，渐松空虚静至极，则眼前全是一片纯粹之白光。</p><p>我以松、空、虚、静守之，待其光稳定，则略用微意向头顶之上一看，只觉顶上有一盏无形之灯，此灯乃天心灯，丹书谓为天罡正星，乃吾人生命之根。悬于头顶虚空之上，光照下面形身，外观其形，则形无其形，渐渐归于虚空，忘形忘身而至于忘心，而眼自然全闭也。</p><p>上为太上三观功法之外观法六，用无上炼己功诀，秘之！宝之！</p><p>2第二步，夏长。</p><p>由忘形全闭眼而有觉，则将意自头顶收归眉心，松而空之，此时眼略睁开，放出一线之光，渐将一部分神意散于两目，松而空之，此时两目半开，渐双眼由近往远看去，同时渐渐放松放空双目，至于远，则目已至于极松而全开也，真意存眉心，而目神驭于外，似看非看，不可用心看，更不可有辨别之心，如是自可将远、近及所有诸物，用神光统的一包，浑而一之，行之久久，眼前远处化为一片白光，远观其物，物无其物。</p><p>上为太上三观法门之远观法诀，千古从未有泄漏于纸上，望诸君珍之重之！细细体会之，勤而行之！</p><p>3第三步，秋收。</p><p>双目自远而渐收归回来，越收越松，渐收至于眉心，此时双目半闭，待眼前一片白光，则微用意向下一看，自鼻端人中穴一定，觉有光生，则渐渐放下，而至于胸腔之内，定住不动，觉胸腔宽松无比，渐化为一片光芒，心其光，光其心也。</p><p>4第四步，冬藏。</p><p>待整个胸膛化为一片光茫而觉胸不存，则略用意放下，至于下面腹部空腔，定于此内，渐放下一切，所谓万缘放下也，眼随之全闭也。内观其身，身无其身。</p><p>此为太上三观功法中之内观法门。</p><p>第二层，凝目止念</p><p>行功打坐之进，心有杂念，有情绪波动之进，皆不可使眼全闭而内视内观，否则全闭为冬藏，如是将杂念杂气藏入身内矣，则必生阴火，照此行之久久，则必是一身阴气或燥火。如内视胸部，则使胸腔内生燥火杂念，内视腹部，则丹田阴气丛生或燥火冲身。中老年人修炼即使无念产，若长此以往闭目修炼，则必使阳陷于阴，阴气丛生，容颜更加衰老，一片昏沉而入鬼路，近世得丹诀而修炼之人，皆不知此理，虽名养生，反而促死，此皆不知“道在目前”之奥义也。</p><p>所以炼目止念之法，杂念太多，则将双目全开，观照于眼前三尺外之虚空。待杂念少一点，已不觉心烦，感觉心初平静，则双目半开而上视眉心前一尺之虚空处而松空我之两眼和眉心，若觉心已完全平静，则可双目半开下照鼻端外之径寸虚空，到无杂念，则双目只留一线之光，而放下一切，直往下照，则眼自然全闭而内视也，所谓万缘放下而内视也。此炼目天机，观空之法，不着于物，从外观而至内观。</p><p>第三层，炼目观心</p><p>目乃心之先机也，以在外而论，目初见物，先觉而后动，心方随之而动，此自外而入内也，此皆因目在头部，中含有元神之妙用，乃元神之使者也。以在内而言，心中一有杂念，而目神即动，如影随形，此皆因目乃人心之机也，就如一支箭，箭尖为目，箭杆为心也。故目中含两神之妙用，一为天心元神之妙用，一为人心识神之妙用。无目的偶然，忽的初见物时，此乃物来见目，而非目去见物，有如镜照物，此之初见初觉，心中无所计较与分辨，此第一眼是为神眼，纯是元神之妙用，丹书叫作真意，此意之发，不过半息之倾。</p><p>在初见之后，心一动，目亦动，又生一意，属魂，魂摇于心间，虽为善意，已含人心识神之妙用，心中已略有计较也，此第二眼乃是人眼，乃是阳魂用事。</p><p>在此一动后，又再一动，则纯是识神用事，已是属魄，魄动于形身，以后杂念及各种情绪滋生，如忿心欲心等，此第三眼乃是鬼眼也，纯是阴魄用事也。</p><p>有目的心有计较的初看某物，第一眼为人眼，其中略显元神之妙用，若在后随之一动，又生计较情绪，则此第二眼已是鬼眼，全为识神用事也。</p><p>能使第三眼之初返于第二眼，再从第二眼还于第一眼，再从第一眼返于虚无，所谓内观其心，心无其心，则人心退位，而天心作主，此法随时随地皆可行之，心一动，即收神定目，而行三观功法，目稍定即存神眉心而放下（此以将好看到鼻尖为准），松松空空，渐归虚无。此观心天机，其关键在返于形神未有之初。</p><p>第四层，运目收功</p><p>此功乃前三层功法之收功功法，其法为双目之眼珠在眼眶内上下左右旋运，左看右看，上看下看，内看外看，在一个旋运中完成，先逆旋三十六，后归入眉心而顺旋二十四，再双眼一闭，内视放松后脑部一会儿，定而放下，开眼。炼至觉天地亦随我双目旋运而旋运，则此功炼成也。</p><p>以上四层功夫炼成，则天目初开，天罡真意初成。</p><p>第五层，炼目总论</p><p>丹书所谓：破军前一位，誓愿不传人。不传人者，不以人心污染天心也，时时如是，行此誓愿，则天心长在，人心不得扰动，能炼目，则天罡在手，天罡在手，则地轴由心矣。</p><p>破军者，以外天地而言，乃天上北斗七星之第七星，以后天人身而论，破军乃在人之下极虚空，破军之前乃北斗六星，实天罡星之妙用也。破军所在处，即生气发动之处，前一位者，发动之前之根处也，故破军前一位即天罡。</p><p>总而论之，双目所指之处，即破军也，即生气发动之处。若双目指内则生机在内，双目指外，则生机指外。指外者为离，指内者为坎。</p><p>双目乃日月天罡，闭眼炼阴神，开眼炼阳神也。</p><p>双眼乃生气之先机也，而天目乃二目之体，故破军之前一位实天目。用此天目方能拨转天机，则天罡与破军互换其位，所谓“道在目前”也。丹道还返接命，皆是应用此天罡为之，所谓“天罡在手，则地轴由心”，乃丹法之秘机也。以后之采药、行火、出胎等等至秘法程，皆以此目为关键，能炼目则能采药，则能止念。丹书云：一团生意在双眸，又有云：机在目，蟾光终日照西川，又言：人身皆阴，只双目一点阳。双目乃人身天罡之用，天罡乃体，双目为用，故仙圣皆言：道在目前。</p><p>双目前者，未察之前，形身未兆之初也，能从目后炼归目前，则金丹可坐而致之。丹书有云：人身遍体属阴，赖以化阴还阳者，两目也。能知用目返照，则触处皆可洞然而头头是道。能善用此目，则玄关一窍可开。目不炼，修道炼丹万无成功之理。</p><p>炼目者，最终乃是不炼而炼，乃是完全伏住目神，不以目之识看，而以目之神会也。庄子云：观觉止而神遇行，即此义。</p><p>“内观一而不二者”，不以二目，而以一目，即神会也。二目之识伏住而不用，自然归于一目，一目者，天之目也，天之目成，方能拨转天罡。“玄学步步无魔，全在拨转天罡一诀”矣，秘之秘之。</p><p>以上天罡及炼目之诀，千古未从泄于纸上，想我以前，花数十万元，历时十五年，遍游访道，方得天罡秘炼全诀，今一朝泄出近半功诀心法，望诸君珍之重之宝之！</p><p>\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n</p><p>以上为炼目无上妙法，虽是初步法程，已是千古不泄不秘密天机重宝也。</p><p><br></p>', '1', '7', '0', '1', '0', '2018-07-03 01:32:53');
INSERT INTO `ys_article` VALUES ('37', '科学惊人发现：中医学的经络穴位实则来源另外维次空间', '15', '推荐', '', '“经络” “穴位”“奇经八脉”', '', '网络', 'admin', '中医理论中的“经络”、“穴位”、“奇经八脉”等事物逐渐在精密的科技仪器下现出原型', '<p>随着人类科技发展，医疗仪器也愈加精良，对人体的检测也是逐步的透彻可视化，中医理论中的“经络”、“穴位”、“奇经八脉”等事物也逐渐在精密的科技仪器下现出原型了。</p><p>本文通过克里安摄影术和核磁共振功能成像技术两种方法来详细论述“经络”的客观存在性。以及疾病的由来、治愈、神医的踪迹等问题进行了探讨，全文干货，偏长烧脑，但却值得你花10分钟重新认知我们的健康理论体系！</p><p>克里安照相技术</p><p>这是由前苏联人S.D.和 \nV.Kirlian（克里安）所共同发明的一种特殊照相机，利用高伏特电压的瞬间激发，将人或是物件的影像摄入底片中，而从中就可以看出该人或物的「气场」、「能量场」。</p><p>现在咸信，克里安相机捕捉到的气场，只是人、物的最基本能量场，称以太体（Etheric）；而人体尚有不同层次的（Layers）、更高的能量体（Energy \nBodies）。</p><p align=\"center\"><img alt=\"\" src=\"http://img.mp.itc.cn/upload/20170602/930d027381aa4c368cc60591904b1807_th.jpg\" width=\"332\" height=\"389\"></p><p>克里安当时认为照片上呈现的光晕是由生物能场所造成，而这种技术能捕捉到被摄物的生命能量状态。</p><p>不过现在一般相信，克里安拍摄到的仅为人和物体最基本的能量场，即乙太体（以太），而人体外围尚有更高层次的能量体（能源机构）。</p><p>然而克里安照片的好处在于能提早辨视出身体功能失调的部位，使它在变成疾病前早一步获得治疗。</p><p>电子摄影中最有趣的现象之一，是「幽灵叶」的效果。很多科研专家，包括亚伦．德翠克（Allen \nDetrick）和杜明崔斯可，都发现了在生物体周围的能量场。</p><p>当他们将一片叶子从中切半时，感到非常惊讶，因為他们发现被切掉的半片叶子区域，仍会放射出与原本整片叶子一样的能量场，也就是所拍到的竟是「完整的整片叶子」。</p><p align=\"center\"><img alt=\"\" src=\"http://img.mp.itc.cn/upload/20170602/0da5ca5903434f2391806d7cbd04f910_th.jpg\" width=\"547\" height=\"270\"></p><p>即使叶子的某部份不在，一个微妙的能量场还是继续存在於原本的部位，就像原来的叶子还在一般。</p><p>从针灸的理论中，我们知道，许多能量的路径是行经整个身体的。我们能够想像这些经络，就像一个比神经系统更细微的系统，某些在皮肤上的重要点位和经络上的要点，已被认定為“穴道”。</p><p>这些穴道分别与不同的器官共振。经由针灸的针刺激这些穴道，或透过指压按摩，相关的器官就会被注入生命能量，也称為“气”。</p><p>举例来说，用针灸的针去刺激肝经络的相关穴道或用按摩方式，都能够活化肝功能，以及与这经络有关的身体其他部位。</p><p>克里安摄影能够确认中医裡的这项知识，它显示出手上某些特定的共振点，甚至就是指尖，是分别与特定经络连接在一起的。</p><p>当我们在克里安相片上的某一区中发现了不寻常时，就能从手指或手的共振点，推断出能量在体内的哪个部位阻塞了。</p><p>下图就是通过克里安摄影的指尖状况：</p><p align=\"center\"><img alt=\"\" src=\"http://img.mp.itc.cn/upload/20170602/fef564cc7d2a41d69a857c340c6f8ef4.jpg\" width=\"195\" height=\"268\"></p><p>在我们的实例裡：像对应於肝点的那一区，若显示出微弱的或狠强的放射能量，便可以推断肝经络或其相连的身体部份能量已產生阻塞现象。</p><p>这一发现引起世界众多国家科学家的注意。本世纪80年代后，德国、日本、美国等分别对人体辉光现象作了探索和研究。</p><p>实验表明，人体辉光的颜色和形状会根据人的健康状况、生理和心理活动等发生变化。当人们把手放在一种高频高压环境中时，手阳明、大肠经的部位会出现一连串明亮光斑。</p><p>更让人感到奇妙的是，对刚死去的人進行测试，发现人体某些部位比其周围地区发出的光要强，而这些明亮的闪光点与中医针炙图上标明的741个穴位一致。</p><p align=\"center\"><img alt=\"\" src=\"http://img.mp.itc.cn/upload/20170602/f6a702df5f0d405089eeba8bb730edb6_th.jpg\" width=\"560\" height=\"415\"></p><p>俄国和美国科学家经过长期研究，认为人体存在着一个光导纤维系统，中医学中的针炙穴位是人体中经络系统对光最敏感的部位。</p><p>这些都证明了经络穴位是客观存在的一种实体。</p><p>通过克里安照相术我们几乎能肯定人体周身有许多肉眼看不见而又客观存在的物质的，这就好比伽马射线、紫外线、空气等物质，确实存在可仅能用仪器检测到，而科学认为，宇宙中的一切物质都有自己频率，即一切物质都以各自不同的频率振动着。</p><p>我们人类感知器官仅仅能接受一个非常小范围内的频率，一旦某个事物真大频率超过了这个范围，就无法被人类感知了，就好比海豚之间用以交流传递信息的超声波，我们人类无法听到，只能被科技仪器探测到，这便是因为超声波的频率不在人类感知器官能接受的范围内造成的。</p><p>这或许也能解释为何人类看不到鬼神、灵魂等事物吧，而人群中又有些人天生就能看到些另外空间的景象，这便是因为他们的感知器官较特殊能接受超过7.23振幅的事物造成的。</p><p align=\"center\"><img alt=\"\" src=\"http://img.mp.itc.cn/upload/20170602/3c04a41661cd406982b4fbd6f667ad50_th.jpg\" width=\"401\" height=\"361\"></p><p>核磁共振（MRI）功能成像技术</p><p>核磁共振功能成像技术对于经络穴位的验证，这是一项相对较新的研究手段。我们知道，通过核磁共振成像（Magnetic Resonance \nImaging，简称MRI）技术，可以得到一张人大脑的图像，并可以从不同的层面去截取，相当于把脑解剖开来一层一层的去看，看到具体的一些结构。</p><p>進而，借用MRI的结构成像，可以间接的得到“功能”成像。</p><p>比如我们已知左侧的大脑管着右手，右侧的大脑管着左手，当人的左手在运动时，在人的大脑的右侧的局部开始有了讯号增强，相反的，如果是人的右手在运动，就看到大脑的另一侧讯号在增强，这样的一种研究，我们就叫“功能成像”。</p><p>据此，韩国的一批科学家，Zang-Hee \nCho教授等，就想到能否用FMRI的方法来探讨东方人的古老的经络学说。当用光刺激人的眼睛的时候，在大脑的视神经区会看到讯号增强，这个是现在科学已知的。</p><p>另外，在针灸学中，通过一些相关的穴位的刺激，可以治疗眼睛的一些疾病。医书上也有相关记载，哪些经络和穴位是与眼睛有关系的，那么这是不是与大脑联系呢？</p><p align=\"center\"><img alt=\"\" src=\"http://img.mp.itc.cn/upload/20170602/97d32dac322545129e5fb9a105b142d4_th.jpg\" width=\"792\" height=\"455\"></p><p>足太阳膀胱经在这脚上有四个穴位点，已知这四个穴位点对治疗眼睛的疾病有作用，实验表明，当用针对受试者的这几个穴位進行刺激的时候，也同样看到了在这个视神经区信号的增强。</p><p>但是，如果将针刺在离这已知的穴位旁边，隔1厘米外的地方，受刺者在大脑视神经区，就没有发现讯号增强的现象。也就是说，必须刺在穴位点上，才会有这个效果。</p><p>读过《皇帝内经》的人或许都对某个词存在着疑惑和印象，大家知道，在《黄帝内经》中将“心、肝、脾、肺、肾”叫做“五藏”，并不是当今西方医学解剖学中的“五脏”，而如今很多中医研究者以为是汉字问题故将此问题忽略。</p><p>其实不然，《黄帝内经》以及中医中所指的“五藏”并不是西方解剖学中那个看的见摸的着的人体器官，而是“藏”在另外空间人类肉眼看不到的一种象，也就是《黄帝内经素问·六节藏象论》中所述的“藏象”。</p><p align=\"center\"><img alt=\"\" src=\"http://img.mp.itc.cn/upload/20170602/c09330460c64401a8daf9fdd23597fa9_th.jpg\" width=\"640\" height=\"640\"></p><p>相反，将中医理论与现代科技探测到的种种现象结合来看，我们似乎可以得出一个结论：</p><p>我们人类实际上有两套生理系统，即西医的解剖生理系统和中医的藏象生理系统，两套生理系统频率各不相同，存在不同空间，且会相互作用，人体病变最初是由藏象系统功能出问题而逐渐影响到解剖生理系统。</p><p>这也是为何很多人身体出现了不正常状态，如失眠、便秘、乏力、脱发等，可是去医院用仪器检测西医大夫便会告诉你一切指标正常，没有任何疾病。</p><p>事实上此时要是经一中医把脉便可得知身体已经出现严重问题了，但随着时间慢慢推移疾病才会显现到解剖生理系统上，一检测才发现为时已晚。</p><p>那么中医的针灸等治疗原理又是什么呢？</p><p>虽然不敢妄下结论，但我们同样可以做出一个假设：所谓的穴位或许就是解剖生理系统和藏象生理系统两者间的沟通点，通过针刺某穴位，便可重新激活和联通两个生理系统之间的联系，使人体回归正常状态。</p><p>其实这解释起来不难，但是需要读者有较为开阔的思维以及对前沿科学一些结论的认知，上文提到频率的问题，有些人似乎就能与众不同感知到看到另外空间的景象，民间叫做“阴阳眼”。</p><p align=\"center\"><img alt=\"\" src=\"http://img.mp.itc.cn/upload/20170602/56ad27b2f2504ff8a65148eda62a2a18_th.jpg\" width=\"450\" height=\"312\"></p><p>那么这个阴阳眼究竟是何物？是迷信还是能用科学解释得通呢？</p><p>其实修炼界将其叫做“天目”，道家叫做“泥丸宫”，科学家叫做“松果体”（科学研究发现松果体完全具备人类眼睛的结构）</p><p>这个器官直指眉心位于我们大脑的正中心，我们能看到另外空间全靠它，大部份人的松果体出生都是活跃状态的。</p><p>所以总有人说小婴儿能看到常人看不见的事物这并不是什么无稽之谈，但随着成长某些观念以及业力的阻挡导致能量丢失，也就慢慢封闭了。</p><p>但是有些人一直都开着，可以透视也能看到其他生命体的存在，我们通常叫做特异功能，透视遥视与天目早在上世纪就被科学界公认为几大特异功能之一。</p><p>而中国也一直有“医道同源”的说法，古代修道者多半也是个好医者，好医者多半也是修道之士，其实修炼文化和理念贯穿于整个中华文明，直到近代无神论的入侵才逐渐消亡，“天时地利人和，顺应自然、做事不要违背天理、要积德别造业！</p><p align=\"center\"><img alt=\"\" src=\"http://img.mp.itc.cn/upload/20170602/31f622740b6840c3868de8a8a522c7de_th.jpg\" width=\"334\" height=\"753\"></p><p>比如易经64卦与DNA64个密码子的惊人对应关系，卦象与疾病的对应，八卦与计算机二進制……很多发现使人叹为观止，简直无法相信古人的智慧。</p><p align=\"center\"><img alt=\"\" src=\"http://img.mp.itc.cn/upload/20170602/ae2bc879e33f49f19305b838b7a56738_th.jpg\" width=\"450\" height=\"307\"></p><p>\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n</p><p align=\"center\"><img alt=\"\" src=\"http://img.mp.itc.cn/upload/20170602/468dde439e5f441dbe29fd5993b598e6.jpg\" width=\"296\" height=\"170\"></p><p><br></p>', '1', '0', '0', '1', '0', '2018-07-03 01:37:04');
INSERT INTO `ys_article` VALUES ('38', '科学家认为：冥想打坐，可帮助治疗很多不治之症', '12', '推荐', '', '冥想 静坐 防治 癌症', '', '疗愈圈', 'admin', '冥想静坐的好处不止是养生，冥想还可以防治癌症，治疗很多不治之症。', '<p>冥想逐渐被人们所认知，冥想静坐的好处不止是养生，科学家发现，冥想还可以防治癌症，治疗很多不治之症。</p><p>1荷兰科学家研究表明：打坐沉思者比其它人致病的可能性低50%，在感染威胁生命的重病方面低87%。</p><p>2美国耶鲁大学医学院外科医生伯尼.塞格尔也认为：“沉思冥想是松弛思想的行动，可治视为绝症的艾滋病和癌症。”</p><p>3美国哈佛大学教授和马里兰州大学哈里博士经五年研究后说：“冥思静坐可对视力、血压、认识功能的激素水平提高大有好处，另可治疗许多不治之症和心脏病、关节炎等慢性病。”</p><p>4科学家还发现，静坐时大脑中出现的大量α波，可很明显的促进一种荷尔蒙激素的增长，从而会使血管扩大和血液畅通，会使人体机构组织细胞进行新陈代谢不可缺少的物质——三磷酸腺甙明显增高，会大大增加人体的免疫功能。</p><p>5据《世界科技译报》报道：美国玛赫里希处置大学，自然医学和预防中心主任罗伯特.施奈德经多年研究后说：“超脱静坐似乎能够恢复身体的自我修复机制和自我平衡的机制。它对神经激素和部分神经系统都起作用，从而有助于缓解心脏病和其它一些疾病的症状……这种技术是一个自然轻松的过程，能使你达到舒适安静的机敏状态。”</p><p>6研究人员还说：“如每天静思两次、每次20分钟，这是预防和治疗心血管疾病的有效方法，并可用做传统心脏病治疗的辅助手段。”</p><p>7美国伊利诺斯大学的科学家们对40名学生进行静坐生理实验观察表明：只要静坐5至10分钟，人的大脑耗氧量就会降低17%，而这个数值相当于深睡7个小时后的变化，同时发现受试者血液中被称为“疲劳素”的乳酸浓度，也在不同程度上有所下降。</p><p>8据新科学家网站近期报道：美国肯塔基大学的科学家发现，如果你一夜没睡，但只要打坐40分钟就可弥补缺觉的不足。研究人员让10位一夜未睡的受试者分别让他们或打坐、或阅读、或聊天、或睡觉，40分钟后又对他们进行“心理动作警觉作业”测试，其实验结果令研究人员震惊。尽管所有的受试者都没有练过冥想打坐，但40分钟后他们马上就能有卓越的表演。而经40分钟小睡后的受试者，则需至少1个小时后才能从朦胧中清醒。而那些聊天的和阅读者，对恢复精力都没有丝毫的帮助，这说明冥想打坐对大脑确实有不可思议的帮助。</p><p>9美国俄亥俄州的某空军基地实验室科研人员，从事大脑研究几十年后发现，大脑的活动有几种不同类型：8-13HZ的α波，14-25HZ的β波，4-7HZ的θ波，1-3HZ的δ波。当我们大脑处于紧张、情绪激动或亢奋时，往往会出现β波;而当我们处于安静休息的清醒状态下时，在脑中会出现α波;而在极度疲劳或熟睡时，大脑中出现的是δ波;而成年人受到挫折、郁抑及精神病患者和少年儿童，一般大脑中出现的是θ波。</p><p>10美国哈佛大学华莱士教授，在对修炼者做松静时的脑电波测试中发现：人体虚静时，头部前额区和中心区的α波强度会大幅度增加，频率由20HZ以上减慢至8至9HZ。原主要分部在大脑后枕部的α波节律逐渐向前额部位转移，其强度增高了425%至525%。而神经细胞的电活动高度有序化同时发生变动，其程度是不练功的4至5倍。</p><p>11科学家还发现，常期冥想打坐，可增加前额叶脑皮层和右前脑皮层的厚度，而这些区域是控制人注意力和感知能力的地方，许多科学家、作家、发明家等名人的前额叶脑皮层都厚，而且他们的脑电波大部分时间都处于α波频率，这说明坐禅确实能使大脑敏捷并容易产生灵感。</p><p>12根据地球物理学家测定得知，空间电离层与地球共振的休曼波是8至14赫兹，而这个频率与人体打坐入静时的脑电波基本相近，故在物我两忘的空灵境界下打坐会发生人天共振证悟人生大道、宇宙真谛。</p><p>13许多科学家认为：超觉静思是大脑充分发挥机能最高技术，它能够巧妙地激发大脑的左半球的力量去影响右半球，从而使右脑最大限度地发挥思维能力。</p><p>14发现超导节并获诺贝尔物理学奖的科学家约瑟夫.森曾说：“科学发展到今天，离开了冥思静坐的体验就很难走下去了。”</p><p>\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n</p><p>15科学家经实验还证明：通过有序的训练，人体完全可以学会控制和调节自己的大脑电波，而当身体和精神完全处于放松状态时，其记忆力几乎是无穷无尽的，并发现这些人的记忆没有明显的饱和点。看完上面的内容，大家对冥想有了很深入的了解，那么还等什么，从今天起，开始打坐冥想吧，不要在意别人的目光，要放空身心，什么都不要去想，静静的，必要的话可以听一些轻音乐。</p><p><br></p>', '1', '0', '0', '1', '0', '2018-07-03 02:10:31');
INSERT INTO `ys_article` VALUES ('39', '经络不通，补什么都没用！只用一招：经络通畅百病消！', '8', '推荐', '', '', '', '全民皆医', 'admin', '经络决定人体健康 想要养生，保持经络畅通是非常必要的', '<p>中医认为，经络决定人体健康，一旦经络出现堵塞，人体就会出现诸多疾病。</p><p>因此，想要养生，保持经络畅通是非常必要的。尤其是冬季，大家很重视进补，但是如果经络堵塞，补什么都没有用！</p><p>当然，人体是非常敏感的，如果经络不通，就会发出很多不舒服的信号来求救!各种不通的信号代表着不同的情况，可以根据自己情况，选择最合适的方法和穴位来调理。</p><p>经络不通的首要感觉：疼痛</p><p>还是那句话：痛则不通，通则不痛。经络不通的第一个感觉就是疼痛，比如有时候我们会有莫名的疼痛，那说明的是此处经络不通。</p><p>疼痛气血不通：冷</p><p>疼痛说明经络不通，继而导致气血不通，产生“冷”的感觉。</p><p>除了头凉之外，身体其他某些部位发凉，比如最常见的是手脚发凉，常常是经络不通的信号。因为人的体温是由气血输送来决定的，气血旺盛，体温才会正常。哪个地方发冷，哪个地方可能经络不通，气血难以到达。</p><p>热气不通：热</p><p>经络不通引起的热主要表现为身体某些地方低热、干燥，或者局部异常出汗。这是因为体内经络不通，热气不能通过正常渠道散发出去引起的。除了外邪引起的红、肿、热、痛与发热所在的经络不通有关，一般低热可能来自相连的经络。</p><p>经络彻底堵塞通：麻和木</p><p>经络不通进一步的发展就是麻或木。比如我们坐得太久，下肢气血不通，就会疼痛；接着，经络被堵死，气血彻底不通，双腿就会麻木，只有改变坐姿，气血通畅才能恢复知觉。</p><p>经络不通的两种形态：肿和胀</p><p>●经络不通可以是有形的，这就是肿，常由血瘀引起；</p><p>●经络不通也可以是无形的，这就是胀，由气滞引起。</p><p>如何疏通经络，消除百病，让冬季进补更有效呢？推荐大家10个妙招。</p><p>疏通经络的10个妙招每天做一遍，经络通畅百病消！</p><p>1滚揉后溪穴：保护颈椎、腰椎</p><p>如果你坐在电脑面前，可以把双手后溪穴的这个部位放在桌子沿上，用腕关节带动双手，轻松地来回滚动，即可达到刺激效果，</p><p>在滚动当中，它会有一种轻微的酸痛。每天只用抽出三分钟的时间来，随手动一下。坚持下来则对颈椎、腰椎确实有着非常非常好的疗效，对保护视力也很好。</p><p>2拍肘窝：排出心肺火气和毒素</p><p>肘窝是一个经络密集的部分，分别有肺经、心包经和心经这三条经络通过，拍打肘窝可以排出心肺的火气和毒素。</p><p>拍的时候手掌放松，有力且有节奏的一下一下拍下去，每条胳膊连续拍打5~10分钟，先拍左肘窝，再拍右肘窝，便会出现青、红、紫、黑等不同颜色的毒素反应物，颜色越深问题越严重。</p><p>最好是每周拍打一次，一般3~5次反应物就会明显减少，毒素也就清理的差不多了。怕疼的话，轻轻拍打到发红也可以通经络，促进气血的运行，</p><p>建议：拍打结束后，马上喝一杯温水以加速排毒，当天不要洗澡。</p><p>3瑜伽蝴蝶式：促进血液循环</p><p>晚上回家做一下这个动作，双手抓住脚尖，膝盖向两侧打开，尽量往地上贴，腰要挺直，双膝有节奏向地板振动。每天10分钟，能有效地伸展背部及胯部强化身体的柔韧度，</p><p>瑜伽蝴蝶式通过下压双腿，瑜伽蝴蝶式可锻炼骨盆，促进骨盆的血液循环，滋养膀胱、肾脏等器官，从而改善肤色，令肌肤白里透红,还能防止妇科疾病的发生，每个月总有几天是女性最脆弱的时候，这个时候就要更重视自己，经期最好是使用质地绵柔无添加舒适透气的缇妍堂卫生巾，平时私处的护理推荐缇妍堂女性护理液，一周使用2-3次能有效杀菌。</p><p>4搓摩脸部焕醒肌肤</p><p>双手搓至发热，干洗脸，搓揉面部60次，手掌搓热轻柔的拂过即可， \n如果只是採取普通的洗脸方式，未必能将肌肤彻底唤醒，相反的，搭配使用滨趣嫩白肌肤净便能一整天看起来神采奕奕，起床后，睡觉前，觉得眼睛疲劳时也都可以做做。</p><p>5双臂上拉护肩颈</p><p>保持这个伸展的动作10秒钟，放松，再做一次。在伸展的过程中会感觉到肩颈背部的痛点。做完以后很舒服。</p><p>6拉伸脊椎缓解腰部疲劳</p><p>保持这个姿势10秒，放松，再做一次。中医认为，肾主藏精，肾强则男人强。肾在人体腰部的位置，对于腰部的护理不容小视。具体可采用按摩肾俞穴位法：将两手掌平放在两侧腰部，上下拉动腰带摩擦50次至100次。可以起到壮腰、健肾、益精作用。 \n也可直接饮用粟滋轩核桃黑豆粉，能强肾养发，补足肾气，内外调和才能达到增强体质的的效果。</p><p>7提肩松膀护肩颈</p><p>站直，提起双肩，感觉到肩部有酸胀的拉伸，保持这个感觉15秒钟，放松肩膀。再做一次。</p><p>8缓解肩膀酸痛+减肥</p><p>买一只擀面杖，时常放在脚下来回的擀，每天10~15分钟。如果你的肩颈部对应的位置会觉得疼，有颗粒，擀到颗粒散开，肩颈部的疼痛也会相应的散开，坚持做还可以减肥。</p><p>9膀胱经排毒</p><p>一次至少要做15~30分钟。</p><p>越是够不到，越说明你的毒素堆积的多，久而久之就容易便秘，从而导致痔疮，但是不要着急，也不要用蛮力，只要大腿后侧的大筋有拉伸的感觉就可以了，否则容易拉伤韧带，坚持一段时间后如果效果不佳便可以使用缇妍堂断痔膏,一般不出三天，病状都能大大改善。坚持使用一个疗程可以彻底恢复痔疮，预防再次反复，有痔疮的朋友可以去试试。</p><p>10按揉印堂改善心脑血管功能</p><p>一次40下为宜。这样做可以调整内分泌、改善心脑血管功能、安神定志。</p><p>冬季天气寒冷人，体有个穴道，揉一揉，暖和一整天手脚冰凉，女人冬季必备</p><p>◆ ◆ ◆ ◆ ◆</p><p>第1招：揉一揉，暖和一整天</p><p>人体有个穴道，叫做阳池穴，这个名字就意味着囤聚太阳的热量。阳池穴在人的手背手腕上（如上图）。寻找的方法，先将手背往上翘，手腕上出现几道皱褶，在靠近手背那一侧的皱褶上按压，这个痛点就是阳池穴。阳池穴是支配全身血液循环及荷尔蒙分泌的重要穴位。只要刺激这一穴位，便可迅速畅通血液循环，温和身体。冬天不妨试试，每天数次，暖和一天。</p><p>第2招：嘴唇干起皮很管用</p><p>秋冬季节天气干燥，嘴唇发干，起皮，教你一招很快就好：抓银耳一把，取红枣10枚，放冰糖煮开。早晚喝一次。2天后起皮发干自然消失。平时少吃辛辣，多吃水果来补充维生素，以后再也不会出现了。</p><p>第3招：柠檬皮轻松祛痘</p><p>脸上长痘，让形象破受影响。教你一招，轻松祛痘。柠檬皮1个，葡萄干20粒</p><p>，甘蔗1尺，切4段，甘草10片，同时下锅，放下4碗水煎至2碗左右，之后还可</p><p>以抹上滨趣芦荟凝胶，对痘痘恢复很有帮助，起到美容的效果。</p><p>第4招：肩周炎用大盐粒热敷</p><p>肩周炎较顽固，教你一个简单管用的方法，用大盐粒子一斤炒热，装布口袋里</p><p>捆结实(不要让盐粒子掉出)，放在肩部来回熨烫，感觉很舒服为宜，每天晚上一次，1-2周即可，也可使用具备相同功效的荟欣福消痛贴，把它贴在肩膀或其他经络骨头疼痛之处，药物成分能很好地渗透到骨骼，舒缓肌肉神经。</p><p>第5招：近视眼小妙招</p><p>经常用电脑，手机或者近视眼，常喝枸杞菊花茶。枸杞适量多些，泡开后将枸杞吃了。每天清晨洗脸时，用半干不湿的热毛巾敷于前额和双眼，眼睛轻闭，头稍仰，毛巾凉后再重新更换，持续一分钟，长期坚持，你会发现神奇就在你身边。</p><p>第6招：有鼻炎用生葱</p><p>新鲜生葱洗净，取葱白捣烂，用小团棉球浸葱汁，先用棉签沾淡盐水擦拭鼻孔，然后把浸了葱汁的棉球塞入鼻孔内，轻轻呼吸，保持数分钟，直到刺激感消失取出，再换新棉球，每天一次，一次5-6个棉球，鼻炎恢复很快，若嫌麻烦可以去试试荟欣福蛇胆喷剂，无需太过复杂，每次只要滴一滴就好了。</p><p>第7招：快速解酒小妙招</p><p>将大白菜帮洗净，切成细丝，加些食醋、白糖拌匀，腌渍10分钟后食用，清凉、酸甜，又解酒。饮酒时吃豆腐是最聪明选择，因为豆腐中有一种主要的氨基酸，能化解乙醇的“毒性”，促进酒中的乙醇迅速排泄，不妨试试。</p><p>第8招：快速解决落枕</p><p>只要将你的脚抬起来，把大脚拇趾掰开，按顺时或逆时的方向慢慢地按摩旋转，则你的落枕将获得最大修复。如果发觉脚拇趾肌肉紧绷，那表示您的脖子扭伤了，左脚管左边，右脚管右边，经常按摩，将直接或间接疏缓颈项的肌力，有助于预防脖子的伤害。</p><p>第9招：老年痴呆小方法</p><p>花生米45克，粳米60克，冰糖适量。同入砂锅内，加水煮至米烂汤稠为度。每晨空腹温热食之。花生米中的卵磷脂是神经系统所需要的 \n重要物质，能延缓脑功能衰退。</p><p>第10招：丰胸小妙招</p><p>木瓜性温味酸，平肝和胃，舒筋络，活筋骨，降血压。 \n木瓜中含凝乳酶有通乳、多吃可以达到丰胸效果，同时睡前采些许滨趣紧实霜按摩乳房穴位，使乳房2次发育持续增大，停用后不会反弹！</p><p>第11招：增强记忆力小妙招</p><p>每日清晨取新鲜鹅蛋一只，打入容器内依个人口味加入适量的白糖搅匀后放笼上清蒸，空腹食用，有清脑益智功能，对增强记忆力有特效，可经常食用。</p><p>第12招十秒钟停止打嗝</p><p>不停打嗝，用按摩法治打嗝，两手拇指同时按两个鱼腰穴(眉毛中点处)，由下向上按压，用力由轻到重，有酸胀感。按摩时，要用力憋气，再用力呼出，反复二三次，即可停止打嗝。</p><p>第12招：常熬夜多吃山药</p><p>山药含有多种微量元素、丰富的维生素和矿物质，对增强免疫功能，延缓细胞衰老。长期熬夜的朋友常吃山药，南瓜，豆类，鱼类对自身免疫力，神经系统都很有帮助。切记熬夜后敷上一张滨趣面膜，快速补充皮肤水分流失，再也不怕熬夜容易导致皱纹出现、皮肤暗淡、长暗疮、黑眼圈了。</p><p>第13招：牙周炎轻松解决</p><p>\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n</p><p>每日早晚、餐后用淡盐水漱口，以清除口腔病菌。还可以搭配缇妍堂珍珠亮白牙粉一起使用，可以美白牙齿仰头含漱使盐水充分冲洗咽部效果更佳，不慎患有口臭的，可使用缇妍堂祛异味喷剂每天喷一喷，可达到根治口臭的作用，还你清新口气一整天。</p><p><br></p>', '1', '3', '0', '1', '0', '2018-07-03 02:14:55');
INSERT INTO `ys_article` VALUES ('40', '恢复人体科学研究对中国科学的意义重大', '11', '推荐,头条', '', '', '', '中西汇通', '赵伟民', '建立人体科学体系，本质上是对西方科学用还原论方法研究人体的一种纠正。', '<p>人体科学是钱学森倡导的，目的是站在系统科学的高度，把中医、气功和特异功能统一起来共同研究，以求实现中国科学大的突破。然而，由于中国科学主流长期受西方科学主导，中医、气功和特异功能一直被视为伪科学加以批判，这大大阻碍了中国自己新科学的发展进程。</p><p>建立人体科学体系，本质上是对西方科学用还原论方法研究人体的一种纠正。看看中医和西医的区别就知道了，它们最大的矛盾在于对人与自然关系以及整体与局部关系的认识，西医基于观察不到就不存在的理由否定了这两种关系，才造成了中西医之间的巨大矛盾。而中医呢，则恰恰建立在对这两种关系的正确认识之上，认识到了人与自然的关系，就得出了人是自然无限小的一部分，顺应自然则生，违背自然则死，疾病就是人与大自然矛盾激化的产物；认识到了整体与局部的关系，就得出了细胞之所以病变，并不是细胞的原因，而是整体的管理失控造成的，只要恢复了整体的管理，病变细胞就会在整体力量的控制下改邪归正。中医本质上就是一门恢复整体管理的科学体系。</p><p>钱学森是研究系统科学的，他很早就认识到了西方科学的局限，它完全忽视了那些精密仪器观察不到的关系，而中医、气功和特异功能问题的出现正是中国实现科学突破的契机。钱学森说得好：“如果我们对已有的系统知识加以综合，那将引起一场21世纪新的科学革命，这‘也许是比20世纪初的量子力学、相对论更大的科学革命’。”</p><p>确实，每一次东西方文化的结合都会引发大的科学革命。西方科学之所以后来居上，是因为东学西渐，它不仅带去了大量先进技术，如四大发明，而且带来了科学革命所必需要的思想，即“道“的思想，树立了科学上“自然规律神圣不可侵犯”的原则，这才给科学家探索自然添加了无穷的动力，推动了西方科学一次次的革命性进展。而在今天，东西方文化的深度融合又将会引起一场更伟大的科学革命，而它必然会使中国科学后来居上。</p><p>现在不少国人总以为“西方的月亮比中国的更圆”，西方科学解释不了中医、气功和特异功能，就以为中国自己肯定也不可能。比如，现代人无法用系统科学来解释中医，就以系统科学解释不了中医。其实不然，中国是系统科学的老祖宗，它几千年前就一直从系统的、整体的角度来认识自然，中医就根源于这种理论体系。而西方的系统科学呢？则只有一百年的历史，更重要的是，它是站在过去还原论的基础上来进行研究的，这就注定了它不可能取得实质性的突破。而中国则不然，它很早就建立了一个完整的系统科学体系，只是它的语言方式一直停留在几千年前，后人难以理解罢了，如果我们能够借助人体科学的研究把它揭示出来，那必然能够建立在一个完整的系统科学体系，这不仅能够推动新一轮的科学革命，而且能够证明证明中医的科学性，实现中国传统文化的全面复兴。</p><p>很多人不明白，为什么西方人走向了用还原论来研究世界的道路，而中国先人却走向了用系统论来研究世界的道路？其实，科学都是建立在假设之上的，前提假设不同，得到了科学结论也完全不同。西方科学虽然看起来富丽堂皇，其实，它也有一个前提假设，这就是原子说，后来牛顿的绝对时空观、爱因斯坦的相对性原理和光速不变，以及以太观，都是原子说假设的一种变体。按照这个学说，空间是空无一物的，实体是由少数几种死寂不变的基本粒子组成的，因此，我们只要挖掘出那几个基本粒子，自然规律就找到了，西方科学一直就是按照这种还原论思想发展起来的。而东方科学也有一个前提假设，它就是气一元论，它认为空间中连续着统一的物质（气），万物都是由这种生命之流汇聚而成的。因此，自然规律不可能是具体的东西，而只可能是一个物质的组织原理，即系统学规律，要正确的认识它，方法只有一个，那就是系统间的类比，即中国传统的方法论——取象类比。</p><p>现在我们就明白了，东西方科学是两个完全不同的科学体系，它们前提假设不同，得出的结论也完全不同，中医建立在东方科学之上，当然无法站在西方科学的前提假设之上来认识。就拿西方的系统科学来说吧，它本质上是建立在原子说假设之上的，不承认物质在空间中的连续性，生命问题永远不可能认识，不承认物质彻底的统一性，普遍联系永远不可能认识，这都是西方科学的死穴。而中国的系统科学呢，则是建立在气一元论假设之上的，有了物质在空间中的连续性，生命问题解决了，有了物质彻底的统一性，普遍联系问题解决了，只有恢复了这个中国特色的系统科学，中医、气功和特异功能才能够真正地走向科学化。</p><p>事实上，这个系统科学就包含在中医学当中。按照气一元论的描述，自然界的一切现象都是由统一的一种物质（气）的有序（阴）和无序（阳）变换的规律形成的，而物质的有序和无序变换的规律就是大自然最普遍的规律，也是系统学规律。因为物质从无序向有序转化就是物质系统的产生，从有序向无序转化就是物质系统的灭亡。阴阳学说本质上就揭示了物质系统产生、发展、壮大、衰退、灭亡的规律性。现在人不理解阴阳学说，是因为人们抛弃了阴阳学说的基石——气一元论，阴本来是指统一物质（气）的有序状态，阳本来是指统一物质（气）的无序状态，如果没有了气一元论，阴阳学说就成了空洞的哲学想象，当然无从理解。</p><p>同理，五行学说也是站在气一元论之上的，它本质上揭示了同一种物质（气）的五种不同运动方式，它们相互促进、相互制约，普遍存在于自然的每一个物质系统当中。在这里，木代表着组织现象，即物质从无序向有序转化；金代表着离散现象，即物质从有序向有序转化；火代表着系统的发展壮大，即无序增长，有序减小；水代表着引力现象，即有序增加，无序减少；土代表着系统的控制，即依赖连续在系统空间某一层次的物质有序和无序周期变换传递能量。只要我们科学地理解了这个学说，就可以用它来认识自然界的一切系统现象。</p><p>让我们来看看建立中国的系统科学对中国科学的意义有多大吧！</p><p>首先，它会引起一场更伟大的科学革命。有了系统科学，我们就可以认识那些精密仪器观察不到的物质运动，从而深刻我们对自然的认识。</p><p>其次，它会引起医学的一场革命。有了系统科学，中医和西医的矛盾就化解了，中医的科学性得到证明，两种医学体系也将汇通如一，成为统一的医学体系。</p><p>其三，它会带来大量技术创新，使中国经济获得全新的内在动力，实现中国经济的良性转型。</p><p>其四，它会带来军事基础理论的创新，帮助中国在军事上全面超越美日。尤其是对气功外气和特异辐射的研究，可以用这种能量制造一种极厉害的海上武器，帮助中国在海上占据绝对的主导地位。</p><p>其五，系统科学也会为管理学带来全面的生机与活力。因为能够帮助认识到了组织内部的普遍联系，实现管理从“金字塔”式的线性管理走上“学习型”的非线性管理。</p><p>\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n</p><p>总之，人体科学研究能够使我们深刻地领悟中医学当中的系统科学体系，帮助中国实现科学全面的突破，使中国科学后来居上。国家应该深刻地认识到当年在西方科学派的主导下对人体科学的打压只是一场闹剧，人体科学研究对中国科学有着巨大的意义。</p><p><br></p>', '1', '2', '0', '1', '0', '2018-07-03 02:19:49');
INSERT INTO `ys_article` VALUES ('41', '揭示人体气（能量）系统的运行机制是中医学术现代化的必经之路', '4', '推荐', '', '', '/static/upload/20180703-10-35-12_13663_图片1.jpg', '心身新生', '刘天君', '人体的结构、功能、能量 三者之间的关系', '<p><!--[if gte mso 9]><xml><w:LatentStyles DefLockedState=\"false\"  DefUnhideWhenUsed=\"true\"  DefSemiHidden=\"true\"  DefQFormat=\"false\"  DefPriority=\"99\"  LatentStyleCount=\"260\" >\n<w:LsdException Locked=\"false\"  Priority=\"99\"  SemiHidden=\"false\"  Name=\"Normal\" ></w:LsdException>\n<w:LsdException Locked=\"false\"  Priority=\"99\"  SemiHidden=\"false\"  Name=\"heading 1\" ></w:LsdException>\n<w:LsdException Locked=\"false\"  Priority=\"99\"  SemiHidden=\"false\"  Name=\"heading 2\" ></w:LsdException>\n<w:LsdException Locked=\"false\"  Priority=\"99\"  SemiHidden=\"false\"  Name=\"heading 3\" ></w:LsdException>\n<w:LsdException Locked=\"false\"  Priority=\"99\"  SemiHidden=\"false\"  Name=\"heading 4\" ></w:LsdException>\n<w:LsdException Locked=\"false\"  Priority=\"99\"  SemiHidden=\"false\"  Name=\"heading 5\" ></w:LsdException>\n<w:LsdException Locked=\"false\"  Priority=\"99\"  SemiHidden=\"false\"  Name=\"heading 6\" ></w:LsdException>\n<w:LsdException Locked=\"false\"  Priority=\"99\"  SemiHidden=\"false\"  Name=\"heading 7\" ></w:LsdException>\n<w:LsdException Locked=\"false\"  Priority=\"99\"  SemiHidden=\"false\"  Name=\"heading 8\" ></w:LsdException>\n<w:LsdException Locked=\"false\"  Priority=\"99\"  SemiHidden=\"false\"  Name=\"heading 9\" ></w:LsdException>\n<w:LsdException Locked=\"false\"  Priority=\"99\"  SemiHidden=\"false\"  Name=\"index 1\" ></w:LsdException>\n<w:LsdException Locked=\"false\"  Priority=\"99\"  SemiHidden=\"false\"  Name=\"index 2\" ></w:LsdException>\n<w:LsdException Locked=\"false\"  Priority=\"99\"  SemiHidden=\"false\"  Name=\"index 3\" ></w:LsdException>\n<w:LsdException Locked=\"false\"  Priority=\"99\"  SemiHidden=\"false\"  Name=\"index 4\" ></w:LsdException>\n<w:LsdException Locked=\"false\"  Priority=\"99\"  SemiHidden=\"false\" 